// src/pages/CreateContest.js
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function CreateContest() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [problems, setProblems] = useState([]);
  const [selectedProblemIds, setSelectedProblemIds] = useState([]);
  const [loadingProblems, setLoadingProblems] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState("");

  // Redirect to login if not logged in
  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Fetch all problems to choose from
  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get("http://localhost:5000/problems");
        setProblems(res.data);
      } catch (err) {
        console.error(err);
        setMessage("Failed to load problems.");
      } finally {
        setLoadingProblems(false);
      }
    }
    fetchProblems();
  }, []);

  function toggleProblemSelection(problemId) {
    setSelectedProblemIds((prev) =>
      prev.includes(problemId)
        ? prev.filter((id) => id !== problemId)
        : [...prev, problemId]
    );
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setMessage("");

    if (!name || !startTime || !endTime || selectedProblemIds.length === 0) {
      setMessage("Please fill all required fields and select at least one problem.");
      return;
    }

    if (!token) {
      setMessage("You must be logged in to create a contest.");
      return;
    }

    try {
      setSubmitting(true);

      const res = await axios.post(
        "http://localhost:5000/contests",
        {
          name,
          description,
          startTime: new Date(startTime).toISOString(),
          endTime: new Date(endTime).toISOString(),
          problemIds: selectedProblemIds,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("Contest created successfully!");
      // redirect to contest page
      navigate(`/contests/${res.data._id}`);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Failed to create contest.");
    } finally {
      setSubmitting(false);
    }
  }

  if (!user) return null; // just in case

  return (
    <div>
      <h2>Create Contest</h2>

      {message && <p style={{ color: message.includes("successfully") ? "green" : "red" }}>{message}</p>}

      <form onSubmit={handleSubmit} style={{ maxWidth: 600 }}>
        <div style={{ marginBottom: 10 }}>
          <label>
            <b>Name*</b>
            <br />
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              style={{ width: "100%", padding: 6 }}
              required
            />
          </label>
        </div>

        <div style={{ marginBottom: 10 }}>
          <label>
            <b>Description</b>
            <br />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              style={{ width: "100%", padding: 6, minHeight: 80 }}
            />
          </label>
        </div>

        <div style={{ marginBottom: 10 }}>
          <label>
            <b>Start Time*</b>
            <br />
            <input
              type="datetime-local"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              style={{ padding: 6 }}
              required
            />
          </label>
        </div>

        <div style={{ marginBottom: 20 }}>
          <label>
            <b>End Time*</b>
            <br />
            <input
              type="datetime-local"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              style={{ padding: 6 }}
              required
            />
          </label>
        </div>

        <div style={{ marginBottom: 20 }}>
          <b>Select Problems*</b>
          <br />
          {loadingProblems ? (
            <p>Loading problems...</p>
          ) : problems.length === 0 ? (
            <p>No problems found. Please add problems first.</p>
          ) : (
            <div
              style={{
                maxHeight: 250,
                overflowY: "auto",
                border: "1px solid #ddd",
                padding: 10,
                borderRadius: 8,
                background: "#fafafa",
              }}
            >
              {problems.map((p) => (
                <label key={p._id} style={{ display: "block", marginBottom: 6 }}>
                  <input
                    type="checkbox"
                    checked={selectedProblemIds.includes(p._id)}
                    onChange={() => toggleProblemSelection(p._id)}
                    style={{ marginRight: 8 }}
                  />
                  {p.title} ({p.difficulty})
                </label>
              ))}
            </div>
          )}
        </div>

        <button type="submit" disabled={submitting}>
          {submitting ? "Creating..." : "Create Contest"}
        </button>
      </form>
    </div>
  );
}

export default CreateContest;
