// src/pages/DuelPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function DuelPage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [problems, setProblems] = useState([]);
  const [loadingProblems, setLoadingProblems] = useState(true);

  const [opponentId, setOpponentId] = useState("");
  const [selectedProblemIds, setSelectedProblemIds] = useState([]);
  const [durationMinutes, setDurationMinutes] = useState(60);

  const [message, setMessage] = useState("");
  const [submitting, setSubmitting] = useState(false);

  // If not logged in, redirect to login
  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Load problems (to choose for the duel)
  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get("http://localhost:5000/problems");
        setProblems(res.data);
      } catch (err) {
        console.error("Error fetching problems:", err);
        setMessage("Failed to load problems. Make sure backend is running.");
      } finally {
        setLoadingProblems(false);
      }
    }
    fetchProblems();
  }, []);

  function toggleProblemSelection(problemId) {
    setSelectedProblemIds((prev) =>
      prev.includes(problemId)
        ? prev.filter((id) => id !== problemId)
        : [...prev, problemId]
    );
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setMessage("");

    if (!token) {
      setMessage("You must be logged in to start a duel.");
      return;
    }

    if (!opponentId.trim()) {
      setMessage("Please enter your opponent's user ID.");
      return;
    }

    if (selectedProblemIds.length === 0) {
      setMessage("Please select at least one problem for the duel.");
      return;
    }

    if (!durationMinutes || durationMinutes <= 0) {
      setMessage("Duration must be a positive number of minutes.");
      return;
    }

    try {
      setSubmitting(true);

      const res = await axios.post(
        "http://localhost:5000/matches/1v1",
        {
          opponentId: opponentId.trim(),
          problemIds: selectedProblemIds,
          durationMinutes: Number(durationMinutes),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("Duel created! Redirecting to contest...");
      const contestId = res.data.contest._id;
      navigate(`/contests/${contestId}`);
    } catch (err) {
      console.error("Create duel error:", err);
      setMessage(
        err.response?.data?.message ||
          "Failed to start duel. Check opponent ID and try again."
      );
    } finally {
      setSubmitting(false);
    }
  }

  if (!user) return null; // avoid flash

  return (
    <div style={{ maxWidth: 800, margin: "0 auto" }}>
      <h2>1v1 Coding Duel</h2>
      <p style={{ color: "#555", marginBottom: 20 }}>
        Create a head-to-head battle. Pick problems, set duration, and invite your opponent.
      </p>

      {message && (
        <p
          style={{
            color: message.toLowerCase().includes("fail") ? "red" : "green",
            marginBottom: 10,
          }}
        >
          {message}
        </p>
      )}

      <form onSubmit={handleSubmit}>
        {/* Opponent ID */}
        <div style={{ marginBottom: 15 }}>
          <label>
            <b>Opponent User ID*</b>
            <br />
            <input
              type="text"
              value={opponentId}
              onChange={(e) => setOpponentId(e.target.value)}
              placeholder="Paste opponent's MongoDB user _id"
              style={{ width: "100%", padding: 6 }}
            />
          </label>
          <small style={{ color: "#777" }}>
            (Later you can upgrade this to search by username.)
          </small>
        </div>

        {/* Duration */}
        <div style={{ marginBottom: 15 }}>
          <label>
            <b>Duration (minutes)*</b>
            <br />
            <input
              type="number"
              value={durationMinutes}
              onChange={(e) => setDurationMinutes(e.target.value)}
              style={{ width: 120, padding: 6 }}
              min={1}
            />
          </label>
        </div>

        {/* Problems */}
        <div style={{ marginBottom: 20 }}>
          <b>Select Problems for the Duel*</b>
          <br />
          {loadingProblems ? (
            <p>Loading problems...</p>
          ) : problems.length === 0 ? (
            <p>No problems found. Seed or create problems first.</p>
          ) : (
            <div
              style={{
                maxHeight: 260,
                overflowY: "auto",
                border: "1px solid #ddd",
                padding: 10,
                borderRadius: 8,
                background: "#fafafa",
                marginTop: 6,
              }}
            >
              {problems.map((p) => (
                <label
                  key={p._id}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    marginBottom: 6,
                    fontSize: 14,
                  }}
                >
                  <span>
                    <input
                      type="checkbox"
                      checked={selectedProblemIds.includes(p._id)}
                      onChange={() => toggleProblemSelection(p._id)}
                      style={{ marginRight: 8 }}
                    />
                    {p.title}{" "}
                    <span style={{ color: "#999" }}>({p.difficulty})</span>
                  </span>
                </label>
              ))}
            </div>
          )}
        </div>

        <button type="submit" disabled={submitting}>
          {submitting ? "Creating duel..." : "Start 1v1 Duel"}
        </button>
      </form>
    </div>
  );
}

export default DuelPage;
