// src/pages/DuelRoomPage.js
import React, { useEffect, useState } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Editor from "@monaco-editor/react";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { user, token } = useAuth();

  const [problems, setProblems] = useState([]);
  const [selectedSlug, setSelectedSlug] = useState("");
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("// Pick a problem to start battling!\n");
  const [language, setLanguage] = useState("javascript");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const [loadingProblems, setLoadingProblems] = useState(false);
  const [error, setError] = useState("");

  const [lastStatus, setLastStatus] = useState("Not submitted");
  const [lastSubmittedAt, setLastSubmittedAt] = useState(null);

  const DUEL_DURATION_SECONDS = 300;
  const [battleStarted, setBattleStarted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(null);
  const [isRunning, setIsRunning] = useState(false);
  const [isOver, setIsOver] = useState(false);
  const [winnerMessage, setWinnerMessage] = useState("");

  // login required
  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  // fetch problems once
  useEffect(() => {
    async function fetchProblems() {
      try {
        setLoadingProblems(true);
        const res = await axios.get(`${API_URL}/problems`);
        setProblems(res.data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load problems list");
      } finally {
        setLoadingProblems(false);
      }
    }
    fetchProblems();
  }, []);

  // fetch problem details (NOT start timer)
  useEffect(() => {
    if (!selectedSlug) {
      setProblem(null);
      setCode("// Pick a problem to start battling!\n");
      return;
    }

    async function fetchProblem() {
      try {
        const res = await axios.get(`${API_URL}/problems/${selectedSlug}`);
        setProblem(res.data);

        const starter =
          res.data.starterCode?.[language] ||
          res.data.starterCode ||
          "// Write your solution here\n";
        setCode(starter);
      } catch (err) {
        console.error(err);
        setError("Failed to load problem");
      }
    }
    fetchProblem();
  }, [selectedSlug, language]);

  // start battle function
  function startBattle() {
    if (!selectedSlug) {
      alert("Choose a problem first!");
      return;
    }
    setBattleStarted(true);
    setTimeLeft(DUEL_DURATION_SECONDS);
    setIsRunning(true);
    setIsOver(false);
    setWinnerMessage("");
    setLastStatus("Not submitted");
    setLastSubmittedAt(null);
    setOutput("");
  }

  // TIMER EFFECT
  useEffect(() => {
    if (!isRunning || timeLeft === null || isOver) return;

    if (timeLeft <= 0) {
      setIsRunning(false);
      setIsOver(true);

      if (lastStatus === "Accepted") {
        setWinnerMessage("â± Time's up â€“ You WIN! (Accepted)");
      } else {
        setWinnerMessage("â± Time's up â€“ No Accepted. You Lose.");
      }
      return;
    }

    const timerId = setInterval(() => {
      setTimeLeft((prev) => (prev !== null ? prev - 1 : prev));
    }, 1000);

    return () => clearInterval(timerId);
  }, [isRunning, timeLeft, isOver, lastStatus]);

  const fullLink = `${window.location.origin}/duel-room/${roomId}`;

  async function handleCopyLink() {
    try {
      await navigator.clipboard.writeText(fullLink);
      alert("Room link copied!");
    } catch (e) {
      console.error(e);
    }
  }

  async function handleRun() {
    if (!battleStarted || isOver) return;
    setLoading(true);
    setOutput("Running...");
    try {
      const res = await axios.post(`${API_URL}/submissions/run`, {
        code,
        language,
      });
      setOutput(res.data.output || "No Output");
    } catch (err) {
      setOutput("Run error");
    } finally {
      setLoading(false);
    }
  }

  async function handleSubmit() {
    if (!battleStarted || isOver) {
      setOutput("Start battle first!");
      return;
    }
    if (!problem) {
      setOutput("Choose a problem first");
      return;
    }

    setLoading(true);
    setOutput("Submitting...");
    try {
      const res = await axios.post(
        `${API_URL}/submissions`,
        { code, language, problemId: problem._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const status = res.data.status || "Submitted";
      setLastStatus(status);
      setLastSubmittedAt(new Date().toISOString());
      setOutput(`Result: ${status}`);

      if (status === "Accepted") {
        setWinnerMessage("ðŸŽ‰ Accepted! You WIN the duel!");
        setIsRunning(false);
        setIsOver(true);
      }
    } catch (err) {
      setLastStatus("Error");
      setOutput("Submit Error");
    } finally {
      setLoading(false);
    }
  }

  const formatTime = (sec) => {
    if (sec === null) return "--:--";
    return `${String(Math.floor(sec / 60)).padStart(2, "0")}:${String(
      sec % 60
    ).padStart(2, "0")}`;
  };

  if (!user) return null;

  return (
    <div className="page-container">
      <div className="page-card battle-header-card">
        <h1 className="page-heading">1v1 Duel Room: {roomId}</h1>

        <div className="battle-link-row">
          <input type="text" value={fullLink} readOnly className="input-control" />
          <button className="btn btn-secondary" onClick={handleCopyLink}>
            Copy Room Link
          </button>
        </div>

        <div className="battle-status-box">
          <h3>Status: {lastStatus}</h3>
          <h3>Time Left: {formatTime(timeLeft)}</h3>
        </div>

        {!battleStarted && !isOver && (
          <button className="btn btn-primary" onClick={startBattle}>
            Start Battle
          </button>
        )}

        {winnerMessage && (
          <h2 style={{ marginTop: "12px", color: "yellow" }}>{winnerMessage}</h2>
        )}
      </div>

      <div className="problem-layout">
        <div className="problem-left">
          <label>Select Problem</label>
          <select
            value={selectedSlug}
            onChange={(e) => setSelectedSlug(e.target.value)}
            className="select-control"
            disabled={battleStarted}
          >
            <option value="">-- Choose Problem --</option>
            {problems.map((p) => (
              <option key={p._id} value={p.slug}>
                {p.title} ({p.difficulty})
              </option>
            ))}
          </select>

          {problem ? (
            <>
              <h2>{problem.title}</h2>
              <p>{problem.description}</p>
            </>
          ) : (
            <p>Select a problem to view details</p>
          )}
        </div>

        <div className="problem-right">
          <div className="problem-controls">
            <button
              className="btn-run"
              disabled={!battleStarted || isOver}
              onClick={handleRun}
            >
              Run
            </button>
            <button
              className="btn-submit"
              disabled={!battleStarted || isOver}
              onClick={handleSubmit}
            >
              Submit
            </button>
          </div>

          <Editor
            height="55vh"
            theme="vs-dark"
            language={language}
            value={code}
            onChange={(v) => battleStarted && !isOver && setCode(v)}
            options={{ readOnly: !battleStarted || isOver }}
          />

          <pre className="problem-output">{output}</pre>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
