import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ContestsList() {
  const [contests, setContests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchContests() {
      try {
        const res = await axios.get("http://localhost:5000/contests");
        setContests(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load contests");
      } finally {
        setLoading(false);
      }
    }
    fetchContests();
  }, []);

  if (loading) return <p>Loading contests...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Contests</h2>

      {contests.length === 0 && <p>No contests yet.</p>}

      {contests.map((c) => (
        <div
          key={c._id}
          style={{
            border: "1px solid #ddd",
            padding: 15,
            borderRadius: 8,
            marginBottom: 10,
            background: "#fafafa",
          }}
        >
          <h3>{c.name}</h3>
          {c.description && <p>{c.description}</p>}
          <p style={{ fontSize: 14, color: "#555" }}>
            Start: {new Date(c.startTime).toLocaleString()} <br />
            End: {new Date(c.endTime).toLocaleString()}
          </p>
          <Link to={`/contests/${c._id}`}>View Contest</Link>
        </div>
      ))}
    </div>
  );
}

export default ContestsList;
