// src/pages/MySubmissions.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API_URL || "http://localhost:5000";

export default function MySubmissions() {
  const [token, setToken] = useState(() => localStorage.getItem("token"));
  const [subs, setSubs] = useState([]);
  const [errMsg, setErrMsg] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    function onStorage(e) {
      if (e.key === "token") setToken(e.newValue);
    }
    window.addEventListener("storage", onStorage);
    return () => window.removeEventListener("storage", onStorage);
  }, []);

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      setErrMsg("");
      try {
        if (!token) {
          setErrMsg("Not authenticated");
          setSubs([]);
          return;
        }

        const res = await axios.get(`${API}/submissions/mine`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = res.data;
        if (!cancelled) {
          if (Array.isArray(data)) setSubs(data);
          else setSubs(data.submissions || []);
        }
      } catch (err) {
        console.error("Load submissions error:", err);
        if (!cancelled) {
          setErrMsg(err.response?.data?.message || err.message || "Request failed");
          setSubs([]);
        }
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => {
      cancelled = true;
    };
  }, [token]);

  if (loading) return <div className="muted">Loading...</div>;
  if (errMsg) return <div className="muted">Failed to load submissions: {errMsg}</div>;

  return (
    <div style={{ padding: 12 }}>
      {subs.length === 0 ? (
        <div>No submissions</div>
      ) : (
        subs.map((s) => (
          <div
            key={s._id || s.id}
            style={{ borderBottom: "1px solid rgba(0,0,0,0.08)", padding: "8px 0" }}
          >
            <div style={{ fontWeight: 700 }}>
              {s.problem && (s.problem.title || s.problem) ? (s.problem.title || s.problem) : "Problem"}
            </div>
            <div style={{ color: "#666", fontSize: 13 }}>
              {s.accepted ? "AC" : "—"} • {new Date(s.createdAt || Date.now()).toLocaleString()}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
