// src/pages/TeamsPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function TeamsPage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [myTeams, setMyTeams] = useState([]);
  const [loadingMyTeams, setLoadingMyTeams] = useState(true);

  const [teamName, setTeamName] = useState("");
  const [memberIds, setMemberIds] = useState("");

  const [joinTeamId, setJoinTeamId] = useState("");

  const [message, setMessage] = useState("");
  const [creating, setCreating] = useState(false);
  const [joining, setJoining] = useState(false);

  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Load only "my teams"
  useEffect(() => {
    if (!token) return;

    async function fetchMyTeams() {
      try {
        setLoadingMyTeams(true);
        const res = await axios.get("http://localhost:5000/teams/mine", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMyTeams(res.data || []);
      } catch (err) {
        console.error("Fetch my teams error:", err);
        setMessage("Failed to load your teams.");
      } finally {
        setLoadingMyTeams(false);
      }
    }

    fetchMyTeams();
  }, [token]);

  async function handleCreateTeam(e) {
    e.preventDefault();
    setMessage("");

    if (!teamName.trim()) {
      setMessage("Team name is required.");
      return;
    }

    try {
      setCreating(true);

      const ids =
        memberIds.trim().length > 0
          ? memberIds
              .split(",")
              .map((s) => s.trim())
              .filter(Boolean)
          : [];

      const res = await axios.post(
        "http://localhost:5000/teams",
        {
          name: teamName.trim(),
          memberIds: ids,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const newTeam = res.data;

      setMessage("Team created successfully!");
      setTeamName("");
      setMemberIds("");

      // Newly created team is one of "my teams"
      setMyTeams((prev) => [...prev, newTeam]);
    } catch (err) {
      console.error("Create team error:", err);
      setMessage(
        err.response?.data?.message || "Failed to create team. Try again."
      );
    } finally {
      setCreating(false);
    }
  }

  async function handleJoinById(e) {
    e.preventDefault();
    setMessage("");

    if (!joinTeamId.trim()) {
      setMessage("Please enter a Team ID.");
      return;
    }
    if (!token) {
      setMessage("You must be logged in to join a team.");
      return;
    }

    try {
      setJoining(true);

      await axios.post(
        `http://localhost:5000/teams/${joinTeamId.trim()}/join`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Joined team successfully.");

      // Refresh my teams
      const res = await axios.get("http://localhost:5000/teams/mine", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMyTeams(res.data || []);
      setJoinTeamId("");
    } catch (err) {
      console.error("Join team error:", err);
      setMessage(
        err.response?.data?.message || "Failed to join team. Check Team ID."
      );
    } finally {
      setJoining(false);
    }
  }

  if (!user) return null;

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <h2>Teams</h2>
      <p style={{ color: "#555" }}>
        Create a team or join an existing one using its <b>Team ID</b>.  
        Teams are used in <b>Team Battles</b>.
      </p>

      {message && (
        <p
          style={{
            color: message.toLowerCase().includes("fail") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}

      {/* Create team */}
      <div
        style={{
          border: "1px solid #ddd",
          padding: 15,
          borderRadius: 8,
          marginBottom: 25,
          background: "#fafafa",
        }}
      >
        <h3>Create New Team</h3>
        <form onSubmit={handleCreateTeam}>
          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Team Name*</b>
              <br />
              <input
                type="text"
                value={teamName}
                onChange={(e) => setTeamName(e.target.value)}
                style={{ width: "100%", padding: 6 }}
              />
            </label>
          </div>

          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Member IDs (optional, comma-separated)</b>
              <br />
              <textarea
                value={memberIds}
                onChange={(e) => setMemberIds(e.target.value)}
                placeholder="Paste MongoDB user IDs, separated by commas"
                style={{ width: "100%", padding: 6, minHeight: 60 }}
              />
            </label>
            <small style={{ color: "#777" }}>
              You will automatically be added as captain and member.
            </small>
          </div>

          <button type="submit" disabled={creating}>
            {creating ? "Creating..." : "Create Team"}
          </button>
        </form>
      </div>

      {/* Join team by ID */}
      <div
        style={{
          border: "1px solid #ddd",
          padding: 15,
          borderRadius: 8,
          marginBottom: 25,
          background: "#f5f5ff",
        }}
      >
        <h3>Join Team by ID</h3>
        <form onSubmit={handleJoinById}>
          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Team ID*</b>
              <br />
              <input
                type="text"
                value={joinTeamId}
                onChange={(e) => setJoinTeamId(e.target.value)}
                placeholder="Enter Team ID (MongoDB _id)"
                style={{ width: "100%", padding: 6 }}
              />
            </label>
          </div>
          <button type="submit" disabled={joining}>
            {joining ? "Joining..." : "Join Team"}
          </button>
        </form>
      </div>

      {/* My teams */}
      <h3>My Teams</h3>
      {loadingMyTeams ? (
        <p>Loading your teams...</p>
      ) : myTeams.length === 0 ? (
        <p>You are not part of any team yet.</p>
      ) : (
        myTeams.map((team) => (
          <div
            key={team._id}
            style={{
              border: "1px solid #ddd",
              padding: 12,
              borderRadius: 8,
              marginBottom: 10,
            }}
          >
            <h4>
              {team.name}{" "}
              <span style={{ fontSize: 12, color: "#888" }}>
                (ID: {team._id})
              </span>
            </h4>
            <p style={{ fontSize: 14, color: "#555" }}>
              Members:{" "}
              {team.members?.map((m) => m.username).join(", ") || "Unknown"}
            </p>
          </div>
        ))
      )}
    </div>
  );
}

export default TeamsPage;
