// client/src/pages/DuelRoomPage.js
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useLocation } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

// Format seconds as mm:ss or hh:mm:ss
function formatSeconds(sec) {
  if (sec <= 0) return "00:00";
  const h = Math.floor(sec / 3600);
  const m = Math.floor((sec % 3600) / 60);
  const s = sec % 60;

  if (h > 0) {
    return `${h.toString().padStart(2, "0")}:${m
      .toString()
      .padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  }
  return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
}

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const { user, token } = useAuth();

  // Optional opponent info passed via navigation state
  const opponentName = location.state?.opponentName || "Opponent";
  const opponentId = location.state?.opponentId || null;

  // Timer state
  const [initialDuration] = useState(600); // 10 minutes
  const [timeLeft, setTimeLeft] = useState(initialDuration);
  const [isRunning, setIsRunning] = useState(false);
  const [isOver, setIsOver] = useState(false);

  // Simple local scores (can later be tied to actual AC count)
  const [myScore, setMyScore] = useState(0);
  const [opponentScore, setOpponentScore] = useState(0);

  // Result state
  const [winner, setWinner] = useState(null); // "A" | "B" | "draw"
  const [resultMsg, setResultMsg] = useState("");
  const [savingResult, setSavingResult] = useState(false);

  const handleStart = () => {
    if (isRunning || isOver) return;
    setTimeLeft(initialDuration);
    setIsRunning(true);
    setIsOver(false);
    setWinner(null);
    setResultMsg("");
    setMyScore(0);
    setOpponentScore(0);
  };

  const handleMySolve = () => {
    if (!isRunning || isOver) return;
    setMyScore((prev) => prev + 1);
  };

  const handleOpponentSolve = () => {
    if (!isRunning || isOver) return;
    setOpponentScore((prev) => prev + 1);
  };

  const decideWinner = async (reason = "time") => {
    if (isOver) return;
    setIsOver(true);
    setIsRunning(false);

    let duelWinner;
    if (myScore > opponentScore) duelWinner = "A";
    else if (opponentScore > myScore) duelWinner = "B";
    else duelWinner = "draw";

    setWinner(duelWinner);

    const readableWinner =
      duelWinner === "A"
        ? "You"
        : duelWinner === "B"
        ? opponentName
        : "Draw";

    // Not logged in — just show result locally
    if (!user || !token) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner} (not logged in — result not saved).`
      );
      return;
    }

    // No opponent ID — cannot update ratings
    if (!opponentId) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner}. (Opponent ID missing — ratings not updated.)`
      );
      return;
    }

    try {
      setSavingResult(true);

      const body = {
        playerAId: user._id || user.id,
        playerBId: opponentId,
        winner: duelWinner,
        problemId: location.state?.problemId || null,
        durationSeconds: initialDuration - timeLeft,
        roomId,
      };

      const res = await axios.post(`${API_URL}/duels/finish`, body, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const info = res.data?.ratings;
      if (info) {
        const me =
          duelWinner === "A"
            ? info.playerA
            : duelWinner === "B"
            ? info.playerB
            : info.playerA;
        setResultMsg(
          `Duel finished (${reason}). Winner: ${readableWinner}. Your rating: ${me.old} → ${me.new}.`
        );
      } else {
        setResultMsg(
          `Duel finished (${reason}). Winner: ${readableWinner}.`
        );
      }
    } catch (err) {
      console.error("duel finish error:", err.response?.data || err.message);
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner}. (Error saving result.)`
      );
    } finally {
      setSavingResult(false);
    }
  };

  // Timer effect
  useEffect(() => {
    if (!isRunning || isOver) return;
    if (timeLeft <= 0) {
      decideWinner("time");
      return;
    }

    const id = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isRunning, timeLeft, isOver]);

  const statusText = useMemo(() => {
    if (isOver) return "Duel finished";
    if (isRunning) return "Duel in progress";
    return "Waiting to start";
  }, [isRunning, isOver]);

  const handleCopyRoom = async () => {
    try {
      await navigator.clipboard.writeText(roomId);
    } catch {
      // ignore
    }
  };

  return (
    <div className="page-container">
      <div
        className="page-card"
        style={{
          borderRadius: 28,
          padding: 26,
          border: "1px solid #111827",
          background:
            "radial-gradient(circle at top, rgba(168,85,247,0.16), transparent 60%), #050509",
          boxShadow: "0 28px 90px rgba(0,0,0,0.95)",
        }}
      >
        {/* Header */}
        <div
          style={{
            marginBottom: 18,
            display: "flex",
            flexDirection: "column",
            gap: 6,
          }}
        >
          <h1 className="page-heading" style={{ marginBottom: 0 }}>
            1v1 Duel Room
          </h1>
          <div
            style={{
              display: "flex",
              alignItems: "baseline",
              gap: 10,
              flexWrap: "wrap",
            }}
          >
            <p className="page-subtitle" style={{ marginBottom: 0 }}>
              Share this room with your opponent and start the timer when both
              are ready.
            </p>
            <div
              style={{
                marginLeft: "auto",
                display: "flex",
                alignItems: "center",
                gap: 8,
              }}
            >
              <span
                style={{
                  fontSize: 12,
                  padding: "4px 10px",
                  borderRadius: 999,
                  border: "1px solid rgba(129,140,248,0.7)",
                  background: "rgba(15,23,42,0.9)",
                  color: "#c4b5fd",
                }}
              >
                Room ID: <strong>{roomId}</strong>
              </span>
              <button
                type="button"
                onClick={handleCopyRoom}
                className="btn btn-ghost-sm"
                style={{ fontSize: 11 }}
              >
                Copy
              </button>
            </div>
          </div>
        </div>

        {/* Main layout */}
        <div
          className="duel-top"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(3, minmax(0, 1fr))",
            gap: 18,
            marginTop: 18,
          }}
        >
          {/* You card */}
          <div
            className="duel-player"
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(34,197,94,0.7)",
              background:
                "radial-gradient(circle at top, rgba(22,163,74,0.25), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#bbf7d0",
              }}
            >
              You
            </h3>
            <p
              className="duel-score"
              style={{ fontSize: 40, fontWeight: 800, margin: "6px 0 10px" }}
            >
              {myScore}
            </p>
            <button
              className="btn btn-secondary"
              onClick={handleMySolve}
              disabled={!isRunning || isOver}
              style={{
                padding: "0.5rem 1rem",
                borderRadius: 999,
                fontSize: 13,
              }}
            >
              I solved one
            </button>
          </div>

          {/* Timer card */}
          <div
            className="duel-timer"
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(59,130,246,0.7)",
              background:
                "radial-gradient(circle at top, rgba(59,130,246,0.25), transparent 60%), #020617",
              textAlign: "center",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <div
              className="timer-label"
              style={{
                fontSize: 13,
                color: "#cbd5f5",
                marginBottom: 4,
              }}
            >
              {statusText}
            </div>
            <div
              className="timer-value"
              style={{
                fontSize: 32,
                fontWeight: 700,
                letterSpacing: 2,
                marginBottom: 10,
              }}
            >
              {formatSeconds(timeLeft)}
            </div>
            <button
              className="btn btn-primary"
              onClick={handleStart}
              disabled={isRunning}
              style={{
                padding: "0.55rem 1.4rem",
                borderRadius: 999,
                fontWeight: 600,
              }}
            >
              {isRunning ? "Running…" : "Start Duel"}
            </button>
            <button
              className="btn btn-ghost-sm"
              onClick={() => decideWinner("manual")}
              disabled={isOver || !isRunning}
              style={{ marginTop: 8, fontSize: 12 }}
            >
              End now & decide winner
            </button>
          </div>

          {/* Opponent card */}
          <div
            className="duel-player"
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(248,250,252,0.12)",
              background:
                "radial-gradient(circle at top, rgba(248,250,252,0.1), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#e5e7eb",
              }}
            >
              {opponentName}
            </h3>
            <p
              className="duel-score"
              style={{ fontSize: 40, fontWeight: 800, margin: "6px 0 10px" }}
            >
              {opponentScore}
            </p>
            <button
              className="btn btn-secondary"
              onClick={handleOpponentSolve}
              disabled={!isRunning || isOver}
              style={{
                padding: "0.5rem 1rem",
                borderRadius: 999,
                fontSize: 13,
              }}
            >
              Opponent solved
            </button>
          </div>
        </div>

        {/* Result / info section */}
        {resultMsg && (
          <div
            style={{
              marginTop: 20,
              padding: 12,
              borderRadius: 12,
              background: "rgba(15,23,42,0.9)",
              border: "1px solid rgba(148,163,184,0.5)",
              fontSize: "0.9rem",
            }}
          >
            {savingResult ? "Saving result… " : null}
            {resultMsg}
          </div>
        )}

        {!user && (
          <p className="page-muted" style={{ marginTop: 10 }}>
            You are not logged in, so duel results will not update ratings.
          </p>
        )}

        {!opponentId && (
          <p className="page-muted" style={{ marginTop: 6, fontSize: 12 }}>
            To enable rating updates for both players, navigate here with{" "}
            <code>opponentId</code> and <code>opponentName</code> set in{" "}
            <code>location.state</code>.
          </p>
        )}
      </div>
    </div>
  );
}

export default DuelRoomPage;
