// src/pages/DuelRoomPage.js
import React, { useEffect } from "react";
import { useParams, useLocation, useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function DuelRoomPage() {
  const { roomId } = useParams();
  const { user } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();

  // Protect route: must be logged in
  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  if (!user) return null;

  const fullLink = `${window.location.origin}/duel-room/${roomId}`;

  const handleCopyLink = async () => {
    try {
      await navigator.clipboard.writeText(fullLink);
      alert("Room link copied to clipboard!");
    } catch (err) {
      console.error("Copy failed:", err);
    }
  };

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">Room: {roomId}</h1>
        <p className="page-subtitle">
          Share this room link with your opponent. Once both of you are here,
          pick a problem and start solving!
        </p>

        <div
          style={{
            marginTop: "1rem",
            padding: "1rem 1.2rem",
            borderRadius: "16px",
            border: "1px solid rgba(255,255,255,0.15)",
            background: "rgba(255,255,255,0.03)",
          }}
        >
          <p style={{ marginBottom: "0.4rem", color: "#cccccc" }}>
            Room Link:
          </p>
          <input
            type="text"
            value={fullLink}
            readOnly
            className="input-control"
            style={{ width: "100%", marginBottom: "0.6rem" }}
          />
          <button onClick={handleCopyLink} className="btn btn-secondary">
            Copy Room Link
          </button>
        </div>

        <div style={{ marginTop: "1.5rem" }}>
          <h3 className="section-title">How to play</h3>
          <ul style={{ fontSize: "0.9rem", color: "#cfcfcf", paddingLeft: "1.2rem" }}>
            <li>Both players join this same room link.</li>
            <li>
              Open the <Link to="/problems">Problems</Link> page and agree on a
              problem.
            </li>
            <li>
              Each player opens the chosen problem and codes their solution (you
              can use the built-in editor).
            </li>
            <li>
              First to get <b>Accepted</b> wins the duel!
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
