// client/src/pages/Profile.js
import React, { useContext, useEffect, useState } from "react";
import api from "../utils/api";
import { AuthContext } from "../context/AuthContext";

const Profile = () => {
  const { token: contextToken } = useContext(AuthContext) || {};
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const getInitials = (name, username) => {
    if (name) {
      const parts = name.trim().split(" ");
      if (parts.length === 1) return parts[0][0].toUpperCase();
      return (parts[0][0] + parts[parts.length - 1][0]).toUpperCase();
    }
    if (username) return username[0].toUpperCase();
    return "?";
  };

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        setError("");

        const t = contextToken || localStorage.getItem("token");
        if (!t) {
          setError("You must be logged in to view your profile.");
          setLoading(false);
          return;
        }

        const headers = { Authorization: `Bearer ${t}` };

        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me", { headers }),
          api.get("/users/me/stats", { headers }),
        ]);

        setProfile(meRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error("Profile load error:", err?.response || err);
        const msg =
          err?.response?.data?.message ||
          err?.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [contextToken]);

  if (loading) {
    return <div style={{ padding: 24 }}>Loading profileâ€¦</div>;
  }

  if (error) {
    return (
      <div style={{ padding: 24, color: "red", fontWeight: 500 }}>{error}</div>
    );
  }

  if (!profile) {
    return <div style={{ padding: 24 }}>No profile data found.</div>;
  }

  const user = profile;
  const initials = getInitials(user.name, user.username);

  const rating =
    user.rating ??
    stats?.rating ??
    1500;

  const problemsSolved =
    stats?.totalSolved ??
    profile.totalSolved ??
    0;

  const totalSubmissions =
    stats?.totalSubmissions ??
    profile.totalSubmissions ??
    0;

  const easySolved =
    stats?.solvedEasy ??
    profile.solvedEasy ??
    0;

  const mediumSolved =
    stats?.solvedMedium ??
    profile.solvedMedium ??
    0;

  const hardSolved =
    stats?.solvedHard ??
    profile.solvedHard ??
    0;

  const acceptanceRate =
    totalSubmissions > 0
      ? Math.round((problemsSolved / totalSubmissions) * 100)
      : 0;

  const duelWins =
    profile.duelWins ??
    stats?.duelWins ??
    0;

  const duelLosses =
    profile.duelLosses ??
    stats?.duelLosses ??
    0;

  const difficultyStats = stats?.difficultyStats || {
    Easy: easySolved,
    Medium: mediumSolved,
    Hard: hardSolved,
  };

  const languageStats = stats?.languageStats || {};
  const recent = stats?.recent || [];

  return (
    <div
      style={{
        padding: "24px",
        maxWidth: "1100px",
        margin: "0 auto",
        fontFamily: "system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif",
      }}
    >
      {/* Top: avatar + basic info */}
      <div
        style={{
          display: "flex",
          gap: "16px",
          marginBottom: "24px",
          alignItems: "center",
        }}
      >
        <div
          style={{
            width: 64,
            height: 64,
            borderRadius: "50%",
            background: "#1d4ed8",
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 24,
            fontWeight: 600,
          }}
        >
          {initials}
        </div>
        <div>
          <div style={{ fontSize: 24, fontWeight: 600 }}>
            {user.username || "User"}
          </div>
          <div style={{ color: "#555" }}>{user.email}</div>
          <div style={{ color: "#777", fontSize: 14, marginTop: 4 }}>
            Joined:{" "}
            {user.createdAt
              ? new Date(user.createdAt).toLocaleDateString()
              : "--"}
          </div>
        </div>
      </div>

      {/* Stats cards */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
          gap: "16px",
          marginBottom: "24px",
        }}
      >
        <div
          style={{
            padding: "16px",
            borderRadius: 12,
            border: "1px solid #e5e7eb",
          }}
        >
          <div style={{ fontSize: 14, color: "#6b7280" }}>Rating</div>
          <div style={{ fontSize: 24, fontWeight: 700, marginTop: 4 }}>
            {rating}
          </div>
        </div>

        <div
          style={{
            padding: "16px",
            borderRadius: 12,
            border: "1px solid #e5e7eb",
          }}
        >
          <div style={{ fontSize: 14, color: "#6b7280" }}>Problems Solved</div>
          <div style={{ fontSize: 24, fontWeight: 700, marginTop: 4 }}>
            {problemsSolved}
          </div>
          <div
            style={{
              fontSize: 12,
              color: "#6b7280",
              marginTop: 8,
              display: "flex",
              gap: 8,
              flexWrap: "wrap",
            }}
          >
            <span>Easy: {easySolved}</span>
            <span>Medium: {mediumSolved}</span>
            <span>Hard: {hardSolved}</span>
          </div>
        </div>

        <div
          style={{
            padding: "16px",
            borderRadius: 12,
            border: "1px solid #e5e7eb",
          }}
        >
          <div style={{ fontSize: 14, color: "#6b7280" }}>Submissions</div>
          <div style={{ fontSize: 24, fontWeight: 700, marginTop: 4 }}>
            {totalSubmissions}
          </div>
          <div style={{ fontSize: 12, color: "#6b7280", marginTop: 8 }}>
            Acceptance Rate: {acceptanceRate}%
          </div>
        </div>

        <div
          style={{
            padding: "16px",
            borderRadius: 12,
            border: "1px solid #e57e7eb",
          }}
        >
          <div style={{ fontSize: 14, color: "#6b7280" }}>Duels</div>
          <div style={{ fontSize: 24, fontWeight: 700, marginTop: 4 }}>
            {duelWins}W / {duelLosses}L
          </div>
        </div>
      </div>

      {/* Difficulty distribution */}
      <div
        style={{
          marginBottom: 24,
          padding: 16,
          borderRadius: 12,
          border: "1px solid #e5e7eb",
        }}
      >
        <div style={{ fontWeight: 600, marginBottom: 12 }}>
          Difficulty Breakdown
        </div>
        <div
          style={{
            display: "flex",
            gap: 12,
            flexWrap: "wrap",
            fontSize: 14,
          }}
        >
          {["Easy", "Medium", "Hard"].map((key) => (
            <div
              key={key}
              style={{
                padding: "8px 12px",
                borderRadius: 999,
                background: "#f3f4f6",
              }}
            >
              {key}: {difficultyStats[key] || 0}
            </div>
          ))}
        </div>
      </div>

      {/* Language stats */}
      <div
        style={{
          marginBottom: 24,
          padding: 16,
          borderRadius: 12,
          border: "1px solid #e5e7eb",
        }}
      >
        <div style={{ fontWeight: 600, marginBottom: 12 }}>
          Languages Used
        </div>
        {Object.keys(languageStats).length === 0 ? (
          <div style={{ fontSize: 14, color: "#6b7280" }}>
            No submissions yet.
          </div>
        ) : (
          <div
            style={{
              display: "flex",
              gap: 8,
              flexWrap: "wrap",
              fontSize: 14,
            }}
          >
            {Object.entries(languageStats).map(([lang, count]) => (
              <div
                key={lang}
                style={{
                  padding: "6px 10px",
                  borderRadius: 999,
                  background: "#eff6ff",
                }}
              >
                {lang}: {count}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Recent submissions */}
      <div
        style={{
          padding: 16,
          borderRadius: 12,
          border: "1px solid #e5e7eb",
        }}
      >
        <div style={{ fontWeight: 600, marginBottom: 12 }}>
          Recent Submissions
        </div>
        {recent.length === 0 ? (
          <div style={{ fontSize: 14, color: "#6b7280" }}>
            No submissions yet.
          </div>
        ) : (
          <div style={{ overflowX: "auto" }}>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                fontSize: 14,
              }}
            >
              <thead>
                <tr
                  style={{
                    textAlign: "left",
                    borderBottom: "1px solid #e5e7eb",
                  }}
                >
                  <th style={{ padding: "8px" }}>Problem</th>
                  <th style={{ padding: "8px" }}>Difficulty</th>
                  <th style={{ padding: "8px" }}>Status</th>
                  <th style={{ padding: "8px" }}>Language</th>
                  <th style={{ padding: "8px" }}>Time</th>
                </tr>
              </thead>
              <tbody>
                {recent.map((sub) => (
                  <tr
                    key={sub._id}
                    style={{ borderBottom: "1px solid #f3f4f6" }}
                  >
                    <td style={{ padding: "8px" }}>
                      {sub.problem?.title || "Unknown problem"}
                    </td>
                    <td style={{ padding: "8px" }}>
                      {sub.problem?.difficulty || "-"}
                    </td>
                    <td style={{ padding: "8px" }}>{sub.status}</td>
                    <td style={{ padding: "8px" }}>
                      {sub.language || sub.lang || "-"}
                    </td>
                    <td style={{ padding: "8px" }}>
                      {sub.createdAt
                        ? new Date(sub.createdAt).toLocaleString()
                        : "-"}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
