// src/pages/DuelRoomPage.js
import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import { createDuelSocket } from "../utils/duelSocket";
import MonacoCodeRunner from "../components/MonacoCodeRunner";

const DEFAULT_LANGUAGE = "python";

const styles = {
  page: {
    padding: "20px 40px",
    color: "#f9fafb",
    background: "#020617",
    minHeight: "calc(100vh - 60px)",
    boxSizing: "border-box",
  },
  header: {
    display: "flex",
    flexDirection: "column",
    gap: "4px",
    marginBottom: "8px",
  },
  title: {
    fontSize: "28px",
    fontWeight: 700,
    margin: 0,
  },
  status: {
    fontSize: "14px",
    opacity: 0.85,
  },
  substatus: {
    fontSize: "15px",
    marginBottom: "14px",
    opacity: 0.8,
  },
  main: {
    display: "grid",
    gridTemplateColumns: "minmax(0, 1.05fr) minmax(0, 1.7fr)",
    gap: "18px",
    alignItems: "stretch",
  },
  problemPanel: {
    background: "#020617",
    borderRadius: "14px",
    padding: "16px 18px",
    border: "1px solid #1f2937",
    boxShadow: "0 0 0 1px rgba(15, 23, 42, 0.6)",
    maxHeight: "calc(100vh - 170px)",
    overflowY: "auto",
  },
  problemTitle: {
    fontSize: "20px",
    marginBottom: "8px",
  },
  problemDescription: {
    fontSize: "14px",
    lineHeight: 1.5,
    marginBottom: "12px",
    whiteSpace: "pre-wrap",
  },
  problemSubheading: {
    marginTop: "14px",
    marginBottom: "6px",
    fontSize: "15px",
  },
  problemConstraints: {
    background: "#020617",
    borderRadius: "8px",
    border: "1px solid #111827",
    padding: "8px 10px",
    fontSize: "13px",
    whiteSpace: "pre-wrap",
  },
  exampleBox: {
    background: "#020617",
    borderRadius: "10px",
    padding: "10px 12px",
    marginBottom: "10px",
    border: "1px solid #111827",
  },
  exampleBlock: {
    marginBottom: "6px",
  },
  examplePre: {
    margin: "4px 0 0 0",
    fontSize: "13px",
    whiteSpace: "pre-wrap",
  },
  waitingProblemTitle: {
    margin: "0 0 4px 0",
  },
  waitingProblemText: {
    margin: 0,
    fontSize: "14px",
    opacity: 0.8,
  },
  editorPanel: {
    background: "#020617",
    borderRadius: "14px",
    padding: "8px",
    border: "1px solid #1f2937",
    boxShadow: "0 0 0 1px rgba(15, 23, 42, 0.6)",
    maxHeight: "calc(100vh - 170px)",
    display: "flex",
    flexDirection: "column",
  },
  editorPanelChild: {
    flex: 1,
    minHeight: 0,
  },
};

export default function DuelRoomPage() {
  const { roomId } = useParams();
  const [socket, setSocket] = useState(null);
  const [problem, setProblem] = useState(null);
  const [status, setStatus] = useState("Waiting to start");
  const [language, setLanguage] = useState(DEFAULT_LANGUAGE);
  const [code, setCode] = useState("");
  const [runOutput, setRunOutput] = useState("");
  const [isRunning, setIsRunning] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // ---------------------------- SOCKET SETUP ----------------------------
  useEffect(() => {
    const s = createDuelSocket(roomId);
    setSocket(s);

    s.on("problemSelected", (prob) => {
      setProblem(prob);
      setStatus("Problem received");
    });

    s.on("duelStarted", () => setStatus("Duel in progress"));

    s.on("runResult", (data) => {
      setIsRunning(false);
      setRunOutput(data.output || data.error || "");
    });

    s.on("submitResult", (res) => {
      setIsSubmitting(false);
      setRunOutput(res.message || "");
    });

    s.on("duelEnded", (res) => {
      setStatus(res.message || "Duel ended");
    });

    return () => {
      s.disconnect();
    };
  }, [roomId]);

  // ---------------------------- ACTIONS ----------------------------
  const handleRun = useCallback(() => {
    if (!socket) return;
    setIsRunning(true);
    setRunOutput("");
    socket.emit("runCode", { code, language, roomId });
  }, [socket, code, language, roomId]);

  const handleSubmit = useCallback(() => {
    if (!socket) return;
    setIsSubmitting(true);
    setRunOutput("");
    socket.emit("submitCode", { code, language, roomId });
  }, [socket, code, language, roomId]);

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <h1 style={styles.title}>1v1 Duel Room</h1>
        <div style={styles.status}>{status}</div>
      </header>

      <div style={styles.substatus}>
        {problem ? "Problem loaded" : "Waiting for problem..."}
      </div>

      <div style={styles.main}>
        {/* -------- LEFT: Problem Statement -------- */}
        <section style={styles.problemPanel}>
          {problem ? (
            <>
              <h2 style={styles.problemTitle}>{problem.title}</h2>
              <p style={styles.problemDescription}>{problem.description}</p>

              {problem?.constraints && (
                <>
                  <h3 style={styles.problemSubheading}>Constraints</h3>
                  <pre style={styles.problemConstraints}>
                    {problem.constraints}
                  </pre>
                </>
              )}

              {problem?.exampleTests?.length > 0 && (
                <>
                  <h3 style={styles.problemSubheading}>Examples</h3>
                  {problem.exampleTests.map((ex, idx) => (
                    <div key={idx} style={styles.exampleBox}>
                      <div style={styles.exampleBlock}>
                        <strong>Input:</strong>
                        <pre style={styles.examplePre}>{ex.input}</pre>
                      </div>
                      <div style={styles.exampleBlock}>
                        <strong>Output:</strong>
                        <pre style={styles.examplePre}>{ex.output}</pre>
                      </div>
                      {ex.explanation && (
                        <div style={styles.exampleBlock}>
                          <strong>Explanation:</strong>
                          <pre style={styles.examplePre}>
                            {ex.explanation}
                          </pre>
                        </div>
                      )}
                    </div>
                  ))}
                </>
              )}
            </>
          ) : (
            <div>
              <h3 style={styles.waitingProblemTitle}>Waiting for problemâ€¦</h3>
              <p style={styles.waitingProblemText}>
                The duel will start as soon as a problem is selected.
              </p>
            </div>
          )}
        </section>

        {/* -------- RIGHT: Monaco Code Runner -------- */}
        <section style={styles.editorPanel}>
          <div style={styles.editorPanelChild}>
            <MonacoCodeRunner
              value={code}
              onChange={setCode}
              language={language}
              onLanguageChange={setLanguage}
              onRun={handleRun}
              onSubmit={handleSubmit}
              runOutput={runOutput}
              allowSubmit={true}
              isRunning={isRunning}
              isSubmitting={isSubmitting}
            />
          </div>
        </section>
      </div>
    </div>
  );
}
