// client/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { useAuth } from "../contexts/AuthContext";

function formatDate(d) {
  if (!d) return "—";
  const date = new Date(d);
  return date.toLocaleDateString(undefined, {
    year: "numeric",
    month: "short",
    day: "numeric",
  });
}

const emptyDifficulty = { Easy: 0, Medium: 0, Hard: 0 };

const Profile = () => {
  const { user: authUser } = useAuth();

  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState({
    difficultyStats: emptyDifficulty,
    recent: [],
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    let cancelled = false;

    async function load() {
      try {
        setLoading(true);
        setError("");

        // basic profile: username, email, rating, totals…
        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me"),
          api.get("/users/me/stats"),
        ]);

        if (cancelled) return;

        setProfile(meRes.data || null);
        setStats(statsRes.data || { difficultyStats: emptyDifficulty, recent: [] });
      } catch (err) {
        if (cancelled) return;
        console.error("Profile load error:", err);
        const msg =
          err.response?.data?.message ||
          err.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    load();
    return () => {
      cancelled = true;
    };
  }, []);

  if (!authUser) {
    return (
      <div className="app-page" style={{ paddingTop: 40 }}>
        <h2 style={{ color: "#f9fafb" }}>Please log in to view your profile.</h2>
      </div>
    );
  }

  if (loading || !profile) {
    return (
      <div className="app-page" style={{ paddingTop: 40 }}>
        <h2 style={{ color: "#f9fafb" }}>Loading profile…</h2>
      </div>
    );
  }

  // ----- Derived stats -----
  const difficultyStats = stats?.difficultyStats || emptyDifficulty;

  const totalSolvedFromUser = profile.totalSolved ?? 0;
  const totalSolvedFromAgg =
    (difficultyStats.Easy || 0) +
    (difficultyStats.Medium || 0) +
    (difficultyStats.Hard || 0);

  // prefer value stored on user, fall back to aggregate
  const totalSolved =
    totalSolvedFromUser > 0 ? totalSolvedFromUser : totalSolvedFromAgg;

  const totalSubmissions =
    profile.totalSubmissions ??
    (stats?.recent ? stats.recent.length : 0) ??
    0;

  const memberSince = profile.createdAt || profile.created_at;

  const rating = profile.rating ?? 1500;
  const duelWins = profile.duelWins ?? 0;
  const duelLosses = profile.duelLosses ?? 0;
  const contestsPlayed = profile.contestsPlayed ?? 0;
  const contestsWon = profile.contestsWon ?? 0;

  // avatar initials
  const initials = (profile.username || "User")
    .slice(0, 2)
    .toUpperCase();

  return (
    <div className="app-page" style={{ padding: "32px 24px" }}>
      {/* main container */}
      <div
        style={{
          maxWidth: 1100,
          margin: "0 auto",
          background:
            "radial-gradient(circle at top left, rgba(59,130,246,0.13), transparent 55%), #020617",
          borderRadius: 22,
          padding: 24,
          boxShadow: "0 24px 80px rgba(0,0,0,0.8)",
          border: "1px solid rgba(148,163,184,0.12)",
        }}
      >
        {/* header row */}
        <div
          style={{
            display: "flex",
            gap: 20,
            alignItems: "center",
            padding: "12px 8px 18px",
            borderBottom: "1px solid rgba(15,23,42,0.8)",
          }}
        >
          <div
            style={{
              width: 64,
              height: 64,
              borderRadius: "999px",
              background:
                "linear-gradient(135deg, #22c55e, #14b8a6, #3b82f6)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: 700,
              fontSize: 22,
              color: "#020617",
              boxShadow: "0 0 0 4px rgba(15,23,42,1)",
            }}
          >
            {initials}
          </div>

          <div style={{ flex: 1 }}>
            <div
              style={{
                display: "flex",
                alignItems: "baseline",
                gap: 10,
                marginBottom: 4,
              }}
            >
              <h1
                style={{
                  margin: 0,
                  fontSize: 26,
                  fontWeight: 700,
                  color: "#f9fafb",
                }}
              >
                {profile.username}
              </h1>
              <span
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                }}
              >
                @{profile.username}
              </span>
            </div>
            <div style={{ fontSize: 13, color: "#9ca3af" }}>
              No bio yet.
            </div>
          </div>

          <div
            style={{
              display: "flex",
              gap: 16,
              flexWrap: "wrap",
              justifyContent: "flex-end",
            }}
          >
            <div
              style={{
                padding: "6px 14px",
                borderRadius: 999,
                border: "1px solid rgba(148,163,184,0.35)",
                fontSize: 13,
                color: "#e5e7eb",
                background: "rgba(15,23,42,0.9)",
              }}
            >
              Rating:{" "}
              <span style={{ fontWeight: 600, color: "#38bdf8" }}>
                {rating}
              </span>
            </div>
            <div
              style={{
                padding: "6px 14px",
                borderRadius: 999,
                border: "1px solid rgba(52,211,153,0.35)",
                fontSize: 13,
                color: "#bbf7d0",
                background: "rgba(6,95,70,0.35)",
              }}
            >
              Solved:{" "}
              <span style={{ fontWeight: 600 }}>{totalSolved}</span>
            </div>
          </div>
        </div>

        {error && (
          <div
            style={{
              marginTop: 12,
              marginBottom: 4,
              padding: "8px 12px",
              borderRadius: 10,
              background: "rgba(239,68,68,0.12)",
              color: "#fecaca",
              fontSize: 13,
            }}
          >
            {error}
          </div>
        )}

        {/* stats grid */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(0, 2fr) minmax(0, 1.4fr)",
            gap: 16,
            marginTop: 18,
          }}
        >
          {/* overview card */}
          <div
            style={{
              background:
                "linear-gradient(145deg, rgba(15,23,42,0.95), rgba(15,23,42,0.8))",
              borderRadius: 16,
              border: "1px solid rgba(30,64,175,0.7)",
              boxShadow: "0 18px 60px rgba(15,23,42,0.9)",
              padding: 16,
            }}
          >
            <div
              style={{
                fontSize: 14,
                fontWeight: 600,
                color: "#e5e7eb",
                marginBottom: 10,
              }}
            >
              Overview
            </div>

            <dl
              style={{
                margin: 0,
                display: "grid",
                gridTemplateColumns: "minmax(0,1fr) minmax(0,1fr)",
                rowGap: 10,
                columnGap: 16,
                fontSize: 13,
                color: "#9ca3af",
              }}
            >
              <div>
                <dt>Member since</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  {formatDate(memberSince)}
                </dd>
              </div>
              <div>
                <dt>Email</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  {profile.email || "—"}
                </dd>
              </div>

              <div>
                <dt>Problems solved</dt>
                <dd style={{ margin: 0, color: "#bbf7d0" }}>
                  {totalSolved}
                </dd>
              </div>
              <div>
                <dt>Total submissions</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  {totalSubmissions}
                </dd>
              </div>

              <div>
                <dt>Solved by difficulty</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  <span style={{ color: "#4ade80" }}>
                    E: {difficultyStats.Easy || 0}
                  </span>{" "}
                  ·{" "}
                  <span style={{ color: "#facc15" }}>
                    M: {difficultyStats.Medium || 0}
                  </span>{" "}
                  ·{" "}
                  <span style={{ color: "#f97373" }}>
                    H: {difficultyStats.Hard || 0}
                  </span>
                </dd>
              </div>
            </dl>
          </div>

          {/* competitive stats card */}
          <div
            style={{
              background:
                "linear-gradient(145deg, rgba(15,23,42,0.95), rgba(15,23,42,0.8))",
              borderRadius: 16,
              border: "1px solid rgba(147,51,234,0.7)",
              boxShadow: "0 18px 60px rgba(15,23,42,0.9)",
              padding: 16,
            }}
          >
            <div
              style={{
                fontSize: 14,
                fontWeight: 600,
                color: "#e5e7eb",
                marginBottom: 10,
              }}
            >
              Competitive Stats
            </div>

            <dl
              style={{
                margin: 0,
                display: "grid",
                gridTemplateColumns: "minmax(0,1fr) minmax(0,1fr)",
                rowGap: 10,
                columnGap: 16,
                fontSize: 13,
                color: "#9ca3af",
              }}
            >
              <div>
                <dt>Duel record</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  {duelWins}W / {duelLosses}L
                </dd>
              </div>
              <div>
                <dt>Contests</dt>
                <dd style={{ margin: 0, color: "#e5e7eb" }}>
                  {contestsPlayed} played · {contestsWon} won
                </dd>
              </div>
            </dl>
          </div>
        </div>

        {/* recent submissions preview (from /users/me/stats) */}
        {stats.recent && stats.recent.length > 0 && (
          <div style={{ marginTop: 22 }}>
            <div
              style={{
                fontSize: 14,
                fontWeight: 600,
                color: "#e5e7eb",
                marginBottom: 8,
              }}
            >
              Recent submissions
            </div>
            <div
              style={{
                borderRadius: 14,
                border: "1px solid rgba(31,41,55,0.9)",
                overflow: "hidden",
              }}
            >
              {stats.recent.slice(0, 5).map((sub) => (
                <div
                  key={sub._id}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "8px 14px",
                    background: "#020617",
                    borderBottom: "1px solid rgba(15,23,42,0.95)",
                    fontSize: 13,
                    color: "#e5e7eb",
                  }}
                >
                  <div>
                    <div style={{ fontWeight: 500 }}>
                      {sub.problem?.title || "Unknown problem"}
                    </div>
                    <div style={{ color: "#9ca3af", fontSize: 12 }}>
                      {new Date(sub.createdAt).toLocaleString()}
                    </div>
                  </div>
                  <div
                    style={{
                      alignSelf: "center",
                      fontSize: 12,
                      padding: "2px 10px",
                      borderRadius: 999,
                      border: "1px solid rgba(148,163,184,0.4)",
                      background:
                        sub.status === "Accepted"
                          ? "rgba(22,163,74,0.18)"
                          : sub.status === "Rejected"
                          ? "rgba(239,68,68,0.18)"
                          : "rgba(37,99,235,0.18)",
                      color:
                        sub.status === "Accepted"
                          ? "#bbf7d0"
                          : sub.status === "Rejected"
                          ? "#fecaca"
                          : "#bfdbfe",
                    }}
                  >
                    {sub.status || "Submitted"}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
