// client/src/pages/DuelRoomPage.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { createDuelSocket } from "../utils/duelSocket";
import MonacoCodeRunner from "../components/MonacoCodeRunner";
import api from "../utils/api";

function formatSeconds(sec) {
  if (sec == null || Number.isNaN(sec)) return "00:00";
  if (sec <= 0) return "00:00";
  const h = Math.floor(sec / 3600);
  const m = Math.floor((sec % 3600) / 60);
  const s = sec % 60;
  if (h > 0) {
    return `${h.toString().padStart(2, "0")}:${m
      .toString()
      .padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  }
  return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
}

function difficultyBadgeMeta(difficulty) {
  const d = (difficulty || "").toLowerCase();
  if (d === "easy")
    return {
      text: "Easy",
      bg: "rgba(22,163,74,0.18)",
      border: "#16a34a",
      color: "#4ade80",
    };
  if (d === "medium")
    return {
      text: "Medium",
      bg: "rgba(245,158,11,0.18)",
      border: "#f59e0b",
      color: "#fbbf24",
    };
  if (d === "hard")
    return {
      text: "Hard",
      bg: "rgba(239,68,68,0.18)",
      border: "#ef4444",
      color: "#f87171",
    };
  return null;
}

function problemText(problem) {
  if (!problem) return "";
  return (
    problem.description ||
    problem.statement ||
    problem.body ||
    problem.content ||
    ""
  );
}

const DEFAULT_LANGUAGE = "python";

const LANGUAGE_SNIPPETS = {
  javascript: `// JavaScript template
function solve() {
  const fs = require("fs");
  const input = fs.readFileSync(0, "utf8").trim().split("\\n");
  // TODO: parse input and print answer
  console.log(input.length);
}

solve();`,
  python: `# Python template
import sys

def solve():
    data = sys.stdin.read().strip().splitlines()
    # TODO: parse data and print answer
    print(len(data))

if __name__ == "__main__":
    solve()`,
  cpp: `#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    vector<string> lines;
    string line;
    while (getline(cin, line)) {
        lines.push_back(line);
    }
    // TODO: parse lines and print answer
    cout << lines.size() << "\\n";
    return 0;
}`,
  java: `import java.io.*;
import java.util.*;

public class Main {
    public static void main(String[] args) throws Exception {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        List<String> lines = new ArrayList<>();
        String line;
        while ((line = br.readLine()) != null) {
            lines.add(line);
        }
        // TODO: parse lines and print answer
        System.out.println(lines.size());
    }
}`,
};

function DuelRoomPage() {
  const { roomId: routeRoomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { user, token } = useAuth();

  const roomId = routeRoomId;
  const myId = user?._id || user?.id;
  const isHost = location.state?.isHost ?? false;

  const [socket, setSocket] = useState(null);
  const [connecting, setConnecting] = useState(false);
  const [connError, setConnError] = useState("");

  const [players, setPlayers] = useState([]);
  const [problem, setProblem] = useState(null);
  const [duelStarted, setDuelStarted] = useState(false);
  const [duelEnded, setDuelEnded] = useState(false);
  const [winnerSlot, setWinnerSlot] = useState(null);
  const [endReason, setEndReason] = useState("");
  const [startAt, setStartAt] = useState(null);
  const [timeLimit, setTimeLimit] = useState(600);
  const [timeLeft, setTimeLeft] = useState(0);
  const [submissions, setSubmissions] = useState([]);

  const [language, setLanguage] = useState(DEFAULT_LANGUAGE);
  const [code, setCode] = useState(null);
  const [stdin, setStdin] = useState("");
  const [runOutput, setRunOutput] = useState("");
  const [isRunningCode, setIsRunningCode] = useState(false);
  const [runStatus, setRunStatus] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // default template
  useEffect(() => {
    setCode(LANGUAGE_SNIPPETS[DEFAULT_LANGUAGE]);
  }, []);

  // auth guard
  useEffect(() => {
    if (!user || !token) {
      navigate("/login", {
        state: {
          from: `/duel-room/${roomId}`,
          message: "You must be logged in to join a duel",
        },
      });
    }
  }, [user, token, navigate, roomId]);

  // socket setup
  useEffect(() => {
    if (!token) return;

    const s = createDuelSocket(token);
    setSocket(s);
    setConnecting(true);
    setConnError("");

    s.on("connect", () => {
      setConnecting(false);
      setConnError("");

      s.emit("join_duel", { duelId: roomId, userId: myId }, (resp) => {
        if (!resp?.ok && !resp?.roomId) {
          setConnError(resp?.message || "Failed to join room");
        }
      });
    });

    s.on("disconnect", () => {
      setConnError("Disconnected from server");
    });

    s.on("duel_error", (payload) => {
      setConnError(payload?.message || "Duel error");
    });

    s.on("room_update", ({ players: p, started }) => {
      setPlayers(p || []);
      setDuelStarted(!!started);
    });

    s.on("duel_started", async (payload = {}) => {
      let problemObj = payload.problem || payload.problemObj || null;
      const startedAt =
        payload.startedAt || payload.startedAtMs || payload.startAt || Date.now();
      const tl =
        payload.timeLimit || payload.timeLimitSecs || payload.timeLimitMs || 600;

      if (
        !problemObj &&
        (payload.problemId || payload.problem_id || payload.problemIdStr)
      ) {
        const id =
          payload.problemId || payload.problem_id || payload.problemIdStr;
        try {
          const resp = await api.get(`/problems/${id}`);
          problemObj = resp.data?.problem || resp.data;
        } catch (err) {
          console.warn("Failed to fetch problem", err);
        }
      }

      setProblem(problemObj || null);
      setStartAt(startedAt);
      setTimeLimit(typeof tl === "number" ? tl : 600);
      setDuelStarted(true);
      setDuelEnded(false);
      setWinnerSlot(null);
      setEndReason("");
      setSubmissions([]);
      setRunStatus("");
    });

    // submission result listener â€“ real verdict comes here
    const handleSubmissionEvent = (payload = {}) => {
      const userIdFromPayload =
        payload.userId || payload.user_id || payload.user?.id;

      const passedCount =
        payload.passedCount ??
        payload.passed ??
        payload.passedTests ??
        0;
      const totalTests =
        payload.total ??
        payload.totalTests ??
        payload.totalTestsCount ??
        0;

      const accepted =
        typeof passedCount === "number" &&
        typeof totalTests === "number" &&
        totalTests > 0 &&
        passedCount === totalTests;

      const userObj =
        payload.user ||
        {
          id: userIdFromPayload,
          name:
            userIdFromPayload === myId
              ? user?.name || user?.username || "You"
              : "Opponent",
        };

      const normalized = {
        user: userObj,
        accepted,
        passedTests: passedCount,
        totalTests,
        createdAt: payload.createdAt || payload.completedAt || Date.now(),
      };

      setSubmissions((prev) => [...prev, normalized]);

      if (userIdFromPayload === myId) {
        const labelBase = `Passed ${passedCount}/${totalTests} tests`;
        setRunStatus(
          accepted
            ? `âœ… Accepted â€“ ${labelBase}`
            : `âš ï¸ Not accepted â€“ ${labelBase}`
        );
      }
    };

    s.on("duel_submission_result", handleSubmissionEvent);
    s.on("duel_submission_update", handleSubmissionEvent);

    const onDuelFinished = ({ winner, reason, summary } = {}) => {
      setDuelEnded(true);
      setWinnerSlot(winner || null);
      setEndReason(reason || "");
      if (summary?.submissions) setSubmissions(summary.submissions);
      setTimeLeft(0);
    };
    s.on("duel_finished", onDuelFinished);
    s.on("duel_ended", onDuelFinished);

    return () => {
      try {
        s.emit("leave_duel", { roomId, userId: myId });
      } catch (e) {}
      s.removeAllListeners();
      s.disconnect();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, roomId, myId]);

  // timer
  useEffect(() => {
    if (!duelStarted || duelEnded || !startAt || !timeLimit) return;

    const tick = () => {
      const now = Date.now();
      const elapsed = Math.floor((now - startAt) / 1000);
      const remaining = Math.max(timeLimit - elapsed, 0);
      setTimeLeft(remaining);
    };

    tick();
    const id = setInterval(tick, 1000);
    return () => clearInterval(id);
  }, [duelStarted, duelEnded, startAt, timeLimit]);

  const me = useMemo(
    () => players.find((p) => p.id === myId || p.userId === myId),
    [players, myId]
  );
  const opponent = useMemo(
    () => players.find((p) => (p.id || p.userId) !== myId),
    [players, myId]
  );

  const statusText = useMemo(() => {
    if (connError) return "Error";
    if (connecting) return "Connectingâ€¦";
    if (!duelStarted) return "Waiting for players / start";
    if (duelEnded) return "Duel finished";
    return "Duel in progress";
  }, [connError, connecting, duelStarted, duelEnded]);

  const editorReadOnly = duelEnded || !!connError || connecting;
  const allowSubmit = duelStarted && !duelEnded && !connError;

  const badge = difficultyBadgeMeta(problem?.difficulty);
  const pText = problemText(problem);
  const examples =
    problem?.examples || problem?.exampleTests || [];
  const hasIO =
    problem?.inputFormat || problem?.outputFormat || problem?.constraints;

  const handleStart = () => {
    if (!socket) return;
    if ((players?.length || 0) < 2) {
      setConnError("Need at least 2 players to start the duel");
      return;
    }
    if (!isHost) {
      setConnError("Only the host can start the duel");
      return;
    }
    socket.emit("start_duel", { duelId: roomId }, (resp) => {
      if (!resp?.ok) {
        setConnError(resp?.message || "Failed to start duel");
      }
    });
  };

  const handleUseExampleInput = (input) => {
    if (!input) return;
    setStdin(input);
    const el = document.getElementById("duel-editor-section");
    if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
  };

  const handleLanguageChange = (nextLang) => {
    setLanguage(nextLang);
    setCode(LANGUAGE_SNIPPETS[nextLang] || "");
  };

  // *** FIX 1: use example input when stdin is empty ***
  const handleRunCode = async () => {
    const source = code ?? "";
    if (!source.trim()) {
      setRunOutput("Write some code before running.");
      return;
    }

    const exs = examples || [];
    const fallbackInput =
      exs[0]?.input || exs[0]?.inputText || "";

    const effectiveInput =
      stdin && stdin.trim().length > 0 ? stdin : fallbackInput;

    // If no input at all and the code clearly expects stdin, show a message
    const expectsInput =
      source.includes("input(") || // Python
      source.includes("fs.readFileSync(0") || // Node
      source.includes("readLine()"); // common in other langs

    if (!effectiveInput && expectsInput) {
      setRunOutput(
        "Your code expects input. Either type it in the Custom Input box or click 'Use as Input' on an example."
      );
      return;
    }

    setIsRunningCode(true);
    setRunOutput("Running via Judge0â€¦");
    try {
      const resp = await api.post("/judge/run", {
        code: source,
        language,
        input: effectiveInput,
      });
      const data = resp.data || {};
      const output =
        data.output ||
        data.stdout ||
        data?.raw?.stdout ||
        data?.raw?.outputFallback ||
        "Program finished with no output.";
      setRunOutput(output);
    } catch (err) {
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        err.message ||
        "Error running code.";
      setRunOutput(`Error: ${msg}`);
    } finally {
      setIsRunningCode(false);
    }
  };

  const handleSubmit = () => {
    if (!socket || !duelStarted || duelEnded) return;
    const source = code ?? "";
    if (!source.trim()) {
      setRunStatus("Write some code before submitting.");
      return;
    }
    setIsSubmitting(true);
    setRunStatus("");

    socket.emit(
      "duel_submit_code",
      { duelId: roomId, userId: myId, code: source, languageId: language, stdin },
      (resp) => {
        setIsSubmitting(false);
        if (!resp) {
          setRunStatus("No response from server");
          return;
        }
        if (!resp.ok) {
          setRunStatus(
            `Submit failed: ${resp.message || "Server error while submitting"}`
          );
          return;
        }
        // actual verdict will arrive via duel_submission_result event
        setRunStatus("Submission received â€“ waiting for evaluationâ€¦");
      }
    );
  };

  return (
    <div className="page-container">
      <div
        className="page-card"
        style={{
          borderRadius: 28,
          padding: 26,
          border: "1px solid #111827",
          background:
            "radial-gradient(circle at top, rgba(168,85,247,0.16), transparent 60%), #050509",
          boxShadow: "0 28px 90px rgba(0,0,0,0.95)",
          maxWidth: "min(1400px, 95vw)",
          margin: "0 auto",
        }}
      >
        {/* Header */}
        <div
          style={{
            marginBottom: 18,
            display: "flex",
            flexDirection: "column",
            gap: 6,
          }}
        >
          <h1 className="page-heading" style={{ marginBottom: 0 }}>
            1v1 Duel Room
          </h1>
          <p className="page-subtitle" style={{ marginBottom: 0 }}>
            Room ID: <strong>{roomId}</strong> â€” share this with your opponent.
            First to solve wins, or server decides when the timer expires.
          </p>
          <p style={{ fontSize: 12, margin: 0, color: "#9ca3af" }}>
            Status: {statusText} â€¢ Time left: {formatSeconds(timeLeft)}
          </p>
        </div>

        {connError && (
          <div
            style={{
              marginBottom: 14,
              padding: 10,
              borderRadius: 12,
              background: "rgba(127,29,29,0.9)",
              border: "1px solid rgba(248,113,113,0.7)",
              fontSize: 13,
              color: "#fee2e2",
            }}
          >
            {connError}
          </div>
        )}

        {/* Player cards */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
            gap: 18,
            marginBottom: 22,
          }}
        >
          {/* You */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(34,197,94,0.7)",
              background:
                "radial-gradient(circle at top, rgba(22,163,74,0.25), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
              maxWidth: 360,
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#bbf7d0",
              }}
            >
              You
            </h3>
            <p style={{ margin: 0, fontSize: 13, color: "#a7f3d0" }}>
              {me ? me.username || me.name : "Waiting..."}
            </p>
            <p
              style={{
                fontSize: 28,
                fontWeight: 800,
                margin: "8px 0 6px",
              }}
            >
              {me?.rating ?? "â€”"}
            </p>
            <p
              style={{
                fontSize: 11,
                color: "#bbf7d0",
                opacity: 0.85,
              }}
            >
              Rating
            </p>

            {!duelStarted && (
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleStart}
                disabled={
                  !socket || !!connError || players.length < 2 || !isHost
                }
                style={{ marginTop: 10, fontSize: 13 }}
              >
                {players.length < 2
                  ? "Waiting for opponentâ€¦"
                  : isHost
                  ? "Start Duel"
                  : "Waiting for hostâ€¦"}
              </button>
            )}

            {duelStarted && !duelEnded && (
              <p
                style={{
                  fontSize: 12,
                  color: "#cbd5f5",
                  marginTop: 4,
                }}
              >
                Duel in progressâ€¦
              </p>
            )}
          </div>

          {/* Opponent */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(248,250,252,0.12)",
              background:
                "radial-gradient(circle at top, rgba(248,250,252,0.1), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
              maxWidth: 360,
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#e5e7eb",
              }}
            >
              Opponent
            </h3>
            <p style={{ margin: 0, fontSize: 13, color: "#e5e7eb" }}>
              {opponent ? opponent.username || opponent.name : "Waiting..."}
            </p>
            <p
              style={{
                fontSize: 28,
                fontWeight: 800,
                margin: "8px 0 6px",
              }}
            >
              {opponent?.rating ?? "â€”"}
            </p>
            <p
              style={{
                fontSize: 11,
                color: "#9ca3af",
                opacity: 0.85,
              }}
            >
              Rating
            </p>
          </div>
        </div>

        {/* Duel result banner */}
        {duelEnded && (
          <div
            style={{
              marginBottom: 18,
              padding: 10,
              borderRadius: 12,
              background: "rgba(15,23,42,0.9)",
              border: "1px solid rgba(148,163,184,0.5)",
              fontSize: "0.9rem",
            }}
          >
            {winnerSlot === null || winnerSlot === "draw"
              ? "Duel finished: draw."
              : winnerSlot === (me?.slot || me?.id || myId)
              ? "Duel finished: You won! ðŸŽ‰"
              : "Duel finished: Opponent won."}
            {endReason && (
              <span style={{ opacity: 0.85 }}>
                {" "}
                (Reason: <code>{endReason}</code>)
              </span>
            )}
          </div>
        )}

        {/* Submission activity */}
        {submissions.length > 0 && (
          <div
            style={{
              marginBottom: 18,
              padding: 10,
              borderRadius: 12,
              background: "rgba(15,23,42,0.88)",
              border: "1px solid rgba(55,65,81,0.8)",
              maxHeight: 140,
              overflowY: "auto",
              fontSize: 12,
            }}
          >
            <div
              style={{
                marginBottom: 6,
                fontWeight: 600,
                fontSize: 13,
                color: "#e5e7eb",
              }}
            >
              Submission activity
            </div>
            {submissions.map((a, idx) => (
              <div
                key={idx}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  gap: 8,
                  padding: "2px 0",
                }}
              >
                <span>
                  <strong>{a.user?.name || a.user?.username || "Player"}</strong>{" "}
                  {a.accepted ? (
                    <span style={{ color: "#4ade80" }}>Accepted</span>
                  ) : (
                    <span style={{ color: "#fbbf24" }}>Not accepted</span>
                  )}
                </span>
                <span style={{ opacity: 0.8 }}>
                  {typeof a.passedTests === "number" &&
                  typeof a.totalTests === "number"
                    ? `${a.passedTests}/${a.totalTests}`
                    : ""}
                </span>
              </div>
            ))}
          </div>
        )}

        {/* Main problem + editor layout */}
        <div
          id="duel-editor-section"
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(260px, 420px) minmax(0, 1fr)",
            gap: 18,
          }}
        >
          {/* Problem panel */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              background: "#020617",
              border: "1px solid rgba(31,41,55,0.9)",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 8,
              }}
            >
              <h2
                style={{
                  fontSize: 18,
                  fontWeight: 600,
                  margin: 0,
                  color: "#e5e7eb",
                }}
              >
                {problem?.title || "Battle Problem"}
              </h2>
              {badge && (
                <span
                  style={{
                    padding: "4px 10px",
                    borderRadius: 999,
                    fontSize: 11,
                    fontWeight: 600,
                    background: badge.bg,
                    border: `1px solid ${badge.border}`,
                    color: badge.color,
                  }}
                >
                  {badge.text}
                </span>
              )}
            </div>

            <div style={{ fontSize: 13, color: "#d1d5db" }}>
              <h3
                style={{
                  fontSize: 13,
                  fontWeight: 600,
                  marginBottom: 4,
                  color: "#e5e7eb",
                }}
              >
                Description
              </h3>
              <p style={{ whiteSpace: "pre-wrap" }}>{pText}</p>

              {hasIO && (
                <div style={{ marginTop: 8 }}>
                  {problem.inputFormat && (
                    <div>
                      <h4
                        style={{
                          fontSize: 13,
                          fontWeight: 600,
                          marginBottom: 4,
                          color: "#e5e7eb",
                        }}
                      >
                        Input Format
                      </h4>
                      <p
                        style={{
                          fontSize: 13,
                          color: "#d1d5db",
                          whiteSpace: "pre-wrap",
                        }}
                      >
                        {problem.inputFormat}
                      </p>
                    </div>
                  )}
                  {problem.outputFormat && (
                    <div>
                      <h4
                        style={{
                          fontSize: 13,
                          fontWeight: 600,
                          marginBottom: 4,
                          color: "#e5e7eb",
                        }}
                      >
                        Output Format
                      </h4>
                      <p
                        style={{
                          fontSize: 13,
                          color: "#d1d5db",
                          whiteSpace: "pre-wrap",
                        }}
                      >
                        {problem.outputFormat}
                      </p>
                    </div>
                  )}
                  {problem.constraints && (
                    <div>
                      <h4
                        style={{
                          fontSize: 13,
                          fontWeight: 600,
                          marginBottom: 4,
                          color: "#e5e7eb",
                        }}
                      >
                        Constraints
                      </h4>
                      <pre
                        style={{
                          margin: 0,
                          padding: 10,
                          borderRadius: 10,
                          fontSize: 12,
                          background: "#020617",
                          border: "1px solid rgba(31,41,55,0.9)",
                          color: "#e5e7eb",
                          whiteSpace: "pre-wrap",
                        }}
                      >
                        {problem.constraints}
                      </pre>
                    </div>
                  )}
                </div>
              )}

              {examples.length > 0 && (
                <div style={{ marginTop: 4 }}>
                  <h3
                    style={{
                      fontSize: 14,
                      fontWeight: 600,
                      marginBottom: 8,
                      color: "#e5e7eb",
                    }}
                  >
                    Examples
                  </h3>
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      gap: 12,
                    }}
                  >
                    {examples.map((ex, idx) => (
                      <div
                        key={idx}
                        style={{
                          padding: 12,
                          borderRadius: 14,
                          background: "#020617",
                          border: "1px solid rgba(31,41,55,0.95)",
                        }}
                      >
                        <div
                          style={{
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                            marginBottom: 6,
                          }}
                        >
                          <span
                            style={{
                              fontSize: 13,
                              fontWeight: 600,
                              color: "#e5e7eb",
                            }}
                          >
                            Example {idx + 1}
                          </span>
                          <button
                            type="button"
                            className="btn btn-secondary"
                            style={{
                              padding: "4px 10px",
                              fontSize: 11,
                              borderRadius: 999,
                            }}
                            onClick={() => handleUseExampleInput(ex.input)}
                          >
                            Use as Input
                          </button>
                        </div>

                        {ex.input && (
                          <div style={{ marginBottom: 6 }}>
                            <span
                              style={{
                                fontSize: 12,
                                fontWeight: 600,
                                color: "#a5b4fc",
                              }}
                            >
                              Input:
                            </span>
                            <pre
                              style={{
                                margin: 0,
                                marginTop: 2,
                                padding: 8,
                                borderRadius: 8,
                                fontSize: 12,
                                background: "#020617",
                                border: "1px solid rgba(30,64,175,0.7)",
                                color: "#e5e7eb",
                                whiteSpace: "pre-wrap",
                              }}
                            >
                              {ex.input}
                            </pre>
                          </div>
                        )}

                        {ex.output && (
                          <div style={{ marginBottom: 6 }}>
                            <span
                              style={{
                                fontSize: 12,
                                fontWeight: 600,
                                color: "#6ee7b7",
                              }}
                            >
                              Output:
                            </span>
                            <pre
                              style={{
                                margin: 0,
                                marginTop: 2,
                                padding: 8,
                                borderRadius: 8,
                                fontSize: 12,
                                background: "#020617",
                                border: "1px solid rgba(22,163,74,0.7)",
                                color: "#e5e7eb",
                                whiteSpace: "pre-wrap",
                              }}
                            >
                              {ex.output}
                            </pre>
                          </div>
                        )}

                        {ex.explanation && (
                          <div>
                            <span
                              style={{
                                fontSize: 12,
                                fontWeight: 600,
                                color: "#facc15",
                              }}
                            >
                              Explanation:
                            </span>
                            <p
                              style={{
                                margin: 0,
                                marginTop: 2,
                                fontSize: 12,
                                color: "#e5e7eb",
                              }}
                            >
                              {ex.explanation}
                            </p>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Editor panel */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              background: "#020617",
              border: "1px solid rgba(31,41,55,0.9)",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 6,
              }}
            >
              <h2
                style={{
                  fontSize: 18,
                  fontWeight: 600,
                  margin: 0,
                  color: "#e5e7eb",
                }}
              >
                Your Code
              </h2>

              {/* FIX 2: dedicated Submit button (visible even if Monaco only shows Run) */}
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleSubmit}
                disabled={!allowSubmit || isSubmitting || editorReadOnly}
                style={{
                  padding: "6px 14px",
                  fontSize: 13,
                  borderRadius: 999,
                }}
              >
                {isSubmitting ? "Submittingâ€¦" : "Submit to Duel"}
              </button>
            </div>

            <p
              style={{
                fontSize: 12,
                color: "#9ca3af",
                marginBottom: 10,
              }}
            >
              ðŸ’¡ Use the examples on the left and the custom input below to test
              your solution, then submit to the duel. First accepted solution
              wins.
            </p>

            <MonacoCodeRunner
              value={code ?? ""}
              language={language}
              onChange={(next) => setCode(next ?? "")}
              onLanguageChange={handleLanguageChange}
              onRun={handleRunCode}
              // we still pass onSubmit in case MonacoCodeRunner supports it
              onSubmit={allowSubmit ? handleSubmit : undefined}
              runOutput={runOutput}
              isRunning={isRunningCode}
              isSubmitting={isSubmitting}
              allowSubmit={allowSubmit}
              readOnly={editorReadOnly}
            />

            <div style={{ marginTop: 12 }}>
              <label
                style={{
                  display: "block",
                  fontSize: 12,
                  color: "#9ca3af",
                  marginBottom: 4,
                }}
              >
                Custom Input (stdin)
              </label>
              <textarea
                className="input-control"
                rows={3}
                placeholder="Optional custom input"
                value={stdin}
                onChange={(e) => setStdin(e.target.value)}
                style={{ fontSize: 13 }}
                disabled={editorReadOnly}
              />
            </div>

            {runStatus && (
              <p
                style={{
                  fontSize: 13,
                  marginTop: 10,
                  color: runStatus.startsWith("âœ…") ? "#4ade80" : "#facc15",
                }}
              >
                {runStatus}
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
