// src/LandingPage.js
import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./styles.css";

const LandingPage = () => {
  useEffect(() => {
    const handleSmoothScroll = (e) => {
      if (e.target.getAttribute("href")?.startsWith("#")) {
        e.preventDefault();
        const target = document.querySelector(e.target.getAttribute("href"));
        if (target) {
          target.scrollIntoView({ behavior: "smooth", block: "start" });
        }
      }
    };

    document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
      anchor.addEventListener("click", handleSmoothScroll);
    });

    const observerOptions = {
      threshold: 0.1,
      rootMargin: "0px 0px -100px 0px",
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("visible");
        }
      });
    }, observerOptions);

    document.querySelectorAll(".fade-in").forEach((el) => {
      observer.observe(el);
    });

    const handleScroll = () => {
      const navbar = document.querySelector(".navbar");
      if (!navbar) return;
      navbar.style.background =
        window.scrollY > 100 ? "rgba(10,10,10,0.95)" : "rgba(10,10,10,0.9)";
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
        anchor.removeEventListener("click", handleSmoothScroll);
      });
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <div className="bg-pattern"></div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          <div className="logo">CodeGen4Future</div>
          <ul className="nav-menu">
            <li><a href="#features">Features</a></li>
            <li><a href="#languages">Languages</a></li>
            <li><a href="#how-it-works">How It Works</a></li>
            <li><a href="#community">Community</a></li>
            <li><Link to="/problems" className="nav-btn">Problems</Link></li>
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Code, Compile, Collaborate â€” Anywhere.</h1>
          <p>The future of coding is in your browser.</p>

          <div className="cta-buttons">
            <Link to="/problems" className="btn btn-primary">
              Problems
            </Link>

            <Link to="/simple-editor" className="btn btn-secondary">
              Start Coding
            </Link>

            <a href="#features" className="btn btn-secondary">
              Learn More
            </a>
          </div>
        </div>
      </section>
    </>

  );
};

export default LandingPage;
