import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { token } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!token && !localStorage.getItem("token")) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        setLoading(true);
        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me"),
          api.get("/users/me/stats"),
        ]);

        setProfile(meRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error("Profile load error:", err);
        const msg =
          err?.response?.data?.message ||
          err?.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [token, navigate]);

  if (loading) {
    return (
      <div style={{ minHeight: "100vh", background: "#020617", padding: 32, color: "#fff" }}>
        Loading profile…
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div style={{ minHeight: "100vh", background: "#020617", padding: 32, color: "red" }}>
        {error || "Profile not found."}
      </div>
    );
  }

  // Data mapping
  const memberSince = profile.createdAt
    ? new Date(profile.createdAt).toLocaleDateString()
    : "—";

  const totalSolved = profile.totalSolved || 0;
  const totalSubmissions = profile.totalSubmissions || 0;

  const difficultyStats = stats?.difficultyStats || {
    Easy: profile.solvedEasy || 0,
    Medium: profile.solvedMedium || 0,
    Hard: profile.solvedHard || 0,
  };

  const rating = profile.rating || 1500;

  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "32px 48px",
        background: "#020617",
        color: "#e5e7eb",
      }}
    >
      <div
        style={{
          maxWidth: 1040,
          margin: "0 auto",
          background:
            "radial-gradient(circle at top, rgba(56,189,248,0.15), transparent 60%), #020617",
          borderRadius: 24,
          padding: 24,
          border: "1px solid #111827",
          boxShadow: "0 24px 80px rgba(0,0,0,0.9)",
        }}
      >
        {/* Header */}
        <div style={{ display: "flex", alignItems: "center", gap: 20, marginBottom: 30 }}>
          <div
            style={{
              width: 64,
              height: 64,
              borderRadius: "999px",
              background:
                "linear-gradient(135deg, #22c55e, #0ea5e9, #6366f1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: 700,
              fontSize: 24,
              color: "#020617",
            }}
          >
            {profile.username?.slice(0, 2).toUpperCase()}
          </div>

          <div style={{ flex: 1 }}>
            <h1 style={{ fontSize: 26, fontWeight: 700 }}>{profile.username}</h1>
            <span style={{ fontSize: 13, color: "#9ca3af" }}>@{profile.username}</span>
          </div>

          {/* Rating box */}
          <div
            style={{
              padding: "10px 14px",
              borderRadius: 18,
              border: "1px solid rgba(96,165,250,0.6)",
              background: "rgba(15,23,42,0.8)",
              fontSize: 14,
              minWidth: 120,
              textAlign: "right",
            }}
          >
            <div style={{ fontSize: 11, color: "#9ca3af" }}>Rating</div>
            <div style={{ fontSize: 22, fontWeight: 700, color: "#60a5fa" }}>{rating}</div>
          </div>
        </div>

        {/* Overview Grid */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(4, minmax(0, 1fr))",
            gap: 16,
            marginBottom: 24,
          }}
        >
          {[
            { label: "Member since", value: memberSince },
            { label: "Problems solved", value: totalSolved },
            { label: "Submissions", value: totalSubmissions },
            {
              label: "Duel score",
              value: `${profile.duelWins || 0} W / ${profile.duelLosses || 0} L`,
            },
          ].map((x) => (
            <div
              key={x.label}
              style={{
                padding: 14,
                borderRadius: 16,
                background: "rgba(15,23,42,0.9)",
                border: "1px solid #0f172a",
              }}
            >
              <div style={{ fontSize: 12, color: "#9ca3af", marginBottom: 4 }}>{x.label}</div>
              <div style={{ fontSize: 18, fontWeight: 600 }}>{x.value}</div>
            </div>
          ))}
        </div>

        {/* Difficulty Breakdown */}
        <h3 style={{ fontSize: 14, color: "#9ca3af", marginBottom: 10, textTransform: "uppercase" }}>
          Difficulty breakdown
        </h3>

        <div style={{ display: "flex", gap: 10 }}>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(34,197,94,0.6)",
              background: "rgba(22,163,74,0.12)",
              textAlign: "center",
            }}
          >
            Easy — {difficultyStats.Easy || 0}
          </div>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(249,115,22,0.6)",
              background: "rgba(234,88,12,0.12)",
              textAlign: "center",
            }}
          >
            Medium — {difficultyStats.Medium || 0}
          </div>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(239,68,68,0.6)",
              background: "rgba(248,113,113,0.10)",
              textAlign: "center",
            }}
          >
            Hard — {difficultyStats.Hard || 0}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
