// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";
import "../styles.css"; // make sure this is imported somewhere globally

const Profile = () => {
  const { token } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const hasToken = token || localStorage.getItem("token");
    if (!hasToken) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        setLoading(true);
        setError("");

        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me"),
          api.get("/users/me/stats"),
        ]);

        setProfile(meRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error("Profile load error:", err);
        const msg =
          err?.response?.data?.message ||
          err?.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [token, navigate]);

  if (loading) {
    return (
      <div className="profile-page">
        <div className="profile-card profile-card--loading">
          <p className="profile-text-muted">Loading profile…</p>
        </div>
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div className="profile-page">
        <div className="profile-card profile-card--error">
          <h1 className="profile-title">Profile</h1>
          <p className="profile-error">{error || "Profile not found."}</p>
        </div>
      </div>
    );
  }

  // ---------- Data mapping ----------
  const memberSince = profile.createdAt
    ? new Date(profile.createdAt).toLocaleDateString()
    : "—";

  const totalSolved = profile.totalSolved || 0;
  const totalSubmissions = profile.totalSubmissions || 0;
  const duelWins = profile.duelWins || 0;
  const duelLosses = profile.duelLosses || 0;
  const rating = profile.rating || 1500;

  const difficultyStats = stats?.difficultyStats || {
    Easy: profile.solvedEasy || 0,
    Medium: profile.solvedMedium || 0,
    Hard: profile.solvedHard || 0,
  };

  const acceptanceRate =
    totalSubmissions > 0
      ? Math.round((totalSolved / totalSubmissions) * 100)
      : 0;

  // ---------- UI ----------
  return (
    <div className="profile-page">
      <div className="profile-card">
        {/* Header */}
        <header className="profile-header">
          <div className="profile-avatar">
            <span>
              {profile.username
                ? profile.username.slice(0, 2).toUpperCase()
                : "U"}
            </span>
          </div>

          <div className="profile-header-main">
            <div className="profile-header-text">
              <h1 className="profile-title">{profile.username}</h1>
              <span className="profile-handle">@{profile.username}</span>
            </div>
            <p className="profile-text-muted">
              Member since <span>{memberSince}</span>
            </p>
          </div>

          <div className="profile-rating-card">
            <span className="profile-rating-label">Rating</span>
            <span className="profile-rating-value">{rating}</span>
          </div>
        </header>

        {/* Overview */}
        <section className="profile-section">
          <h2 className="profile-section-title">Overview</h2>
          <div className="profile-overview-grid">
            <div className="profile-overview-item">
              <span className="profile-overview-label">Problems solved</span>
              <span className="profile-overview-value">{totalSolved}</span>
            </div>

            <div className="profile-overview-item">
              <span className="profile-overview-label">Submissions</span>
              <span className="profile-overview-value">
                {totalSubmissions}
              </span>
              <span className="profile-overview-sub">
                Acceptance rate: {acceptanceRate}%
              </span>
            </div>

            <div className="profile-overview-item">
              <span className="profile-overview-label">Duel record</span>
              <span className="profile-overview-value">
                {duelWins}W / {duelLosses}L
              </span>
            </div>

            <div className="profile-overview-item">
              <span className="profile-overview-label">Account</span>
              <span className="profile-overview-value-small">
                Verified email • Active
              </span>
            </div>
          </div>
        </section>

        {/* Difficulty breakdown */}
        <section className="profile-section">
          <div className="profile-section-header">
            <h2 className="profile-section-title">Difficulty breakdown</h2>
          </div>
          <div className="profile-difficulty-row">
            <div className="profile-chip profile-chip--easy">
              <span>Easy</span>
              <span>{difficultyStats.Easy || 0}</span>
            </div>
            <div className="profile-chip profile-chip--medium">
              <span>Medium</span>
              <span>{difficultyStats.Medium || 0}</span>
            </div>
            <div className="profile-chip profile-chip--hard">
              <span>Hard</span>
              <span>{difficultyStats.Hard || 0}</span>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Profile;
