// src/pages/ProblemPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Editor from "@monaco-editor/react";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function ProblemPage() {
  const { slug, contestId } = useParams();
  const { token } = useAuth();

  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("// Write your solution here...");
  const [language, setLanguage] = useState("javascript");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchProblem() {
      try {
        const res = await axios.get(`${API_URL}/problems/${slug}`);
        setProblem(res.data);
      } catch (err) {
        console.error("Error loading problem", err);
      }
    }
    fetchProblem();
  }, [slug]);

  async function runCode() {
    setLoading(true);
    setResult("Running...");
    try {
      const res = await axios.post(`${API_URL}/submissions/run`, {
        code,
        language,
      });
      setResult(res.data.output);
    } catch (err) {
      setResult("Error executing code");
    } finally {
      setLoading(false);
    }
  }

  async function submitCode() {
    setLoading(true);
    setResult("Evaluating...");
    try {
      const res = await axios.post(
        `${API_URL}/submissions`,
        { code, language, problemId: problem._id, contestId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setResult(`Result: ${res.data.status}`);
    } catch (err) {
      setResult("Submit error: " + err.response?.data?.message);
    } finally {
      setLoading(false);
    }
  }

  if (!problem) return <p style={{ color: "#ccc" }}>Loading problem...</p>;

  return (
    <div className="problem-layout">
      {/* LEFT AREA: Problem details */}
      <div className="problem-left">
        <h1 className="problem-title">{problem.title}</h1>
        <p className="problem-difficulty pill">
          {problem.difficulty}
        </p>

        <div className="problem-description">
          <h2>Description</h2>
          <p>{problem.description}</p>

          <h2>Example</h2>
          <pre className="problem-example">
            {problem.exampleTests?.[0]?.input && (
              <>
                <strong>Input:</strong> {problem.exampleTests[0].input}{"\n"}
              </>
            )}
            {problem.exampleTests?.[0]?.output && (
              <>
                <strong>Output:</strong> {problem.exampleTests[0].output}
              </>
            )}
          </pre>
        </div>
      </div>

      {/* RIGHT AREA: Editor + buttons + output */}
      <div className="problem-right">
        <div className="problem-controls">
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="select-control"
          >
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="cpp">C++</option>
          </select>

          <button className="btn-run" onClick={runCode} disabled={loading}>
            Run Code
          </button>
          <button className="btn-submit" onClick={submitCode} disabled={loading}>
            Submit
          </button>
        </div>

        <Editor
          height="55vh"
          theme="vs-dark"
          language={language}
          value={code}
          onChange={(value) => setCode(value)}
          className="problem-editor"
        />

        <div className="problem-output">
          <strong>Output:</strong>
          <pre>{result}</pre>
        </div>
      </div>
    </div>
  );
}

export default ProblemPage;
