import React, { useEffect, useState } from "react";
import axios from "../utils/api";

export default function LeaderboardPage() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get("/stats/leaderboard")
      .then(res => setUsers(res.data))
      .catch(() => console.log("Failed to load leaderboard"));
  }, []);

  return (
    <div className="glass-card">
      <h2 className="page-title">üèÜ Leaderboard</h2>

      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Rating</th>
            <th>Wins</th>
            <th>Losses</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u, i) => (
            <tr key={u._id}>
              <td>{i + 1}</td>
              <td>{u.username}</td>
              <td>{u.rating}</td>
              <td>{u.duelWins || 0}</td>
              <td>{u.duelLosses || 0}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
