// client/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

function Profile() {
  const { user: authUser } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    async function load() {
      try {
        setLoading(true);
        setError("");

        // Try /users/me first, fall back to /users/profile if needed
        let res;
        try {
          res = await api.get("/users/me");
        } catch (e) {
          // if 404 or similar, try legacy route
          if (e.response && e.response.status === 404) {
            res = await api.get("/users/profile");
          } else {
            throw e;
          }
        }

        const data = res.data;
        // some APIs return { user: {...} }, some return the user directly
        const u = data.user || data;
        setProfile(u);
      } catch (err) {
        console.error("Profile load failed:", err);
        const msg =
          err.response?.data?.message ||
          err.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        setLoading(false);
      }
    }

    if (authUser) {
      load();
    } else {
      setLoading(false);
      setError("You must be logged in to view your profile.");
    }
  }, [authUser]);

  if (!authUser) {
    return (
      <div className="app-page" style={{ paddingTop: 40 }}>
        <div
          style={{
            maxWidth: 640,
            margin: "0 auto",
            background: "rgba(15,23,42,0.9)",
            borderRadius: 18,
            padding: 24,
            border: "1px solid rgba(148,163,184,0.15)",
            color: "#e5e7eb",
            boxShadow: "0 18px 45px rgba(0,0,0,0.6)",
          }}
        >
          <h2 style={{ fontSize: 20, marginBottom: 8 }}>Login required</h2>
          <p style={{ fontSize: 14, color: "#9ca3af" }}>
            Please log in to view your profile and statistics.
          </p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="app-page" style={{ paddingTop: 40 }}>
        <div
          style={{
            maxWidth: 900,
            margin: "0 auto",
            textAlign: "center",
            color: "#e5e7eb",
          }}
        >
          <div
            style={{
              display: "inline-block",
              width: 32,
              height: 32,
              borderRadius: "50%",
              border: "3px solid #6366f1",
              borderTopColor: "transparent",
              animation: "spin 0.8s linear infinite",
            }}
          />
          <p style={{ marginTop: 12, color: "#9ca3af" }}>Loading profile…</p>
        </div>
      </div>
    );
  }

  if (error && !profile) {
    return (
      <div className="app-page" style={{ paddingTop: 40 }}>
        <div
          style={{
            maxWidth: 640,
            margin: "0 auto",
            background: "rgba(15,23,42,0.9)",
            borderRadius: 18,
            padding: 24,
            border: "1px solid rgba(248,113,113,0.4)",
            color: "#fecaca",
            boxShadow: "0 18px 45px rgba(0,0,0,0.6)",
          }}
        >
          <h2 style={{ fontSize: 20, marginBottom: 6 }}>Failed to load</h2>
          <p style={{ fontSize: 14 }}>{error}</p>
        </div>
      </div>
    );
  }

  const p = profile || {};
  const initials =
    (p.name || p.username || authUser?.username || "User")
      .split(" ")
      .map((x) => x[0])
      .join("")
      .slice(0, 2)
      .toUpperCase();

  const memberSince = p.createdAt
    ? new Date(p.createdAt).toLocaleDateString()
    : "—";

  const stats = p.stats || {};
  const solved = stats.solvedCount ?? stats.problemsSolved ?? 0;
  const contestsPlayed = stats.contestsPlayed ?? 0;
  const duelsPlayed = stats.duelsPlayed ?? stats.duels ?? 0;
  const duelWins = stats.duelWins ?? 0;
  const duelLosses = stats.duelLosses ?? 0;

  const rating = p.rating ?? 1500;

  return (
    <div className="app-page" style={{ paddingBottom: 40 }}>
      <div
        style={{
          maxWidth: 1050,
          margin: "24px auto 0",
          display: "flex",
          flexDirection: "column",
          gap: 16,
        }}
      >
        {/* Top hero card */}
        <div
          style={{
            position: "relative",
            overflow: "hidden",
            borderRadius: 22,
            padding: 24,
            background:
              "radial-gradient(circle at 0 0, rgba(56,189,248,0.14), transparent 55%), radial-gradient(circle at 100% 0, rgba(168,85,247,0.16), transparent 55%), #020617",
            border: "1px solid rgba(148,163,184,0.25)",
            boxShadow: "0 24px 80px rgba(15,23,42,0.9)",
          }}
        >
          <div
            style={{
              position: "absolute",
              inset: 0,
              opacity: 0.18,
              background:
                "radial-gradient(circle at 20% 0, #22c55e 0, transparent 55%)",
              pointerEvents: "none",
            }}
          />
          <div
            style={{
              position: "relative",
              display: "flex",
              alignItems: "center",
              gap: 20,
            }}
          >
            {/* Avatar */}
            <div
              style={{
                width: 72,
                height: 72,
                borderRadius: "999px",
                background:
                  "conic-gradient(from 160deg, #22c55e, #6366f1, #a855f7, #22c55e)",
                padding: 2,
                flexShrink: 0,
              }}
            >
              <div
                style={{
                  width: "100%",
                  height: "100%",
                  borderRadius: "999px",
                  background:
                    "radial-gradient(circle at 30% 0, #0f172a, #020617 65%)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  fontWeight: 700,
                  fontSize: 26,
                  color: "#e5e7eb",
                }}
              >
                {initials}
              </div>
            </div>

            {/* Name + username + rating */}
            <div style={{ flex: 1, minWidth: 0 }}>
              <div
                style={{
                  display: "flex",
                  alignItems: "baseline",
                  gap: 8,
                  flexWrap: "wrap",
                }}
              >
                <h1
                  style={{
                    fontSize: 26,
                    fontWeight: 700,
                    color: "#f9fafb",
                  }}
                >
                  {p.name || p.username || authUser?.username}
                </h1>
                <span
                  style={{
                    fontSize: 14,
                    color: "#9ca3af",
                  }}
                >
                  @{p.username || authUser?.username}
                </span>
              </div>

              <div
                style={{
                  display: "flex",
                  flexWrap: "wrap",
                  gap: 10,
                  marginTop: 10,
                  alignItems: "center",
                  fontSize: 13,
                }}
              >
                <span
                  style={{
                    padding: "4px 10px",
                    borderRadius: 999,
                    background: "rgba(15,23,42,0.9)",
                    border: "1px solid rgba(129,140,248,0.7)",
                    color: "#c7d2fe",
                    fontWeight: 500,
                  }}
                >
                  Rating: <strong>{rating}</strong>
                </span>
                <span
                  style={{
                    padding: "4px 10px",
                    borderRadius: 999,
                    background: "rgba(15,23,42,0.9)",
                    border: "1px solid rgba(45,212,191,0.6)",
                    color: "#a5f3fc",
                    display: "inline-flex",
                    alignItems: "center",
                    gap: 6,
                  }}
                >
                  <span
                    style={{
                      width: 7,
                      height: 7,
                      borderRadius: "999px",
                      background:
                        solved > 0 ? "#22c55e" : "rgba(148,163,184,0.7)",
                    }}
                  />
                  {solved} problems solved
                </span>
                <span
                  style={{
                    padding: "4px 10px",
                    borderRadius: 999,
                    background: "rgba(15,23,42,0.9)",
                    border: "1px solid rgba(248,250,252,0.06)",
                    color: "#e5e7eb",
                  }}
                >
                  Member since: {memberSince}
                </span>
              </div>

              <p
                style={{
                  marginTop: 12,
                  fontSize: 13,
                  color: "#cbd5f5",
                  maxWidth: 520,
                }}
              >
                {p.bio || "No bio yet. Start solving problems to build your profile!"}
              </p>
            </div>
          </div>
        </div>

        {/* Bottom grid: overview + stats + contact */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(0, 2fr) minmax(0, 1.2fr)",
            gap: 16,
          }}
        >
          {/* Left column: overview & performance */}
          <div style={{ display: "flex", flexDirection: "column", gap: 16 }}>
            {/* Overview card */}
            <div
              style={{
                background: "#020617",
                borderRadius: 18,
                padding: 18,
                border: "1px solid rgba(31,41,55,0.9)",
                boxShadow: "0 18px 45px rgba(15,23,42,0.85)",
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  marginBottom: 10,
                }}
              >
                <h2
                  style={{
                    fontSize: 15,
                    fontWeight: 600,
                    color: "#e5e7eb",
                  }}
                >
                  Overview
                </h2>
                <span
                  style={{
                    fontSize: 11,
                    textTransform: "uppercase",
                    letterSpacing: 1,
                    color: "#6b7280",
                  }}
                >
                  Snapshot
                </span>
              </div>

              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(3,minmax(0,1fr))",
                  gap: 12,
                  marginTop: 6,
                }}
              >
                {[
                  {
                    label: "Problems solved",
                    value: solved,
                    accent: "#22c55e",
                  },
                  {
                    label: "Contests played",
                    value: contestsPlayed,
                    accent: "#38bdf8",
                  },
                  {
                    label: "1v1 duels",
                    value: duelsPlayed,
                    accent: "#a855f7",
                  },
                ].map((item) => (
                  <div
                    key={item.label}
                    style={{
                      background: "rgba(15,23,42,0.96)",
                      borderRadius: 12,
                      padding: 10,
                      border: `1px solid ${item.accent}33`,
                    }}
                  >
                    <div
                      style={{
                        fontSize: 11,
                        textTransform: "uppercase",
                        color: "#9ca3af",
                        marginBottom: 4,
                      }}
                    >
                      {item.label}
                    </div>
                    <div
                      style={{
                        fontSize: 20,
                        fontWeight: 700,
                        color: "#f9fafb",
                      }}
                    >
                      {item.value}
                    </div>
                    <div
                      style={{
                        marginTop: 6,
                        height: 4,
                        borderRadius: 999,
                        background: "#0f172a",
                        overflow: "hidden",
                      }}
                    >
                      <div
                        style={{
                          width: `${
                            item.value === 0
                              ? 15
                              : Math.min(100, 10 + item.value * 6)
                          }%`,
                          height: "100%",
                          background: item.accent,
                        }}
                      />
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Performance card */}
            <div
              style={{
                background: "#020617",
                borderRadius: 18,
                padding: 18,
                border: "1px solid rgba(31,41,55,0.9)",
                boxShadow: "0 18px 45px rgba(15,23,42,0.85)",
              }}
            >
              <h2
                style={{
                  fontSize: 15,
                  fontWeight: 600,
                  color: "#e5e7eb",
                  marginBottom: 8,
                }}
              >
                Duel performance
              </h2>

              <div
                style={{
                  display: "flex",
                  gap: 16,
                  flexWrap: "wrap",
                  marginTop: 6,
                }}
              >
                <div style={{ minWidth: 120 }}>
                  <div
                    style={{
                      fontSize: 12,
                      color: "#9ca3af",
                      marginBottom: 4,
                    }}
                  >
                    Wins
                  </div>
                  <div
                    style={{
                      fontSize: 18,
                      fontWeight: 600,
                      color: "#22c55e",
                    }}
                  >
                    {duelWins}
                  </div>
                </div>

                <div style={{ minWidth: 120 }}>
                  <div
                    style={{
                      fontSize: 12,
                      color: "#9ca3af",
                      marginBottom: 4,
                    }}
                  >
                    Losses
                  </div>
                  <div
                    style={{
                      fontSize: 18,
                      fontWeight: 600,
                      color: "#f97373",
                    }}
                  >
                    {duelLosses}
                  </div>
                </div>

                <div style={{ minWidth: 140 }}>
                  <div
                    style={{
                      fontSize: 12,
                      color: "#9ca3af",
                      marginBottom: 4,
                    }}
                  >
                    Win rate
                  </div>
                  <div
                    style={{
                      fontSize: 18,
                      fontWeight: 600,
                      color: "#38bdf8",
                    }}
                  >
                    {duelWins + duelLosses === 0
                      ? "—"
                      : `${Math.round(
                          (duelWins / (duelWins + duelLosses)) * 100
                        )}%`}
                  </div>
                </div>
              </div>

              <p
                style={{
                  marginTop: 10,
                  fontSize: 12,
                  color: "#6b7280",
                }}
              >
                Duel stats will update automatically as you play more 1v1
                matches.
              </p>
            </div>
          </div>

          {/* Right column: contact + meta */}
          <div style={{ display: "flex", flexDirection: "column", gap: 16 }}>
            {/* Contact card */}
            <div
              style={{
                background: "#020617",
                borderRadius: 18,
                padding: 18,
                border: "1px solid rgba(31,41,55,0.9)",
                boxShadow: "0 18px 45px rgba(15,23,42,0.85)",
              }}
            >
              <h2
                style={{
                  fontSize: 15,
                  fontWeight: 600,
                  color: "#e5e7eb",
                  marginBottom: 10,
                }}
              >
                Contact
              </h2>
              <div style={{ fontSize: 13, color: "#9ca3af", marginBottom: 4 }}>
                Email
              </div>
              <div
                style={{
                  fontSize: 14,
                  color: "#bfdbfe",
                  wordBreak: "break-all",
                }}
              >
                {p.email || authUser?.email || "—"}
              </div>
            </div>

            {/* Small meta card */}
            <div
              style={{
                background: "#020617",
                borderRadius: 18,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
                boxShadow: "0 18px 45px rgba(15,23,42,0.85)",
                fontSize: 13,
                color: "#9ca3af",
              }}
            >
              <div style={{ marginBottom: 6, color: "#e5e7eb" }}>
                Profile status
              </div>
              <p>
                This profile updates automatically from your{" "}
                <strong>submissions</strong>, <strong>contests</strong> and{" "}
                <strong>duels</strong>. Keep solving problems to grow your
                stats.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* tiny inline keyframes so the loader spinner works even if you don't add CSS */}
      <style>
        {`@keyframes spin { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }`}
      </style>
    </div>
  );
}

export default Profile;
