// src/pages/DuelRoomPage.js
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useLocation } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

// Simple helper to format seconds as mm:ss or hh:mm:ss
function formatSeconds(sec) {
  if (sec <= 0) return "00:00";
  const h = Math.floor(sec / 3600);
  const m = Math.floor((sec % 3600) / 60);
  const s = sec % 60;

  if (h > 0) {
    return `${h.toString().padStart(2, "0")}:${m
      .toString()
      .padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  }
  return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
}

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const { user, token } = useAuth();

  // opponent info optionally passed from DuelPage
  const opponentName = location.state?.opponentName || "Opponent";
  const opponentId = location.state?.opponentId || null;

  // Timer
  const [initialDuration] = useState(600); // 10 minutes
  const [timeLeft, setTimeLeft] = useState(initialDuration);
  const [isRunning, setIsRunning] = useState(false);
  const [isOver, setIsOver] = useState(false);

  // simple local score counters (you can later tie this to actual AC count)
  const [myScore, setMyScore] = useState(0);
  const [opponentScore, setOpponentScore] = useState(0);

  // duel result
  const [winner, setWinner] = useState(null); // "A" | "B" | "draw"
  const [resultMsg, setResultMsg] = useState("");
  const [savingResult, setSavingResult] = useState(false);

  // Start duel
  const handleStart = () => {
    if (isRunning || isOver) return;
    setTimeLeft(initialDuration);
    setIsRunning(true);
    setIsOver(false);
    setWinner(null);
    setResultMsg("");
  };

  // local “I solved one” buttons (for demo; you can replace with real AC events)
  const handleMySolve = () => {
    setMyScore((prev) => prev + 1);
  };

  const handleOpponentSolve = () => {
    setOpponentScore((prev) => prev + 1);
  };

  // Decide winner when time ends or manually
  const decideWinner = async (reason = "time") => {
    if (isOver) return; // avoid double call
    setIsOver(true);
    setIsRunning(false);

    let duelWinner;
    if (myScore > opponentScore) duelWinner = "A";
    else if (opponentScore > myScore) duelWinner = "B";
    else duelWinner = "draw";

    setWinner(duelWinner);

    // No user or no token? just show local message
    if (!user || !token) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${
          duelWinner === "A"
            ? "You"
            : duelWinner === "B"
            ? opponentName
            : "Draw"
        } (not saved, not logged in)`
      );
      return;
    }

    // If we don't know opponentId, skip rating update (still show winner)
    if (!opponentId) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${
          duelWinner === "A"
            ? "You"
            : duelWinner === "B"
            ? opponentName
            : "Draw"
        }. (opponentId missing – ratings not updated)`
      );
      return;
    }

    // Call backend /duels/finish to update ratings & history
    try {
      setSavingResult(true);
      const body = {
        playerAId: user._id || user.id, // adjust if your user object uses different field
        playerBId: opponentId,
        winner: duelWinner,
        problemId: location.state?.problemId || null,
        durationSeconds: initialDuration - timeLeft,
        roomId,
      };

      const res = await axios.post(`${API_URL}/duels/finish`, body, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const info = res.data?.ratings;
      if (info) {
        const me =
          duelWinner === "A"
            ? info.playerA
            : duelWinner === "B"
            ? info.playerB
            : info.playerA; // in draw, no matter

        setResultMsg(
          `Duel finished (${reason}). Winner: ${
            duelWinner === "A"
              ? "You"
              : duelWinner === "B"
              ? opponentName
              : "Draw"
          }. Your rating: ${me.old} → ${me.new}`
        );
      } else {
        setResultMsg(
          `Duel finished (${reason}). Winner: ${
            duelWinner === "A"
              ? "You"
              : duelWinner === "B"
              ? opponentName
              : "Draw"
          }.`
        );
      }
    } catch (err) {
      console.error("duel finish error:", err.response?.data || err.message);
      setResultMsg(
        `Duel finished (${reason}). Winner: ${
          duelWinner === "A"
            ? "You"
            : duelWinner === "B"
            ? opponentName
            : "Draw"
        }. (Error saving result)`
      );
    } finally {
      setSavingResult(false);
    }
  };

  // Timer effect
  useEffect(() => {
    if (!isRunning || isOver) return;

    if (timeLeft <= 0) {
      // time up
      decideWinner("time");
      return;
    }

    const id = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isRunning, timeLeft, isOver]);

  const statusText = useMemo(() => {
    if (isOver) return "Duel finished";
    if (isRunning) return "Duel in progress";
    return "Waiting to start";
  }, [isRunning, isOver]);

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">1v1 Duel Room</h1>
        <p className="page-subtitle">
          Room ID: <strong>{roomId}</strong> — share this with your opponent.
        </p>

        <div className="duel-top">
          <div className="duel-player">
            <h3>You</h3>
            <p className="duel-score">{myScore}</p>
            <button
              className="btn btn-secondary"
              onClick={handleMySolve}
              disabled={!isRunning || isOver}
            >
              I solved one
            </button>
          </div>

          <div className="duel-timer">
            <div className="timer-label">{statusText}</div>
            <div className="timer-value">{formatSeconds(timeLeft)}</div>
            <button
              className="btn btn-primary"
              onClick={handleStart}
              disabled={isRunning}
            >
              {isRunning ? "Running..." : "Start Duel"}
            </button>
          </div>

          <div className="duel-player">
            <h3>{opponentName}</h3>
            <p className="duel-score">{opponentScore}</p>
            <button
              className="btn btn-secondary"
              onClick={handleOpponentSolve}
              disabled={!isRunning || isOver}
            >
              Opponent solved
            </button>
          </div>
        </div>

        <div style={{ marginTop: 16, display: "flex", gap: 8 }}>
          <button
            className="btn btn-ghost-sm"
            onClick={() => decideWinner("manual")}
            disabled={isOver || !isRunning}
          >
            End now & decide winner
          </button>
        </div>

        {resultMsg && (
          <div
            style={{
              marginTop: 16,
              padding: 12,
              borderRadius: 8,
              background: "rgba(0,0,0,0.4)",
              border: "1px solid rgba(255,255,255,0.1)",
              fontSize: "0.9rem",
            }}
          >
            {savingResult ? "Saving result... " : null}
            {resultMsg}
          </div>
        )}

        {!user && (
          <p className="page-muted" style={{ marginTop: 12 }}>
            You are not logged in, so duel results will not update ratings.
          </p>
        )}

        {!opponentId && (
          <p className="page-muted" style={{ marginTop: 4 }}>
            Opponent ID was not provided in navigation state. To enable rating
            updates, navigate here with{" "}
            <code>opponentId</code> and <code>opponentName</code> in{" "}
            <code>location.state</code>.
          </p>
        )}
      </div>
    </div>
  );
}

export default DuelRoomPage;
