// src/pages/DuelRoomPage.js
import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import createDuelSocket from "../utils/duelSocket";
import MonacoCodeRunner from "../components/MonacoCodeRunner";

const DEFAULT_LANGUAGE = "python";

export default function DuelRoomPage() {
  const { roomId } = useParams();
  const [socket, setSocket] = useState(null);
  const [problem, setProblem] = useState(null);
  const [status, setStatus] = useState("Waiting to start");
  const [language, setLanguage] = useState(DEFAULT_LANGUAGE);
  const [code, setCode] = useState("");
  const [runOutput, setRunOutput] = useState("");
  const [isRunning, setIsRunning] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // ---------------------------- SOCKET SETUP ----------------------------
  useEffect(() => {
    const s = createDuelSocket(roomId);
    setSocket(s);

    s.on("problemSelected", (prob) => setProblem(prob));
    s.on("duelStarted", () => setStatus("Duel in progress"));
    s.on("runResult", (data) => {
      setIsRunning(false);
      setRunOutput(data.output || data.error);
    });

    s.on("submitResult", (res) => {
      setIsSubmitting(false);
      setRunOutput(res.message || "");
    });

    return () => s.disconnect();
  }, [roomId]);

  // ---------------------------- ACTIONS ----------------------------
  const handleRun = useCallback(() => {
    if (!socket) return;
    setIsRunning(true);
    setRunOutput("");

    socket.emit("runCode", { code, language, roomId });
  }, [socket, code, language, roomId]);

  const handleSubmit = useCallback(() => {
    if (!socket) return;
    setIsSubmitting(true);
    setRunOutput("");

    socket.emit("submitCode", { code, language, roomId });
  }, [socket, code, language, roomId]);

  return (
    <div className="duel-room container">
      <h1>1v1 Duel Room</h1>
      <div className="duel-status">{status}</div>

      <div className="duel-content">
        {/* -------- LEFT: Problem Statement -------- */}
        <div className="problem-panel">
          {problem ? (
            <>
              <h2>{problem.title}</h2>
              <p>{problem.description}</p>

              <h3>Examples</h3>
              {problem.exampleTests.map((ex, idx) => (
                <div key={idx} className="example-box">
                  <div><strong>Input:</strong><pre>{ex.input}</pre></div>
                  <div><strong>Output:</strong><pre>{ex.output}</pre></div>
                </div>
              ))}
            </>
          ) : (
            <h3>Waiting for problemâ€¦</h3>
          )}
        </div>

        {/* -------- RIGHT: Monaco Code Runner -------- */}
        <div className="editor-panel">
          <MonacoCodeRunner
            value={code}
            onChange={setCode}
            language={language}
            onLanguageChange={setLanguage}
            onRun={handleRun}
            onSubmit={handleSubmit}
            runOutput={runOutput}
            allowSubmit={true}
            isRunning={isRunning}
            isSubmitting={isSubmitting}
          />
        </div>
      </div>
    </div>
  );
}
