// client/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

function ProfilePage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    let cancelled = false;

    async function load() {
      try {
        setLoading(true);
        setError("");

        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me"),
          api.get("/users/me/stats"),
        ]);

        if (cancelled) return;

        setProfile(meRes.data || null);
        setStats(statsRes.data || null);
      } catch (err) {
        if (cancelled) return;
        console.error("Profile load error:", err);
        const msg =
          err.response?.data?.message ||
          err.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    load();

    return () => {
      cancelled = true;
    };
  }, [token, navigate]);

  if (!token) return null;

  if (loading) {
    return (
      <div
        style={{
          minHeight: "100vh",
          padding: "32px 48px",
          background: "#020617",
          color: "#e5e7eb",
        }}
      >
        <div
          style={{
            maxWidth: 960,
            margin: "0 auto",
            background: "#020617",
            borderRadius: 18,
            padding: 24,
            border: "1px solid #111827",
            boxShadow: "0 20px 60px rgba(0,0,0,0.85)",
          }}
        >
          <p style={{ color: "#9ca3af", fontSize: 14 }}>Loading profile…</p>
        </div>
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div
        style={{
          minHeight: "100vh",
          padding: "32px 48px",
          background: "#020617",
          color: "#e5e7eb",
        }}
      >
        <div
          style={{
            maxWidth: 960,
            margin: "0 auto",
            background: "#020617",
            borderRadius: 18,
            padding: 24,
            border: "1px solid #111827",
            boxShadow: "0 20px 60px rgba(0,0,0,0.85)",
          }}
        >
          <h1 style={{ fontSize: 24, fontWeight: 700, marginBottom: 8 }}>
            Profile
          </h1>
          <p style={{ color: "#f97373", fontSize: 14 }}>
            {error || "Profile not found."}
          </p>
        </div>
      </div>
    );
  }

  const memberSince = profile.createdAt
    ? new Date(profile.createdAt).toLocaleDateString()
    : "—";

  const totalSolved = profile.totalSolved || 0;
  const totalSubmissions = profile.totalSubmissions || 0;
  const rating = profile.rating || 1500;

  const difficultyStats = stats?.difficultyStats || {
    Easy: profile.solvedEasy || 0,
    Medium: profile.solvedMedium || 0,
    Hard: profile.solvedHard || 0,
  };

  const recent = stats?.recent || [];

  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "32px 48px",
        background: "#020617",
        color: "#e5e7eb",
      }}
    >
      <div
        style={{
          maxWidth: 1040,
          margin: "0 auto",
          background:
            "radial-gradient(circle at top, rgba(56,189,248,0.18), transparent 55%), #020617",
          borderRadius: 24,
          padding: 24,
          border: "1px solid #111827",
          boxShadow: "0 24px 80px rgba(0,0,0,0.9)",
        }}
      >
        {/* top header */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            gap: 20,
            marginBottom: 24,
          }}
        >
          <div
            style={{
              width: 64,
              height: 64,
              borderRadius: "999px",
              background:
                "linear-gradient(135deg, #22c55e, #0ea5e9, #6366f1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: 700,
              fontSize: 24,
              color: "#020617",
              boxShadow: "0 12px 30px rgba(0,0,0,0.55)",
            }}
          >
            {profile.username?.slice(0, 2).toUpperCase()}
          </div>
          <div style={{ flex: 1 }}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: 10,
                marginBottom: 4,
              }}
            >
              <h1
                style={{
                  fontSize: 26,
                  fontWeight: 700,
                  letterSpacing: 0.2,
                }}
              >
                {profile.username}
              </h1>
              <span
                style={{
                  fontSize: 12,
                  padding: "2px 10px",
                  borderRadius: 999,
                  border: "1px solid rgba(148,163,184,0.6)",
                  color: "#9ca3af",
                }}
              >
                @{profile.username}
              </span>
            </div>
            <p style={{ fontSize: 13, color: "#9ca3af" }}>No bio yet.</p>
          </div>

          <div
            style={{
              padding: "10px 14px",
              borderRadius: 18,
              background: "rgba(15,23,42,0.9)",
              border: "1px solid rgba(96,165,250,0.6)",
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-end",
              gap: 2,
              minWidth: 130,
            }}
          >
            <span
              style={{
                fontSize: 11,
                color: "#9ca3af",
                textTransform: "uppercase",
                letterSpacing: 0.08,
              }}
            >
              Rating
            </span>
            <span
              style={{
                fontSize: 22,
                fontWeight: 700,
                color: "#60a5fa",
              }}
            >
              {rating}
            </span>
          </div>
        </div>

        {/* grid */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(0, 3fr) minmax(0, 2.4fr)",
            gap: 18,
          }}
        >
          {/* left: overview */}
          <section
            style={{
              background: "rgba(15,23,42,0.96)",
              borderRadius: 18,
              padding: 18,
              border: "1px solid #111827",
            }}
          >
            <h2
              style={{
                fontSize: 14,
                fontWeight: 600,
                letterSpacing: 0.08,
                color: "#9ca3af",
                textTransform: "uppercase",
                marginBottom: 12,
              }}
            >
              Overview
            </h2>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(4, minmax(0, 1fr))",
                gap: 10,
                marginBottom: 16,
              }}
            >
              {[
                { label: "Member since", value: memberSince },
                { label: "Problems solved", value: totalSolved },
                { label: "Submissions", value: totalSubmissions },
                {
                  label: "Duel score",
                  value: `${profile.duelWins || 0} W / ${
                    profile.duelLosses || 0
                  } L`,
                },
              ].map((item) => (
                <div
                  key={item.label}
                  style={{
                    padding: 10,
                    borderRadius: 14,
                    background:
                      "radial-gradient(circle at top left, rgba(15,118,110,0.4), transparent 65%)",
                    border: "1px solid #0f172a",
                  }}
                >
                  <div
                    style={{
                      fontSize: 11,
                      color: "#9ca3af",
                      marginBottom: 4,
                    }}
                  >
                    {item.label}
                  </div>
                  <div
                    style={{
                      fontSize: 16,
                      fontWeight: 600,
                    }}
                  >
                    {item.value}
                  </div>
                </div>
              ))}
            </div>

            <div
              style={{
                display: "flex",
                gap: 10,
                marginTop: 4,
              }}
            >
              <div
                style={{
                  flex: 1,
                  borderRadius: 999,
                  padding: "6px 10px",
                  border: "1px solid rgba(34,197,94,0.5)",
                  background: "rgba(22,163,74,0.12)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  fontSize: 13,
                }}
              >
                <span>Easy</span>
                <span>{difficultyStats.Easy || 0}</span>
              </div>
              <div
                style={{
                  flex: 1,
                  borderRadius: 999,
                  padding: "6px 10px",
                  border: "1px solid rgba(249,115,22,0.5)",
                  background: "rgba(234,88,12,0.12)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  fontSize: 13,
                }}
              >
                <span>Medium</span>
                <span>{difficultyStats.Medium || 0}</span>
              </div>
              <div
                style={{
                  flex: 1,
                  borderRadius: 999,
                  padding: "6px 10px",
                  border: "1px solid rgba(239,68,68,0.6)",
                  background: "rgba(248,113,113,0.10)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  fontSize: 13,
                }}
              >
                <span>Hard</span>
                <span>{difficultyStats.Hard || 0}</span>
              </div>
            </div>
          </section>

          {/* right: recent activity */}
          <section
            style={{
              background: "rgba(15,23,42,0.96)",
              borderRadius: 18,
              padding: 18,
              border: "1px solid #111827",
            }}
          >
            <h2
              style={{
                fontSize: 14,
                fontWeight: 600,
                letterSpacing: 0.08,
                color: "#9ca3af",
                textTransform: "uppercase",
                marginBottom: 12,
              }}
            >
              Recent activity
            </h2>

            {recent.length === 0 ? (
              <p style={{ fontSize: 13, color: "#9ca3af" }}>
                No submissions yet. Solve a problem to see activity here.
              </p>
            ) : (
              <ul
                style={{
                  listStyle: "none",
                  padding: 0,
                  margin: 0,
                  display: "flex",
                  flexDirection: "column",
                  gap: 8,
                  maxHeight: 260,
                  overflowY: "auto",
                }}
              >
                {recent.map((sub) => (
                  <li
                    key={sub._id}
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "space-between",
                      padding: "8px 10px",
                      borderRadius: 12,
                      background: "#020617",
                      border: "1px solid #020617",
                    }}
                  >
                    <div>
                      <div
                        style={{
                          fontSize: 13,
                          fontWeight: 500,
                          marginBottom: 2,
                        }}
                      >
                        {sub.problem?.title || "Unknown problem"}
                      </div>
                      <div
                        style={{
                          fontSize: 11,
                          color: "#9ca3af",
                        }}
                      >
                        {sub.problem?.difficulty || "—"} •{" "}
                        {new Date(sub.createdAt).toLocaleString()}
                      </div>
                    </div>
                    <span
                      style={{
                        fontSize: 11,
                        padding: "4px 10px",
                        borderRadius: 999,
                        border:
                          sub.status === "Accepted"
                            ? "1px solid rgba(34,197,94,0.7)"
                            : "1px solid rgba(248,113,113,0.8)",
                        background:
                          sub.status === "Accepted"
                            ? "rgba(22,163,74,0.15)"
                            : "rgba(248,113,113,0.12)",
                        color:
                          sub.status === "Accepted"
                            ? "#4ade80"
                            : "#fca5a5",
                      }}
                    >
                      {sub.status}
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </section>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
