import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

function ProblemPage() {
  const { slug } = useParams();
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("");

  useEffect(() => {
    fetch(`http://localhost:5000/problems/${slug}`)
      .then((res) => res.json())
      .then((data) => setProblem(data))
      .catch((err) => console.error(err));
  }, [slug]);

  if (!problem) return <p>Loading problem...</p>;

  return (
    <div>
      <h2>{problem.title}</h2>
      <p><b>Difficulty:</b> {problem.difficulty}</p>

      <h3>Description</h3>
      <p>{problem.description}</p>

      <h3>Examples</h3>
      {problem.exampleTests.map((ex, i) => (
        <pre key={i} style={{ background: "#eee", padding: 10 }}>
          <b>Input:</b> {ex.input}{"\n"}
          <b>Output:</b> {ex.output}
        </pre>
      ))}

      <h3>Your Code</h3>
      <textarea
        value={code}
        onChange={(e) => setCode(e.target.value)}
        style={{ width: "100%", height: "200px" }}
      />

      <button
        style={{ marginTop: 10, padding: "10px 20px", fontSize: 16 }}
        onClick={() => alert("Submit logic coming soon ðŸš€")}
      >
        Submit
      </button>
    </div>
  );
}

export default ProblemPage;
