import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function Home() {
  const [problems, setProblems] = useState([]);
  const [search, setSearch] = useState("");
  const [filter, setFilter] = useState("");

  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get("http://localhost:5000/problems");
        setProblems(res.data);
      } catch (err) {
        console.error(err);
      }
    }
    fetchProblems();
  }, []);

  const difficultyColor = {
    Easy: "green",
    Medium: "orange",
    Hard: "red",
  };

  const filtered = problems.filter((p) => {
    const matchesSearch = p.title.toLowerCase().includes(search.toLowerCase());
    const matchesFilter = !filter || p.difficulty === filter;
    return matchesSearch && matchesFilter;
  });

  return (
    <div>
      <h1 style={{ marginBottom: 16 }}>Problems</h1>

      <div style={{ marginBottom: 16, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Search problems..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: 6, flex: 1 }}
        />

        <select
          style={{ padding: 6, minWidth: 120 }}
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="">All difficulties</option>
          <option value="Easy">Easy</option>
          <option value="Medium">Medium</option>
          <option value="Hard">Hard</option>
        </select>
      </div>

      <div>
        {filtered.length === 0 && <p>No problems found.</p>}

        {filtered.map((p) => (
          <div
            key={p.slug}
            style={{
              border: "1px solid #ddd",
              padding: 15,
              borderRadius: 8,
              marginBottom: 10,
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              background: "#fafafa",
            }}
          >
            <div>
              <Link
                to={`/problems/${p.slug}`}
                style={{ textDecoration: "none", color: "#333" }}
              >
                <b>{p.title}</b>
              </Link>
            </div>

            <span
              style={{
                padding: "4px 10px",
                borderRadius: 999,
                background: "#eee",
                color: difficultyColor[p.difficulty] || "#333",
                fontWeight: "bold",
                fontSize: 13,
              }}
            >
              {p.difficulty}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
