// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Profile.css";

const API = process.env.REACT_APP_API_URL || "http://localhost:5000";

function Avatar({ name, src, size = 112 }) {
  const initials = name
    ? name
        .split(" ")
        .map((n) => n[0])
        .slice(0, 2)
        .join("")
        .toUpperCase()
    : "?";
  return src ? (
    <img
      src={src}
      alt={name}
      className="profile-avatar-img"
      style={{ width: size, height: size }}
    />
  ) : (
    <div className="profile-avatar-fallback" style={{ width: size, height: size }}>
      {initials}
    </div>
  );
}

function StatCard({ label, value }) {
  return (
    <div className="stat-card">
      <div className="stat-label">{label}</div>
      <div className="stat-value">{value}</div>
    </div>
  );
}

function RecentActivity({ items = [] }) {
  if (!items.length) {
    return <div className="muted">No recent submissions</div>;
  }
  return (
    <ul className="recent-list">
      {items.map((it) => (
        <li key={it._id || it.id} className="recent-item">
          <div className="recent-left">
            <div className="recent-title">{it.problemName || it.title || "Problem"}</div>
            <div className="recent-sub">{it.language || it.lang || "—"}</div>
          </div>
          <div className={`recent-badge ${badgeClass(it.status)}`}>{it.status || "Unknown"}</div>
        </li>
      ))}
    </ul>
  );
}

function badgeClass(status) {
  if (!status) return "badge-unknown";
  const s = status.toLowerCase();
  if (s.includes("accept")) return "badge-ok";
  if (s.includes("wrong") || s.includes("fail")) return "badge-bad";
  return "badge-warn";
}

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const fetchProfile = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`${API}/users/profile`, {
          headers: token ? { Authorization: `Bearer ${token}` } : undefined,
        });
        setUser(res.data);
      } catch (err) {
        console.error(err?.response?.data || err.message || err);
        setError("Failed to load profile");
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  if (loading) {
    return (
      <div className="page-wrap">
        <div className="muted">Loading profile...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="page-wrap">
        <div className="muted error">{error}</div>
      </div>
    );
  }

  const {
    name,
    username,
    email,
    bio,
    avatarUrl,
    joinedAt,
    stats = {},
    recentSubmissions,
  } = user || {};

  return (
    <div className="page-wrap">
      <div className="profile-card">
        <div className="profile-header">
          <div className="profile-left">
            <Avatar name={name || username} src={avatarUrl} />
          </div>
          <div className="profile-main">
            <div className="profile-title">{name || username || "Unknown User"}</div>
            <div className="profile-username">{username ? `@${username}` : ""}</div>
            <div className="profile-bio">{bio || "No bio yet. Add a short description about yourself."}</div>
            <div className="profile-meta">
              <div>Member since: <strong>{joinedAt ? new Date(joinedAt).toLocaleDateString() : "—"}</strong></div>
            </div>
          </div>
          <div className="profile-actions">
            <button className="btn btn-primary">Edit Profile</button>
          </div>
        </div>

        <div className="profile-body">
          <div className="left-col">
            <div className="stats-row">
              <StatCard label="Problems Solved" value={stats.solved ?? 0} />
              <StatCard label="Rating" value={stats.rating ?? "—"} />
              <StatCard label="Contests" value={stats.contests ?? 0} />
              <StatCard label="Rank" value={stats.rank ?? "—"} />
            </div>

            <div className="panel">
              <div className="panel-title">Overview</div>
              <div className="panel-body">
                A quick summary of your activity and performance. View recent submissions and contest history below.
              </div>
            </div>

            <div className="panel">
              <div className="panel-title">Recent Activity</div>
              <div className="panel-body">
                <RecentActivity items={recentSubmissions || user.recentSubmissions || []} />
              </div>
            </div>
          </div>

          <aside className="right-col">
            <div className="panel">
              <div className="panel-title">Contact</div>
              <div className="panel-body">{email || "No email"}</div>
            </div>

            <div className="panel">
              <div className="panel-title">Quick Actions</div>
              <div className="panel-body actions">
                <button className="btn btn-ghost">View Submissions</button>
                <button className="btn btn-ghost">View Contests</button>
              </div>
            </div>
          </aside>
        </div>
      </div>
    </div>
  );
}
