// src/pages/Profile.js
import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

const API_URL = "http://localhost:5000";

function Profile() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [subs, setSubs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: "/profile", message: "You must be logged in" },
      });
      return;
    }

    async function fetchSubs() {
      try {
        setLoading(true);
        const res = await axios.get(`${API_URL}/submissions/mine`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setSubs(res.data || []);
      } catch (err) {
        console.error("Profile submissions error:", err);
      } finally {
        setLoading(false);
      }
    }

    fetchSubs();
  }, [user, navigate]);

  const stats = useMemo(() => {
    const total = subs.length;
    const accepted = subs.filter((s) => s.status === "Accepted");
    const uniqueSolved = new Set(
      accepted
        .filter((s) => s.problem && s.problem._id)
        .map((s) => s.problem._id)
    ).size;

    const byLang = {};
    subs.forEach((s) => {
      const lang = (s.language || "unknown").toLowerCase();
      byLang[lang] = (byLang[lang] || 0) + 1;
    });

    return {
      totalSubmissions: total,
      solvedCount: uniqueSolved,
      acceptedCount: accepted.length,
      byLanguage: byLang,
    };
  }, [subs]);

  if (!user) return null;

  const joinedDate = user.createdAt
    ? new Date(user.createdAt).toLocaleDateString()
    : "N/A";

  return (
    <div className="page-container">
      <div className="page-card">
        {/* Header */}
        <div className="profile-header">
          <div className="profile-avatar">
            {user.username?.charAt(0)?.toUpperCase() || "U"}
          </div>
          <div>
            <h1 className="page-heading">{user.username}</h1>
            <p className="page-subtitle">
              Welcome back, <span className="auth-brand">{user.username}</span>.
              Keep grinding those problems!
            </p>
            <p className="page-muted">
              Joined: <strong>{joinedDate}</strong>
            </p>
            {user.email && (
              <p className="page-muted">
                Email: <strong>{user.email}</strong>
              </p>
            )}
          </div>
        </div>

        {/* Stats cards */}
        <div className="profile-stats-grid">
          <div className="stat-card">
            <p className="stat-label">Problems Solved</p>
            <p className="stat-value">{stats.solvedCount}</p>
            <p className="stat-hint">
              Unique problems with at least one Accepted submission.
            </p>
          </div>

          <div className="stat-card">
            <p className="stat-label">Total Submissions</p>
            <p className="stat-value">{stats.totalSubmissions}</p>
            <p className="stat-hint">
              Every attempt you&apos;ve made across all problems.
            </p>
          </div>

          <div className="stat-card">
            <p className="stat-label">Accepted Submissions</p>
            <p className="stat-value">{stats.acceptedCount}</p>
            <p className="stat-hint">
              Number of times you got that sweet &quot;Accepted&quot;.
            </p>
          </div>
        </div>

        {/* Language usage */}
        <div className="section-block">
          <h2 className="section-title">Language Breakdown</h2>
          {loading ? (
            <p className="page-muted">Analyzing your submissions...</p>
          ) : Object.keys(stats.byLanguage).length === 0 ? (
            <p className="page-muted">
              No submissions yet. Try solving a problem in your favorite
              language!
            </p>
          ) : (
            <div className="lang-chips-row">
              {Object.entries(stats.byLanguage).map(([lang, count]) => (
                <div key={lang} className="lang-chip">
                  <span className="lang-chip-label">
                    {lang.toUpperCase()}
                  </span>
                  <span className="lang-chip-count">{count} submissions</span>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Recent activity */}
        <div className="section-block">
          <h2 className="section-title">Recent Activity</h2>
          {loading ? (
            <p className="page-muted">Loading recent submissions...</p>
          ) : subs.length === 0 ? (
            <p className="page-muted">
              You haven&apos;t submitted any solutions yet.
            </p>
          ) : (
            <ul className="recent-list">
              {subs.slice(0, 5).map((s) => (
                <li key={s._id} className="recent-item">
                  <div>
                    <p className="recent-problem">
                      {s.problem?.title || "Unknown Problem"}
                    </p>
                    <p className="recent-meta">
                      {new Date(s.createdAt).toLocaleString()} â€¢{" "}
                      <span className="pill pill-lang">
                        {s.language?.toUpperCase()}
                      </span>
                    </p>
                  </div>
                  <div>
                    <span
                      className={
                        s.status === "Accepted"
                          ? "pill pill-accepted"
                          : s.status === "Rejected"
                          ? "pill pill-rejected"
                          : "pill pill-pending"
                      }
                    >
                      {s.status}
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
