import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function Profile() {
  const { user, token } = useAuth();
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    async function fetchProfile() {
      try {
        const res = await axios.get("http://localhost:5000/profile/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setStats(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load profile");
      }
    }

    fetchProfile();
  }, [user, token, navigate]);

  if (!user) return null;

  return (
    <div>
      <h2>Profile</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!stats ? (
        <p>Loading...</p>
      ) : (
        <>
          <div
            style={{
              border: "1px solid #ddd",
              padding: 16,
              marginBottom: 20,
              maxWidth: 400,
            }}
          >
            <p>
              <b>Username:</b> {stats.user.username}
            </p>
            <p>
              <b>Email:</b> {stats.user.email}
            </p>
            <p>
              <b>Joined:</b>{" "}
              {new Date(stats.user.createdAt).toLocaleDateString()}
            </p>
          </div>

          <h3>Stats</h3>
          <ul>
            <li>Total submissions: {stats.stats.totalSubmissions}</li>
            <li>Accepted submissions: {stats.stats.acceptedSubmissions}</li>
            <li>Problems solved: {stats.stats.totalSolvedProblems}</li>
          </ul>
        </>
      )}
    </div>
  );
}

export default Profile;
