// src/pages/TeamsPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function TeamsPage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [myTeams, setMyTeams] = useState([]);
  const [allTeams, setAllTeams] = useState([]);

  const [loadingMyTeams, setLoadingMyTeams] = useState(true);
  const [loadingAllTeams, setLoadingAllTeams] = useState(true);

  const [teamName, setTeamName] = useState("");
  const [memberIds, setMemberIds] = useState("");
  const [message, setMessage] = useState("");
  const [creating, setCreating] = useState(false);
  const [joiningTeamId, setJoiningTeamId] = useState(null);

  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Load myTeams and allTeams
  useEffect(() => {
    if (!token) return;

    async function fetchTeams() {
      try {
        setLoadingMyTeams(true);
        setLoadingAllTeams(true);

        const [mineRes, allRes] = await Promise.all([
          axios.get("http://localhost:5000/teams/mine", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/teams", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setMyTeams(mineRes.data || []);
        setAllTeams(allRes.data || []);
      } catch (err) {
        console.error("Fetch teams error:", err);
        setMessage("Failed to load teams.");
      } finally {
        setLoadingMyTeams(false);
        setLoadingAllTeams(false);
      }
    }

    fetchTeams();
  }, [token]);

  // ✅ New logic: membership = team _id exists inside myTeams
  function isMemberOfTeam(team) {
    return myTeams.some((t) => t._id === team._id);
  }

  async function handleCreateTeam(e) {
    e.preventDefault();
    setMessage("");

    if (!teamName.trim()) {
      setMessage("Team name is required.");
      return;
    }

    try {
      setCreating(true);

      const ids =
        memberIds.trim().length > 0
          ? memberIds
              .split(",")
              .map((s) => s.trim())
              .filter(Boolean)
          : [];

      const res = await axios.post(
        "http://localhost:5000/teams",
        {
          name: teamName.trim(),
          memberIds: ids,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const newTeam = res.data;

      setMessage("Team created successfully!");
      setTeamName("");
      setMemberIds("");

      // After creating, user is captain & member → add to myTeams
      setMyTeams((prev) => [...prev, newTeam]);
      setAllTeams((prev) => [...prev, newTeam]);
    } catch (err) {
      console.error("Create team error:", err);
      setMessage(
        err.response?.data?.message || "Failed to create team. Try again."
      );
    } finally {
      setCreating(false);
    }
  }

  async function handleJoinTeam(teamId) {
    if (!token) {
      setMessage("You must be logged in to join a team.");
      return;
    }

    try {
      setJoiningTeamId(teamId);
      setMessage("");

      await axios.post(
        `http://localhost:5000/teams/${teamId}/join`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Joined team successfully.");

      // Find that team from allTeams and add to myTeams
      const joinedTeam = allTeams.find((t) => t._id === teamId);
      if (joinedTeam && !isMemberOfTeam(joinedTeam)) {
        setMyTeams((prev) => [...prev, joinedTeam]);
      }
    } catch (err) {
      console.error("Join team error:", err);
      setMessage(
        err.response?.data?.message || "Failed to join team. Try again."
      );
    } finally {
      setJoiningTeamId(null);
    }
  }

  if (!user) return null;

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <h2>Teams</h2>
      <p style={{ color: "#555" }}>
        Create teams and join existing ones. Teams can then battle in{" "}
        <b>Team Battles</b>.
      </p>

      {message && (
        <p
          style={{
            color: message.toLowerCase().includes("fail") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}

      {/* Create team */}
      <div
        style={{
          border: "1px solid #ddd",
          padding: 15,
          borderRadius: 8,
          marginBottom: 25,
          background: "#fafafa",
        }}
      >
        <h3>Create New Team</h3>
        <form onSubmit={handleCreateTeam}>
          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Team Name*</b>
              <br />
              <input
                type="text"
                value={teamName}
                onChange={(e) => setTeamName(e.target.value)}
                style={{ width: "100%", padding: 6 }}
              />
            </label>
          </div>

          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Member IDs (comma-separated, optional)</b>
              <br />
              <textarea
                value={memberIds}
                onChange={(e) => setMemberIds(e.target.value)}
                placeholder="Paste MongoDB user IDs, separated by commas"
                style={{ width: "100%", padding: 6, minHeight: 60 }}
              />
            </label>
            <small style={{ color: "#777" }}>
              You will be added automatically as captain and member.
            </small>
          </div>

          <button type="submit" disabled={creating}>
            {creating ? "Creating..." : "Create Team"}
          </button>
        </form>
      </div>

      {/* My teams */}
      <h3>My Teams</h3>
      {loadingMyTeams ? (
        <p>Loading your teams...</p>
      ) : myTeams.length === 0 ? (
        <p>You are not part of any team yet.</p>
      ) : (
        myTeams.map((team) => (
          <div
            key={team._id}
            style={{
              border: "1px solid #ddd",
              padding: 12,
              borderRadius: 8,
              marginBottom: 10,
            }}
          >
            <h4>{team.name}</h4>
            <p style={{ fontSize: 14, color: "#555" }}>
              Members:{" "}
              {team.members?.map((m) => m.username).join(", ") || "Unknown"}
            </p>
          </div>
        ))
      )}

      {/* All teams */}
      <h3 style={{ marginTop: 25 }}>All Teams</h3>
      {loadingAllTeams ? (
        <p>Loading all teams...</p>
      ) : allTeams.length === 0 ? (
        <p>No teams created yet.</p>
      ) : (
        allTeams.map((team) => {
          const isMember = isMemberOfTeam(team);
          return (
            <div
              key={team._id}
              style={{
                border: "1px solid #ddd",
                padding: 12,
                borderRadius: 8,
                marginBottom: 10,
              }}
            >
              <h4>{team.name}</h4>
              <p style={{ fontSize: 14, color: "#555" }}>
                Members:{" "}
                {team.members?.map((m) => m.username).join(", ") || "Unknown"}
              </p>
              {isMember ? (
                <button disabled>Joined</button>
              ) : (
                <button
                  onClick={() => handleJoinTeam(team._id)}
                  disabled={joiningTeamId === team._id}
                >
                  {joiningTeamId === team._id ? "Joining..." : "Join"}
                </button>
              )}
            </div>
          );
        })
      )}
    </div>
  );
}

export default TeamsPage;
