// src/pages/TeamBattlePage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function TeamBattlePage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [teams, setTeams] = useState([]);
  const [problems, setProblems] = useState([]);
  const [loadingTeams, setLoadingTeams] = useState(true);
  const [loadingProblems, setLoadingProblems] = useState(true);

  const [teamAId, setTeamAId] = useState("");
  const [teamBId, setTeamBId] = useState("");
  const [selectedProblemIds, setSelectedProblemIds] = useState([]);
  const [durationMinutes, setDurationMinutes] = useState(90);
  const [message, setMessage] = useState("");
  const [creating, setCreating] = useState(false);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }
  }, [user, navigate]);

  // Load all teams
  useEffect(() => {
    if (!token) return;

    async function fetchTeams() {
      try {
        const res = await axios.get("http://localhost:5000/teams", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeams(res.data);
      } catch (err) {
        console.error("Error fetching teams:", err);
        setMessage("Failed to load teams.");
      } finally {
        setLoadingTeams(false);
      }
    }
    fetchTeams();
  }, [token]);

  // Load problems
  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get("http://localhost:5000/problems");
        setProblems(res.data);
      } catch (err) {
        console.error("Error fetching problems:", err);
        setMessage("Failed to load problems.");
      } finally {
        setLoadingProblems(false);
      }
    }
    fetchProblems();
  }, []);

  function toggleProblemSelection(problemId) {
    setSelectedProblemIds((prev) =>
      prev.includes(problemId)
        ? prev.filter((id) => id !== problemId)
        : [...prev, problemId]
    );
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setMessage("");

    if (!token) {
      setMessage("You must be logged in to start a team battle.");
      return;
    }
    if (!teamAId || !teamBId || teamAId === teamBId) {
      setMessage("Select two different teams.");
      return;
    }
    if (selectedProblemIds.length === 0) {
      setMessage("Select at least one problem.");
      return;
    }
    if (!durationMinutes || durationMinutes <= 0) {
      setMessage("Duration must be positive minutes.");
      return;
    }

    try {
      setCreating(true);
      const res = await axios.post(
        "http://localhost:5000/matches/team",
        {
          teamAId,
          teamBId,
          problemIds: selectedProblemIds,
          durationMinutes: Number(durationMinutes),
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Team battle created! Redirecting to contest...");
      const contestId = res.data.contest._id;
      navigate(`/contests/${contestId}`);
    } catch (err) {
      console.error("Create team match error:", err);
      setMessage(
        err.response?.data?.message ||
          "Failed to create team battle. Try again."
      );
    } finally {
      setCreating(false);
    }
  }

  if (!user) return null;

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <h2>Team vs Team Battle</h2>
      <p style={{ color: "#555" }}>
        Pick two teams, choose problems, and start a team coding battle.
      </p>

      {message && (
        <p
          style={{
            color: message.toLowerCase().includes("fail") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}

      <form onSubmit={handleSubmit}>
        {/* Team selection */}
        <div
          style={{
            display: "flex",
            gap: 20,
            marginBottom: 20,
            flexWrap: "wrap",
          }}
        >
          <div style={{ flex: 1, minWidth: 200 }}>
            <label>
              <b>Team A*</b>
              <br />
              {loadingTeams ? (
                <p>Loading teams...</p>
              ) : (
                <select
                  value={teamAId}
                  onChange={(e) => setTeamAId(e.target.value)}
                  style={{ width: "100%", padding: 6 }}
                >
                  <option value="">Select Team A</option>
                  {teams.map((t) => (
                    <option key={t._id} value={t._id}>
                      {t.name}
                    </option>
                  ))}
                </select>
              )}
            </label>
          </div>

          <div style={{ flex: 1, minWidth: 200 }}>
            <label>
              <b>Team B*</b>
              <br />
              {loadingTeams ? (
                <p>Loading teams...</p>
              ) : (
                <select
                  value={teamBId}
                  onChange={(e) => setTeamBId(e.target.value)}
                  style={{ width: "100%", padding: 6 }}
                >
                  <option value="">Select Team B</option>
                  {teams.map((t) => (
                    <option key={t._id} value={t._id}>
                      {t.name}
                    </option>
                  ))}
                </select>
              )}
            </label>
          </div>
        </div>

        {/* Duration */}
        <div style={{ marginBottom: 15 }}>
          <label>
            <b>Duration (minutes)*</b>
            <br />
            <input
              type="number"
              value={durationMinutes}
              onChange={(e) => setDurationMinutes(e.target.value)}
              style={{ width: 120, padding: 6 }}
              min={1}
            />
          </label>
        </div>

        {/* Problems */}
        <div style={{ marginBottom: 20 }}>
          <b>Select Problems*</b>
          <br />
          {loadingProblems ? (
            <p>Loading problems...</p>
          ) : problems.length === 0 ? (
            <p>No problems found. Add some first.</p>
          ) : (
            <div
              style={{
                maxHeight: 260,
                overflowY: "auto",
                border: "1px solid #ddd",
                padding: 10,
                borderRadius: 8,
                background: "#fafafa",
                marginTop: 6,
              }}
            >
              {problems.map((p) => (
                <label
                  key={p._id}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    marginBottom: 6,
                    fontSize: 14,
                  }}
                >
                  <span>
                    <input
                      type="checkbox"
                      checked={selectedProblemIds.includes(p._id)}
                      onChange={() => toggleProblemSelection(p._id)}
                      style={{ marginRight: 8 }}
                    />
                    {p.title}{" "}
                    <span style={{ color: "#999" }}>({p.difficulty})</span>
                  </span>
                </label>
              ))}
            </div>
          )}
        </div>

        <button type="submit" disabled={creating}>
          {creating ? "Creating battle..." : "Start Team Battle"}
        </button>
      </form>
    </div>
  );
}

export default TeamBattlePage;
