import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function Home() {
  const [problems, setProblems] = useState([]);
  const [search, setSearch] = useState("");
  const [filter, setFilter] = useState("");

  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get("http://localhost:5000/problems");
        setProblems(res.data);
      } catch (err) {
        console.error(err);
      }
    }
    fetchProblems();
  }, []);

  // Moved OUTSIDE the return block
  const difficultyColor = {
    Easy: "green",
    Medium: "orange",
    Hard: "red",
  };

  const filtered = problems.filter((p) => {
    const matchesSearch = p.title.toLowerCase().includes(search.toLowerCase());
    const matchesFilter = !filter || p.difficulty === filter;
    return matchesSearch && matchesFilter;
  });

  return (
    <div>
      <h1>Problems</h1>

      <input
        type="text"
        placeholder="Search..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginBottom: 10, padding: 6, width: "50%" }}
      />

      <select
        style={{ marginLeft: 10, padding: 6 }}
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
      >
        <option value="">All</option>
        <option value="Easy">Easy</option>
        <option value="Medium">Medium</option>
        <option value="Hard">Hard</option>
      </select>

      <div>
        {filtered.map((p) => (
          <div
            key={p.slug}
            style={{
              border: "1px solid #ddd",
              padding: 15,
              borderRadius: 8,
              marginBottom: 10,
              display: "flex",
              justifyContent: "space-between",
            }}
          >
            <Link to={`/problems/${p.slug}`}>
              <b>{p.title}</b>
            </Link>

            <span
              style={{
                color: difficultyColor[p.difficulty],
                fontWeight: "bold",
              }}
            >
              {p.difficulty}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
