// client/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { token } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  const [isEditing, setIsEditing] = useState(false);
  const [form, setForm] = useState({
    name: "",
    bio: "",
    location: "",
  });

  useEffect(() => {
    const hasToken = token || localStorage.getItem("token");
    if (!hasToken) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        setLoading(true);
        setError("");

        const [meRes, statsRes] = await Promise.all([
          api.get("/users/me"),
          api.get("/users/me/stats"),
        ]);

        setProfile(meRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error("Profile load error:", err);
        const msg =
          err?.response?.data?.message ||
          err?.response?.data?.error ||
          "Failed to load profile.";
        setError(msg);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [token, navigate]);

  const openEdit = () => {
    if (!profile) return;
    setForm({
      name: profile.name || "",
      bio: profile.bio || "",
      location: profile.location || "",
    });
    setIsEditing(true);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      setError("");

      const res = await api.put("/users/me", form);
      setProfile(res.data);
      setIsEditing(false);
    } catch (err) {
      console.error("Save profile error:", err);
      const msg =
        err?.response?.data?.message ||
        err?.response?.data?.error ||
        "Failed to update profile.";
      setError(msg);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div
        style={{
          minHeight: "100vh",
          padding: "32px 48px",
          background: "#020617",
          color: "#e5e7eb",
        }}
      >
        <div
          style={{
            maxWidth: 960,
            margin: "0 auto",
            background: "#020617",
            borderRadius: 18,
            padding: 24,
            border: "1px solid #111827",
            boxShadow: "0 20px 60px rgba(0,0,0,0.85)",
          }}
        >
          <p style={{ color: "#9ca3af", fontSize: 14 }}>Loading profile‚Ä¶</p>
        </div>
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div
        style={{
          minHeight: "100vh",
          padding: "32px 48px",
          background: "#020617",
          color: "#e5e7eb",
        }}
      >
        <div
          style={{
            maxWidth: 960,
            margin: "0 auto",
            background: "#020617",
            borderRadius: 18,
            padding: 24,
            border: "1px solid #111827",
            boxShadow: "0 20px 60px rgba(0,0,0,0.85)",
          }}
        >
          <h1 style={{ fontSize: 24, fontWeight: 700, marginBottom: 8 }}>
            Profile
          </h1>
          <p style={{ color: "#f97373", fontSize: 14 }}>
            {error || "Profile not found."}
          </p>
        </div>
      </div>
    );
  }

  // ---------- Data mapping ----------
  const memberSince = profile.createdAt
    ? new Date(profile.createdAt).toLocaleDateString()
    : "‚Äî";

  const totalSolved = profile.totalSolved || 0;
  const totalSubmissions = profile.totalSubmissions || 0;
  const duelWins = profile.duelWins || 0;
  const duelLosses = profile.duelLosses || 0;
  const rating = profile.rating || 1500;

  const difficultyStats = stats?.difficultyStats || {
    Easy: profile.solvedEasy || 0,
    Medium: profile.solvedMedium || 0,
    Hard: profile.solvedHard || 0,
  };

  const acceptanceRate =
    totalSubmissions > 0
      ? Math.round((totalSolved / totalSubmissions) * 100)
      : 0;

  const currentStreak = stats?.streakCurrent ?? profile.streakCurrent ?? 0;
  const longestStreak = stats?.streakLongest ?? profile.streakLongest ?? 0;

  const displayName = profile.name || profile.username;

  // ---------- UI ----------
  return (
    <div
      style={{
        minHeight: "100vh",
        padding: "32px 48px",
        background: "#020617",
        color: "#e5e7eb",
      }}
    >
      <div
        style={{
          maxWidth: 1040,
          margin: "0 auto",
          background:
            "radial-gradient(circle at top, rgba(56,189,248,0.15), transparent 60%), #020617",
          borderRadius: 24,
          padding: 24,
          border: "1px solid #111827",
          boxShadow: "0 24px 80px rgba(0,0,0,0.9)",
        }}
      >
        {/* Header */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            gap: 20,
            marginBottom: 20,
          }}
        >
          <div
            style={{
              width: 64,
              height: 64,
              borderRadius: "999px",
              background:
                "linear-gradient(135deg, #22c55e, #0ea5e9, #6366f1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontWeight: 700,
              fontSize: 24,
              color: "#020617",
              boxShadow: "0 12px 30px rgba(0,0,0,0.7)",
            }}
          >
            {profile.username?.slice(0, 2).toUpperCase()}
          </div>

          <div style={{ flex: 1 }}>
            <div
              style={{
                display: "flex",
                alignItems: "baseline",
                gap: 8,
                marginBottom: 4,
              }}
            >
              <h1
                style={{
                  fontSize: 24,
                  fontWeight: 700,
                  letterSpacing: 0.2,
                }}
              >
                {displayName}
              </h1>
              <span
                style={{
                  fontSize: 12,
                  padding: "2px 10px",
                  borderRadius: 999,
                  border: "1px solid rgba(148,163,184,0.6)",
                  color: "#9ca3af",
                }}
              >
                @{profile.username}
              </span>
            </div>
            <p style={{ fontSize: 13, color: "#9ca3af", marginBottom: 4 }}>
              Member since{" "}
              <span style={{ color: "#e5e7eb" }}>{memberSince}</span>
            </p>
            <p
              style={{
                fontSize: 13,
                color: profile.bio ? "#e5e7eb" : "#6b7280",
              }}
            >
              {profile.bio || "Add a short bio to tell others about you."}
            </p>
            {profile.location && (
              <p style={{ fontSize: 12, color: "#9ca3af", marginTop: 2 }}>
                üìç {profile.location}
              </p>
            )}
          </div>

          <div
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-end",
              gap: 8,
            }}
          >
            <div
              style={{
                padding: "10px 14px",
                borderRadius: 18,
                border: "1px solid rgba(96,165,250,0.6)",
                background: "rgba(15,23,42,0.9)",
                minWidth: 130,
              }}
            >
              <div
                style={{
                  fontSize: 11,
                  color: "#9ca3af",
                  textTransform: "uppercase",
                  letterSpacing: 0.08,
                }}
              >
                Rating
              </div>
              <div
                style={{
                  fontSize: 22,
                  fontWeight: 700,
                  color: "#60a5fa",
                }}
              >
                {rating}
              </div>
            </div>

            <button
              type="button"
              onClick={openEdit}
              style={{
                padding: "6px 12px",
                fontSize: 13,
                borderRadius: 999,
                border: "1px solid #4b5563",
                background: "#020617",
                color: "#e5e7eb",
                cursor: "pointer",
              }}
            >
              Edit profile
            </button>
          </div>
        </div>

        {/* Edit form */}
        {isEditing && (
          <form
            onSubmit={handleSaveProfile}
            style={{
              marginBottom: 20,
              padding: 16,
              borderRadius: 16,
              background: "rgba(15,23,42,0.95)",
              border: "1px solid #111827",
            }}
          >
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(2, minmax(0, 1fr))",
                gap: 12,
                marginBottom: 12,
              }}
            >
              <div>
                <label
                  style={{
                    fontSize: 12,
                    color: "#9ca3af",
                    display: "block",
                    marginBottom: 4,
                  }}
                >
                  Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={form.name}
                  onChange={handleFormChange}
                  style={{
                    width: "100%",
                    padding: "6px 10px",
                    borderRadius: 8,
                    border: "1px solid #374151",
                    background: "#020617",
                    color: "#e5e7eb",
                    fontSize: 13,
                  }}
                />
              </div>
              <div>
                <label
                  style={{
                    fontSize: 12,
                    color: "#9ca3af",
                    display: "block",
                    marginBottom: 4,
                  }}
                >
                  Location
                </label>
                <input
                  type="text"
                  name="location"
                  value={form.location}
                  onChange={handleFormChange}
                  style={{
                    width: "100%",
                    padding: "6px 10px",
                    borderRadius: 8,
                    border: "1px solid #374151",
                    background: "#020617",
                    color: "#e5e7eb",
                    fontSize: 13,
                  }}
                />
              </div>
            </div>

            <div style={{ marginBottom: 12 }}>
              <label
                style={{
                  fontSize: 12,
                  color: "#9ca3af",
                  display: "block",
                  marginBottom: 4,
                }}
              >
                Bio
              </label>
              <textarea
                name="bio"
                value={form.bio}
                onChange={handleFormChange}
                rows={3}
                style={{
                  width: "100%",
                  padding: "6px 10px",
                  borderRadius: 8,
                  border: "1px solid #374151",
                  background: "#020617",
                  color: "#e5e7eb",
                  fontSize: 13,
                  resize: "vertical",
                }}
              />
            </div>

            <div style={{ display: "flex", gap: 8, justifyContent: "flex-end" }}>
              <button
                type="button"
                onClick={() => setIsEditing(false)}
                style={{
                  padding: "6px 12px",
                  fontSize: 13,
                  borderRadius: 999,
                  border: "1px solid #4b5563",
                  background: "#020617",
                  color: "#e5e7eb",
                  cursor: "pointer",
                }}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={saving}
                style={{
                  padding: "6px 14px",
                  fontSize: 13,
                  borderRadius: 999,
                  border: "1px solid #22c55e",
                  background: saving ? "#064e3b" : "#16a34a",
                  color: "#ecfdf5",
                  cursor: saving ? "default" : "pointer",
                }}
              >
                {saving ? "Saving‚Ä¶" : "Save changes"}
              </button>
            </div>
          </form>
        )}

        {/* Overview grid */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(4, minmax(0, 1fr))",
            gap: 16,
            marginBottom: 24,
          }}
        >
          <div
            style={{
              padding: 14,
              borderRadius: 16,
              background: "rgba(15,23,42,0.96)",
              border: "1px solid #0f172a",
            }}
          >
            <div style={{ fontSize: 12, color: "#9ca3af", marginBottom: 4 }}>
              Problems solved
            </div>
            <div style={{ fontSize: 18, fontWeight: 600 }}>{totalSolved}</div>
          </div>

          <div
            style={{
              padding: 14,
              borderRadius: 16,
              background: "rgba(15,23,42,0.96)",
              border: "1px solid #0f172a",
            }}
          >
            <div style={{ fontSize: 12, color: "#9ca3af", marginBottom: 4 }}>
              Submissions
            </div>
            <div style={{ fontSize: 18, fontWeight: 600 }}>
              {totalSubmissions}
            </div>
            <div
              style={{
                marginTop: 4,
                fontSize: 11,
                color: "#9ca3af",
              }}
            >
              Acceptance rate: {acceptanceRate}%
            </div>
          </div>

          <div
            style={{
              padding: 14,
              borderRadius: 16,
              background: "rgba(15,23,42,0.96)",
              border: "1px solid #0f172a",
            }}
          >
            <div style={{ fontSize: 12, color: "#9ca3af", marginBottom: 4 }}>
              Duel record
            </div>
            <div style={{ fontSize: 18, fontWeight: 600 }}>
              {duelWins}W / {duelLosses}L
            </div>
          </div>

          <div
            style={{
              padding: 14,
              borderRadius: 16,
              background: "rgba(15,23,42,0.96)",
              border: "1px solid #0f172a",
            }}
          >
            <div style={{ fontSize: 12, color: "#9ca3af", marginBottom: 4 }}>
              Streak
            </div>
            <div style={{ fontSize: 16, fontWeight: 600 }}>
              Current: {currentStreak} day
              {currentStreak === 1 ? "" : "s"}
            </div>
            <div
              style={{
                marginTop: 2,
                fontSize: 12,
                color: "#9ca3af",
              }}
            >
              Longest: {longestStreak} day
              {longestStreak === 1 ? "" : "s"}
            </div>
          </div>
        </div>

        {/* Difficulty breakdown */}
        <h3
          style={{
            fontSize: 14,
            color: "#9ca3af",
            marginBottom: 10,
            textTransform: "uppercase",
            letterSpacing: 0.08,
          }}
        >
          Difficulty breakdown
        </h3>

        <div style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(34,197,94,0.6)",
              background: "rgba(22,163,74,0.16)",
              textAlign: "center",
              minWidth: 120,
              fontSize: 13,
            }}
          >
            Easy ‚Äî {difficultyStats.Easy || 0}
          </div>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(249,115,22,0.6)",
              background: "rgba(234,88,12,0.18)",
              textAlign: "center",
              minWidth: 120,
              fontSize: 13,
            }}
          >
            Medium ‚Äî {difficultyStats.Medium || 0}
          </div>
          <div
            style={{
              flex: 1,
              padding: "8px 12px",
              borderRadius: 999,
              border: "1px solid rgba(239,68,68,0.6)",
              background: "rgba(248,113,113,0.15)",
              textAlign: "center",
              minWidth: 120,
              fontSize: 13,
            }}
          >
            Hard ‚Äî {difficultyStats.Hard || 0}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
