// src/pages/TeamsPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function TeamsPage() {
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [myTeams, setMyTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [teamName, setTeamName] = useState("");
  const [memberIds, setMemberIds] = useState("");
  const [message, setMessage] = useState("");
  const [creating, setCreating] = useState(false);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }
  }, [user, navigate]);

  useEffect(() => {
    if (!token) return;

    async function fetchMyTeams() {
      try {
        const res = await axios.get("http://localhost:5000/teams/mine", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMyTeams(res.data);
      } catch (err) {
        console.error("Fetch my teams error:", err);
        setMessage("Failed to load your teams.");
      } finally {
        setLoading(false);
      }
    }

    fetchMyTeams();
  }, [token]);

  async function handleCreateTeam(e) {
    e.preventDefault();
    setMessage("");

    if (!teamName.trim()) {
      setMessage("Team name is required.");
      return;
    }

    try {
      setCreating(true);

      const ids =
        memberIds.trim().length > 0
          ? memberIds.split(",").map((s) => s.trim())
          : [];

      const res = await axios.post(
        "http://localhost:5000/teams",
        {
          name: teamName.trim(),
          memberIds: ids,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Team created successfully!");
      setTeamName("");
      setMemberIds("");
      setMyTeams((prev) => [...prev, res.data]);
    } catch (err) {
      console.error("Create team error:", err);
      setMessage(
        err.response?.data?.message || "Failed to create team. Try again."
      );
    } finally {
      setCreating(false);
    }
  }

  if (!user) return null;

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <h2>Teams</h2>
      <p style={{ color: "#555" }}>
        Create teams and then use them in <b>Team Battles</b>.
      </p>

      {message && (
        <p
          style={{
            color: message.toLowerCase().includes("fail") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}

      {/* Create team form */}
      <div
        style={{
          border: "1px solid #ddd",
          padding: 15,
          borderRadius: 8,
          marginBottom: 25,
          background: "#fafafa",
        }}
      >
        <h3>Create New Team</h3>
        <form onSubmit={handleCreateTeam}>
          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Team Name*</b>
              <br />
              <input
                type="text"
                value={teamName}
                onChange={(e) => setTeamName(e.target.value)}
                style={{ width: "100%", padding: 6 }}
              />
            </label>
          </div>

          <div style={{ marginBottom: 10 }}>
            <label>
              <b>Member IDs (comma-separated, optional)</b>
              <br />
              <textarea
                value={memberIds}
                onChange={(e) => setMemberIds(e.target.value)}
                placeholder="Paste MongoDB user IDs, separated by commas"
                style={{ width: "100%", padding: 6, minHeight: 60 }}
              />
            </label>
            <small style={{ color: "#777" }}>
              You will be added automatically as the captain and a member.
            </small>
          </div>

          <button type="submit" disabled={creating}>
            {creating ? "Creating..." : "Create Team"}
          </button>
        </form>
      </div>

      {/* My teams */}
      <h3>My Teams</h3>
      {loading ? (
        <p>Loading your teams...</p>
      ) : myTeams.length === 0 ? (
        <p>You are not part of any team yet.</p>
      ) : (
        myTeams.map((team) => (
          <div
            key={team._id}
            style={{
              border: "1px solid #ddd",
              padding: 12,
              borderRadius: 8,
              marginBottom: 10,
            }}
          >
            <h4>{team.name}</h4>
            <p style={{ fontSize: 14, color: "#555" }}>
              Members:{" "}
              {team.members?.map((m) => m.username).join(", ") || "Unknown"}
            </p>
          </div>
        ))
      )}
    </div>
  );
}

export default TeamsPage;
