// client/src/context/AuthContext.js
import React, {
  createContext,
  useContext,
  useEffect,
  useState,
} from "react";
import api from "../utils/api";

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(() => localStorage.getItem("token"));
  const [user, setUser] = useState(null); // current logged-in user (from /users/me)
  const [loading, setLoading] = useState(true);

  // Load user when token changes
  useEffect(() => {
    const loadUser = async () => {
      if (!token) {
        setUser(null);
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const res = await api.get("/users/me");
        setUser(res.data);
      } catch (err) {
        console.error("AuthContext loadUser error:", err);
        setUser(null);
        localStorage.removeItem("token");
        setToken(null);
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, [token]);

  const login = (newToken, userFromLogin) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    // we can optimistically set user here, or let the /users/me fetch update it
    if (userFromLogin) {
      setUser(userFromLogin);
    }
    // optional: set default, but interceptor already handles requests
    api.defaults.headers.common.Authorization = `Bearer ${newToken}`;
  };

  const logout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setUser(null);
    delete api.defaults.headers.common.Authorization;
  };

  const value = {
    token,
    user,
    loading,
    login,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
