// src/pages/ContestPage.js
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

function ContestPage() {
  const { id } = useParams(); // contest id
  const { user, token } = useAuth();
  const [contest, setContest] = useState(null);
  const [leaderboard, setLeaderboard] = useState([]);
  const [error, setError] = useState("");
  const [joinMessage, setJoinMessage] = useState("");
  const [joined, setJoined] = useState(false);
  const [loadingLeaderboard, setLoadingLeaderboard] = useState(false);

  // Chat state
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loadingChat, setLoadingChat] = useState(false);

  // Fetch contest details (with participants)
  useEffect(() => {
    async function fetchContest() {
      try {
        const res = await axios.get(`http://localhost:5000/contests/${id}`);
        setContest(res.data);

        if (user && res.data.participants) {
          const userId = user.id || user._id;
          const isJoined = res.data.participants.some(
            (p) => p._id === userId
          );
          setJoined(isJoined);
        }
      } catch (err) {
        console.error(err);
        setError("Failed to load contest");
      }
    }
    fetchContest();
  }, [id, user]);

  // Fetch leaderboard (and auto-refresh)
  useEffect(() => {
    if (!token) return;

    let intervalId;

    async function fetchLeaderboard() {
      try {
        setLoadingLeaderboard(true);
        const res = await axios.get(
          `http://localhost:5000/contests/${id}/leaderboard`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setLeaderboard(res.data);
      } catch (err) {
        console.error("Leaderboard fetch error:", err);
      } finally {
        setLoadingLeaderboard(false);
      }
    }

    fetchLeaderboard();
    intervalId = setInterval(fetchLeaderboard, 10000);

    return () => clearInterval(intervalId);
  }, [id, token]);

  // Fetch chat messages (poll every 3 seconds)
  useEffect(() => {
    if (!token) return;

    let intervalId;

    async function fetchChat() {
      try {
        setLoadingChat(true);
        const res = await axios.get(
          `http://localhost:5000/contests/${id}/chat?limit=50`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setChatMessages(res.data);
      } catch (err) {
        console.error("Chat fetch error:", err);
      } finally {
        setLoadingChat(false);
      }
    }

    fetchChat();
    intervalId = setInterval(fetchChat, 3000);

    return () => clearInterval(intervalId);
  }, [id, token]);

  async function handleJoin() {
    if (!token) {
      setJoinMessage("You must be logged in to join.");
      return;
    }

    try {
      const res = await axios.post(
        `http://localhost:5000/contests/${id}/join`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setJoinMessage(res.data.message || "Joined contest");
      setJoined(true);

      const contestRes = await axios.get(
        `http://localhost:5000/contests/${id}`
      );
      setContest(contestRes.data);
    } catch (err) {
      console.error(err);
      setJoinMessage(
        err.response?.data?.message || "Failed to join contest."
      );
    }
  }

  async function handleSendMessage(e) {
    e.preventDefault();
    if (!token) {
      return;
    }
    if (!newMessage.trim()) return;

    try {
      const res = await axios.post(
        `http://localhost:5000/contests/${id}/chat`,
        { message: newMessage },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setNewMessage("");
      setChatMessages((prev) => [...prev, res.data]);
    } catch (err) {
      console.error("Send chat error:", err);
    }
  }

  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!contest) return <p>Loading contest...</p>;

  const now = new Date();
  const isActive =
    now >= new Date(contest.startTime) && now <= new Date(contest.endTime);
  const isUpcoming = now < new Date(contest.startTime);
  const isFinished = now > new Date(contest.endTime);

  const statusLabel = isActive
    ? "Running"
    : isUpcoming
    ? "Upcoming"
    : "Finished";

  return (
    <div style={{ maxWidth: 900, margin: "0 auto" }}>
      <h2>{contest.name}</h2>
      {contest.description && <p>{contest.description}</p>}

      <p style={{ fontSize: 14, color: "#555" }}>
        <b>Status:</b> {statusLabel}
        <br />
        <b>Start:</b> {new Date(contest.startTime).toLocaleString()}
        <br />
        <b>End:</b> {new Date(contest.endTime).toLocaleString()}
      </p>

      <div style={{ margin: "10px 0" }}>
        <button onClick={handleJoin} disabled={joined}>
          {joined ? "Joined" : "Join Contest"}
        </button>
        {joinMessage && (
          <p style={{ marginTop: 6, color: "blue" }}>{joinMessage}</p>
        )}
      </div>

      {/* Participants */}
      <div style={{ marginTop: 20, marginBottom: 20 }}>
        <h3>
          Participants{" "}
          <span style={{ fontSize: 14, color: "#777" }}>
            ({contest.participants?.length || 0})
          </span>
        </h3>
        {contest.participants && contest.participants.length > 0 ? (
          <ul>
            {contest.participants.map((p) => (
              <li key={p._id}>{p.username}</li>
            ))}
          </ul>
        ) : (
          <p>No participants yet.</p>
        )}
      </div>

      {/* Problems */}
      <h3>Problems</h3>
      {contest.problems.length === 0 ? (
        <p>No problems in this contest yet.</p>
      ) : (
        <ul>
          {contest.problems.map((cp) => (
            <li key={cp.problem._id}>
              <Link
                to={`/contests/${contest._id}/problems/${cp.problem.slug}`}
              >
                {cp.problem.title} ({cp.problem.difficulty})
              </Link>
            </li>
          ))}
        </ul>
      )}

      {/* Leaderboard */}
      <h3 style={{ marginTop: 30 }}>Leaderboard</h3>
      {loadingLeaderboard && <p>Refreshing leaderboard...</p>}
      {leaderboard.length === 0 ? (
        <p>No submissions yet.</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Rank</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>User</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Score</th>
              <th style={{ border: "1px solid #ddd", padding: 8 }}>Solved</th>
            </tr>
          </thead>
          <tbody>
            {leaderboard.map((row, index) => (
              <tr key={row.userId}>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  {index + 1}
                </td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  {row.username}
                </td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  {row.score}
                </td>
                <td style={{ border: "1px solid #ddd", padding: 8 }}>
                  {row.solvedCount}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Chat */}
      <h3 style={{ marginTop: 30 }}>Contest Chat</h3>
      {!user && <p>You must be logged in to see chat.</p>}

      {user && (
        <>
          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: 8,
              padding: 10,
              maxHeight: 250,
              overflowY: "auto",
              background: "#fafafa",
              marginBottom: 10,
            }}
          >
            {loadingChat && chatMessages.length === 0 && (
              <p>Loading chat...</p>
            )}
            {chatMessages.length === 0 && !loadingChat && (
              <p>No messages yet. Start the conversation!</p>
            )}
            {chatMessages.map((msg) => (
              <div
                key={msg._id}
                style={{ marginBottom: 8, fontSize: 14 }}
              >
                <b>{msg.user?.username || "User"}:</b>{" "}
                <span>{msg.message}</span>{" "}
                <span style={{ color: "#999", fontSize: 11 }}>
                  ({new Date(msg.createdAt).toLocaleTimeString()})
                </span>
              </div>
            ))}
          </div>

          <form onSubmit={handleSendMessage} style={{ display: "flex", gap: 8 }}>
            <input
              type="text"
              placeholder="Type a message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              style={{ flex: 1, padding: 6 }}
            />
            <button type="submit">Send</button>
          </form>
        </>
      )}
    </div>
  );
}

export default ContestPage;
