// src/pages/ProblemPage.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import api from "../utils/api";
import { useAuth } from "../context/AuthContext";

const languageOptions = [
  { value: "python", label: "Python" },
  { value: "javascript", label: "JavaScript" },
  { value: "cpp", label: "C++" },
];

const defaultTemplates = {
  python: `# Write your solution here...
def twoSum(nums, target):
    lookup = {}
    for i, num in enumerate(nums):
        diff = target - num
        if diff in lookup:
            return [lookup[diff], i]
        lookup[num] = i

# Example
nums = [2,7,11,15]; target = 9
print(twoSum(nums, target))`,
  javascript: `// Write your solution here...
function twoSum(nums, target) {
  const map = new Map();
  for (let i = 0; i < nums.length; i++) {
    const complement = target - nums[i];
    if (map.has(complement)) {
      return [map.get(complement), i];
    }
    map.set(nums[i], i);
  }
  return [];
}

// Example
// console.log(twoSum([2,7,11,15], 9));`,
  cpp: `#include <bits/stdc++.h>
using namespace std;

// Write your solution here...
vector<int> twoSum(vector<int>& nums, int target) {
    unordered_map<int,int> mp;
    for (int i = 0; i < (int)nums.size(); i++) {
        int need = target - nums[i];
        if (mp.count(need)) return {mp[need], i};
        mp[nums[i]] = i;
    }
    return {};
}

int main() {
    vector<int> nums = {2,7,11,15};
    int target = 9;
    auto ans = twoSum(nums, target);
    if (ans.size() == 2) cout << ans[0] << " " << ans[1] << "\\n";
    return 0;
}`,
};

function ProblemPage() {
  const { slug } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();

  const [problem, setProblem] = useState(null);
  const [loading, setLoading] = useState(true);

  const [language, setLanguage] = useState("python");
  const [code, setCode] = useState(defaultTemplates.python);
  const [customInput, setCustomInput] = useState("");
  const [output, setOutput] = useState("");
  const [running, setRunning] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState("");

  const contestId = location.state?.contestId || null;

  // Load problem by slug
  useEffect(() => {
    async function load() {
      try {
        setLoading(true);
        const res = await api.get(`/problems/${slug}`);
        setProblem(res.data);

        if (res.data.exampleTests && res.data.exampleTests.length > 0) {
          setCustomInput(res.data.exampleTests[0].input || "");
        }
      } catch (err) {
        console.error("Failed to load problem:", err);
      } finally {
        setLoading(false);
      }
    }
    load();
  }, [slug]);

  // When language changes, load default template
  useEffect(() => {
    setCode(defaultTemplates[language] || "");
  }, [language]);

  // Run Code (no auth needed)
  async function handleRunCode() {
    try {
      setRunning(true);
      setOutput("");

      const res = await api.post("/judge/run", {
        code,
        language,
        input: customInput,
      });

      setOutput(res.data.output || "No output received from runner.");
    } catch (err) {
      console.error("Run code error:", err);
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        "Error executing code.";
      setOutput(msg);
    } finally {
      setRunning(false);
    }
  }

  // Submit (auth required)
  async function handleSubmit() {
    if (!user) {
      setSubmitMessage("You must be logged in to submit.");
      return;
    }
    if (!problem) return;

    try {
      setSubmitting(true);
      setSubmitMessage("");

      const res = await api.post("/submissions", {
        problemId: problem._id,
        code,
        language,
        contestId: contestId || undefined,
      });

      const data = res.data || {};

      // üîç Safely derive a status string from whatever backend returns
      const rawStatus =
        data.status ||
        data.submission?.status ||
        data.result?.status ||
        data.message ||
        null;

      const statusText = rawStatus || "Submitted";

      setSubmitMessage(`Submission status: ${statusText}`);
    } catch (err) {
      console.error("Submit error:", err);
      const msg =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Error submitting solution.";
      setSubmitMessage(msg);
    } finally {
      setSubmitting(false);
    }
  }

  if (loading || !problem) {
    return (
      <div className="app-page">
        <h2 style={{ color: "#fff" }}>Loading problem...</h2>
      </div>
    );
  }

  const difficultyColor =
    problem.difficulty === "Easy"
      ? "#22c55e"
      : problem.difficulty === "Medium"
      ? "#f97316"
      : "#ef4444";

  return (
    <div className="app-page" style={{ display: "flex", gap: "24px" }}>
      {/* LEFT: Problem description */}
      <div
        style={{
          flex: 1,
          background: "#111827",
          borderRadius: "18px",
          padding: "24px",
          boxShadow: "0 18px 60px rgba(0,0,0,0.7)",
          color: "#e5e7eb",
          minHeight: "calc(100vh - 140px)",
        }}
      >
        <div style={{ marginBottom: 16 }}>
          <h1 style={{ fontSize: 32, fontWeight: 700, color: "#5ee7ff" }}>
            {problem.title}
          </h1>
          <span
            style={{
              display: "inline-block",
              marginTop: 8,
              padding: "4px 10px",
              borderRadius: 999,
              fontSize: 12,
              fontWeight: 600,
              backgroundColor: "#020617",
              color: difficultyColor,
              border: `1px solid ${difficultyColor}`,
            }}
          >
            {problem.difficulty}
          </span>
        </div>

        <h3 style={{ color: "#38bdf8", fontSize: 18, marginBottom: 6 }}>
          Description
        </h3>
        <p
          style={{
            whiteSpace: "pre-wrap",
            lineHeight: 1.6,
            marginBottom: 18,
          }}
        >
          {problem.description}
        </p>

        {problem.exampleTests && problem.exampleTests.length > 0 && (
          <>
            <h3
              style={{
                color: "#38bdf8",
                fontSize: 18,
                marginBottom: 10,
                marginTop: 12,
              }}
            >
              Examples
            </h3>
            <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
              {problem.exampleTests.map((t, idx) => (
                <div
                  key={idx}
                  style={{
                    background: "#020617",
                    borderRadius: 12,
                    padding: 16,
                    border: "1px solid #1f2937",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      marginBottom: 8,
                      color: "#9ca3af",
                      fontSize: 13,
                    }}
                  >
                    <span>Example {idx + 1}</span>
                    <button
                      onClick={() => setCustomInput(t.input || "")}
                      style={{
                        fontSize: 12,
                        padding: "2px 10px",
                        borderRadius: 999,
                        border: "1px solid #4b5563",
                        background: "transparent",
                        color: "#e5e7eb",
                        cursor: "pointer",
                      }}
                    >
                      Use as Input
                    </button>
                  </div>
                  <pre
                    style={{
                      margin: 0,
                      fontFamily: "monospace",
                      fontSize: 13,
                      color: "#e5e7eb",
                    }}
                  >
                    {t.input && `Input:\n${t.input}\n`}
                    {t.output && `Output:\n${t.output}`}
                  </pre>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* RIGHT: editor + IO */}
      <div
        style={{
          flex: 1,
          background: "#020617",
          borderRadius: "18px",
          padding: "18px 18px 22px",
          boxShadow: "0 18px 60px rgba(0,0,0,0.7)",
          color: "#e5e7eb",
          display: "flex",
          flexDirection: "column",
          minHeight: "calc(100vh - 140px)",
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: 12,
            gap: 12,
          }}
        >
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            style={{
              background: "#020617",
              border: "1px solid #4b5563",
              borderRadius: 999,
              color: "#e5e7eb",
              padding: "6px 14px",
            }}
          >
            {languageOptions.map((opt) => (
              <option value={opt.value} key={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>

          <button
            onClick={handleRunCode}
            disabled={running}
            style={{
              background: "#22c55e",
              border: "none",
              color: "#020617",
              fontWeight: 600,
              padding: "6px 16px",
              borderRadius: 999,
              cursor: running ? "default" : "pointer",
              opacity: running ? 0.7 : 1,
            }}
          >
            {running ? "Running..." : "Run Code"}
          </button>

          <button
            onClick={handleSubmit}
            disabled={submitting}
            style={{
              background: "#a855f7",
              border: "none",
              color: "#f9fafb",
              fontWeight: 600,
              padding: "6px 16px",
              borderRadius: 999,
              cursor: submitting ? "default" : "pointer",
              opacity: submitting ? 0.7 : 1,
            }}
          >
            {submitting ? "Submitting..." : "Submit"}
          </button>
        </div>

        <div
          style={{
            flex: 1,
            borderRadius: 12,
            overflow: "hidden",
            border: "1px solid #111827",
            marginBottom: 12,
          }}
        >
          <textarea
            value={code}
            onChange={(e) => setCode(e.target.value)}
            spellCheck={false}
            style={{
              width: "100%",
              height: "100%",
              minHeight: 280,
              border: "none",
              outline: "none",
              resize: "none",
              background: "#020617",
              color: "#e5e7eb",
              fontFamily: "JetBrains Mono, Menlo, monospace",
              fontSize: 13,
              padding: 12,
            }}
          />
        </div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 12,
          }}
        >
          <div>
            <div
              style={{
                fontSize: 13,
                color: "#9ca3af",
                marginBottom: 4,
              }}
            >
              Custom Input
            </div>
            <textarea
              value={customInput}
              onChange={(e) => setCustomInput(e.target.value)}
              spellCheck={false}
              style={{
                width: "100%",
                minHeight: 120,
                borderRadius: 10,
                border: "1px solid #111827",
                background: "#020617",
                color: "#e5e7eb",
                fontFamily: "JetBrains Mono, monospace",
                fontSize: 13,
                padding: 10,
                resize: "vertical",
              }}
            />
          </div>

          <div>
            <div
              style={{
                fontSize: 13,
                color: "#9ca3af",
                marginBottom: 4,
              }}
            >
              Output
            </div>
            <pre
              style={{
                width: "100%",
                minHeight: 120,
                borderRadius: 10,
                border: "1px solid #111827",
                background: "#020617",
                color: "#e5e7eb",
                fontFamily: "JetBrains Mono, monospace",
                fontSize: 13,
                padding: 10,
                margin: 0,
                whiteSpace: "pre-wrap",
              }}
            >
              {output || "Run your code to see output here."}
            </pre>
          </div>
        </div>

        {submitMessage && (
          <div
            style={{
              marginTop: 8,
              fontSize: 13,
              color: submitMessage.startsWith("Submission status")
                ? "#22c55e"
                : "#f97316",
            }}
          >
            {submitMessage}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProblemPage;
