// src/pages/DuelPage.js
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function DuelPage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [roomCode, setRoomCode] = useState("");

  // If not logged in, redirect to login with message
  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  if (!user) return null;

  const handleCreateRoom = () => {
    const roomId = Math.random().toString(36).substring(2, 8);
    navigate(`/duel-room/${roomId}`);
  };

  const handleJoinRoom = (e) => {
    e.preventDefault();
    const trimmed = roomCode.trim();
    if (!trimmed) return;
    navigate(`/duel-room/${trimmed}`);
  };

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">1 vs 1 Room</h1>
        <p className="page-subtitle">
          Create a room and share the code, or join an existing room to battle
          your friend.
        </p>

        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "1.5rem",
            marginTop: "1rem",
          }}
        >
          {/* Create Room */}
          <div
            style={{
              padding: "1rem 1.2rem",
              borderRadius: "16px",
              border: "1px solid rgba(255,255,255,0.15)",
              background: "rgba(255,255,255,0.04)",
            }}
          >
            <h3 className="section-title">Create Room</h3>
            <p className="page-muted" style={{ marginBottom: "0.6rem" }}>
              We&apos;ll generate a short room code. Share the URL with your
              opponent.
            </p>
            <button className="btn btn-primary" onClick={handleCreateRoom}>
              Create New Room
            </button>
          </div>

          {/* Join Room */}
          <div
            style={{
              padding: "1rem 1.2rem",
              borderRadius: "16px",
              border: "1px solid rgba(255,255,255,0.15)",
              background: "rgba(255,255,255,0.04)",
            }}
          >
            <h3 className="section-title">Join Room</h3>
            <p className="page-muted" style={{ marginBottom: "0.4rem" }}>
              Enter the room code shared by your friend.
            </p>
            <form
              onSubmit={handleJoinRoom}
              style={{ display: "flex", gap: "0.6rem", flexWrap: "wrap" }}
            >
              <input
                type="text"
                value={roomCode}
                onChange={(e) => setRoomCode(e.target.value)}
                className="input-control"
                placeholder="e.g. ab12cd"
                style={{ maxWidth: 220 }}
              />
              <button type="submit" className="btn btn-secondary">
                Join Room
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DuelPage;
