import React, { useMemo } from "react";
import Editor from "@monaco-editor/react";
import "./MonacoCodeRunner.css";

const LANGUAGE_OPTIONS = [
  { value: "javascript", label: "JavaScript" },
  { value: "python", label: "Python" },
  { value: "cpp", label: "C++" },
];

const DEFAULT_SNIPPETS = {
  javascript: `// JavaScript template
function solve() {
  const input = require("fs").readFileSync(0, "utf8").trim();
  console.log(input);
}

solve();`,
  python: `# Python template
def solve():
    import sys
    data = sys.stdin.read().strip()
    print(data)

if __name__ == "__main__":
    solve()`,
  cpp: `#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    string s;
    if (getline(cin, s)) {
        cout << s << "\\n";
    }
    return 0;
}`,
};

export default function MonacoCodeRunner({
  value,
  onChange,
  language,
  onLanguageChange,
  onRun,
  onSubmit,
  runOutput,
  isRunning = false,
  isSubmitting = false,
  allowSubmit = true,
  readOnly = false,
}) {
  const code = value ?? DEFAULT_SNIPPETS[language] ?? "";

  const handleLanguageChange = (event) => {
    const next = event.target.value;
    onLanguageChange?.(next);
    if (!value && DEFAULT_SNIPPETS[next]) {
      onChange?.(DEFAULT_SNIPPETS[next]);
    }
  };

  const toolbarButtons = useMemo(
    () => [
      {
        key: "run",
        label: isRunning ? "Running..." : "Run Code",
        disabled: readOnly || isRunning,
        action: () => onRun?.(),
        className: "monaco-btn primary",
      },
      allowSubmit && {
        key: "submit",
        label: isSubmitting ? "Submitting..." : "Submit to Duel",
        disabled: readOnly || isSubmitting,
        action: () => onSubmit?.(),
        className: "monaco-btn accent",
      },
    ].filter(Boolean),
    [allowSubmit, isRunning, isSubmitting, onRun, onSubmit, readOnly]
  );

  return (
    <div className="monaco-runner">
      <div className="monaco-toolbar">
        <div className="language-select">
          <label htmlFor="monaco-language">Language</label>
          <select
            id="monaco-language"
            value={language}
            onChange={handleLanguageChange}
            disabled={readOnly}
          >
            {LANGUAGE_OPTIONS.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
        </div>
        <div className="toolbar-actions">
          {toolbarButtons.map((btn) => (
            <button
              key={btn.key}
              type="button"
              className={btn.className}
              disabled={btn.disabled}
              onClick={btn.action}
            >
              {btn.label}
            </button>
          ))}
        </div>
      </div>
      <div className="editor-wrapper">
        <Editor
          height="360px"
          theme="vs-dark"
          language={language}
          value={code}
          onChange={(next) => onChange?.(next ?? "")}
          options={{
            fontSize: 14,
            minimap: { enabled: false },
            readOnly,
            scrollBeyondLastLine: false,
            smoothScrolling: true,
            automaticLayout: true,
          }}
        />
      </div>
      <div className="output-panel">
        <div className="output-header">Output</div>
        <pre>{runOutput || "Run code to see output…"}</pre>
      </div>
    </div>
  );
}
