// client/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import api from "../utils/api";

function getInitials(name = "", username = "") {
  const source = name || username || "";
  const parts = source.trim().split(" ");
  if (parts.length === 1) {
    return parts[0].slice(0, 2).toUpperCase();
  }
  return (parts[0][0] + parts[1][0]).toUpperCase();
}

function formatDate(dateStr) {
  if (!dateStr) return "—";
  const d = new Date(dateStr);
  if (Number.isNaN(d.getTime())) return "—";
  return d.toLocaleDateString(undefined, {
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
  });
}

const pillBase = {
  padding: "8px 16px",
  borderRadius: 999,
  fontSize: 13,
  display: "inline-flex",
  alignItems: "center",
  gap: 6,
};

function Profile() {
  const [profile, setProfile] = useState(null); // { user, stats }
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    let cancelled = false;

    async function load() {
      try {
        setLoading(true);
        setError("");
        const res = await api.get("/users/me");
        if (!cancelled) {
          setProfile(res.data || null);
        }
      } catch (err) {
        console.error("Profile load error:", err);
        if (!cancelled) {
          setError(
            err.response?.data?.message ||
              err.response?.data?.error ||
              "Failed to load profile."
          );
        }
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    load();
    return () => {
      cancelled = true;
    };
  }, []);

  if (loading) {
    return (
      <div
        className="app-page"
        style={{
          minHeight: "calc(100vh - 80px)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "#e5e7eb",
        }}
      >
        Loading profile…
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div
        className="app-page"
        style={{
          minHeight: "calc(100vh - 80px)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "#fca5a5",
        }}
      >
        {error || "Failed to load profile."}
      </div>
    );
  }

  const user = profile.user || {};
  const stats = profile.stats || {};

  const initials = getInitials(user.name, user.username);
  const rating = user.rating || stats.rating || 1500;

  const problemsSolved = stats.problemsSolved ?? stats.solved ?? 0;
  const easySolved = stats.easySolved ?? 0;
  const mediumSolved = stats.mediumSolved ?? 0;
  const hardSolved = stats.hardSolved ?? 0;

  const totalSubmissions = stats.totalSubmissions ?? stats.submissions ?? 0;
  const acceptedCount = stats.acceptedCount ?? stats.accepted ?? 0;
  const acceptanceRate =
    totalSubmissions > 0
      ? Math.round((acceptedCount / totalSubmissions) * 100)
      : 0;

  const duelWins = stats.duelStats?.wins ?? 0;
  const duelLosses = stats.duelStats?.losses ?? 0;

  const streakCurrent = stats.streak?.current ?? 0;
  const streakLongest = stats.streak?.longest ?? 0;

  const languageStats = stats.languageStats || {};
  const languageEntries = Object.entries(languageStats);
  const totalLangSubs = languageEntries.reduce(
    (sum, [, v]) => sum + (v || 0),
    0
  );

  return (
    <div
      className="app-page"
      style={{
        minHeight: "calc(100vh - 80px)",
        paddingTop: 24,
        paddingBottom: 32,
      }}
    >
      <div
        style={{
          maxWidth: 1120,
          margin: "0 auto",
        }}
      >
        {/* Main card */}
        <div
          style={{
            background:
              "radial-gradient(circle at top left,#1d2433 0,#020617 45%,#020617 100%)",
            borderRadius: 24,
            padding: 24,
            boxShadow: "0 28px 80px rgba(0,0,0,0.8)",
            border: "1px solid rgba(148,163,184,0.12)",
            color: "#e5e7eb",
          }}
        >
          {/* Header row */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              gap: 24,
              alignItems: "center",
              marginBottom: 24,
            }}
          >
            <div style={{ display: "flex", alignItems: "center", gap: 16 }}>
              {/* Avatar */}
              <div
                style={{
                  width: 72,
                  height: 72,
                  borderRadius: "50%",
                  background:
                    "conic-gradient(from 180deg, #22c55e, #38bdf8, #a855f7, #22c55e)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  padding: 2,
                }}
              >
                <div
                  style={{
                    width: "100%",
                    height: "100%",
                    borderRadius: "50%",
                    background: "#020617",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontWeight: 700,
                    fontSize: 24,
                    letterSpacing: 1,
                  }}
                >
                  {initials}
                </div>
              </div>

              <div>
                <div
                  style={{
                    fontSize: 24,
                    fontWeight: 700,
                    letterSpacing: 0.4,
                  }}
                >
                  {user.name || user.username || "Anonymous"}
                </div>
                <div
                  style={{
                    fontSize: 13,
                    color: "#9ca3af",
                    marginTop: 4,
                  }}
                >
                  @{user.username || "user"}
                </div>
                <div
                  style={{
                    fontSize: 13,
                    color: "#64748b",
                    marginTop: 8,
                  }}
                >
                  {user.bio || "No bio yet."}
                </div>
              </div>
            </div>

            {/* Rating + quick numbers */}
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "flex-end",
                gap: 10,
              }}
            >
              <div
                style={{
                  ...pillBase,
                  background:
                    "linear-gradient(135deg, rgba(59,130,246,0.1), rgba(59,130,246,0.26))",
                  border: "1px solid rgba(59,130,246,0.7)",
                  color: "#bfdbfe",
                  fontSize: 12,
                  letterSpacing: 1.2,
                  textTransform: "uppercase",
                }}
              >
                <span style={{ opacity: 0.8 }}>Rating</span>
                <span style={{ fontSize: 18, fontWeight: 700 }}>{rating}</span>
              </div>

              <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
                <div
                  style={{
                    ...pillBase,
                    background: "rgba(34,197,94,0.1)",
                    border: "1px solid rgba(34,197,94,0.5)",
                    color: "#bbf7d0",
                  }}
                >
                  <span style={{ opacity: 0.75, fontSize: 12 }}>
                    Problems solved
                  </span>
                  <span style={{ fontWeight: 600 }}>{problemsSolved}</span>
                </div>
                <div
                  style={{
                    ...pillBase,
                    background: "rgba(129,140,248,0.12)",
                    border: "1px solid rgba(129,140,248,0.5)",
                    color: "#c7d2fe",
                  }}
                >
                  <span style={{ opacity: 0.75, fontSize: 12 }}>
                    Submissions
                  </span>
                  <span style={{ fontWeight: 600 }}>{totalSubmissions}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Overview grid */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(4, minmax(0,1fr))",
              gap: 16,
              marginBottom: 24,
            }}
          >
            {/* Member since */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 8,
                }}
              >
                Member since
              </div>
              <div style={{ fontSize: 18, fontWeight: 600 }}>
                {formatDate(user.createdAt)}
              </div>
            </div>

            {/* Problems by difficulty */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 8,
                }}
              >
                Problems solved
              </div>
              <div
                style={{
                  fontSize: 22,
                  fontWeight: 700,
                  marginBottom: 6,
                }}
              >
                {problemsSolved}
              </div>
              <div
                style={{
                  display: "flex",
                  gap: 8,
                  flexWrap: "wrap",
                  fontSize: 12,
                }}
              >
                <span style={{ color: "#bbf7d0" }}>Easy {easySolved}</span>
                <span style={{ color: "#fed7aa" }}>Medium {mediumSolved}</span>
                <span style={{ color: "#fecaca" }}>Hard {hardSolved}</span>
              </div>
            </div>

            {/* Submissions / acceptance */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 8,
                }}
              >
                Submissions
              </div>
              <div
                style={{
                  fontSize: 22,
                  fontWeight: 700,
                  marginBottom: 4,
                }}
              >
                {totalSubmissions}
              </div>
              <div
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                  marginBottom: 6,
                }}
              >
                Accepted {acceptedCount} •{" "}
                <span style={{ color: "#22c55e" }}>{acceptanceRate}%</span>
              </div>
              <div
                style={{
                  position: "relative",
                  width: "100%",
                  height: 6,
                  borderRadius: 999,
                  background: "#020617",
                  overflow: "hidden",
                }}
              >
                <div
                  style={{
                    position: "absolute",
                    inset: 0,
                    width: `${Math.min(100, acceptanceRate)}%`,
                    background:
                      "linear-gradient(90deg,#22c55e,#4ade80,#bef264)",
                  }}
                />
              </div>
            </div>

            {/* Duel & streak */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 8,
                }}
              >
                Competitive
              </div>
              <div
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                  marginBottom: 4,
                }}
              >
                Duel record
              </div>
              <div
                style={{
                  fontSize: 16,
                  fontWeight: 600,
                  marginBottom: 8,
                }}
              >
                {duelWins} W / {duelLosses} L
              </div>
              <div
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                }}
              >
                Current streak:{" "}
                <span style={{ color: "#e5e7eb", fontWeight: 500 }}>
                  {streakCurrent} day{streakCurrent === 1 ? "" : "s"}
                </span>
              </div>
              <div
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                }}
              >
                Best streak:{" "}
                <span style={{ color: "#e5e7eb", fontWeight: 500 }}>
                  {streakLongest} day{streakLongest === 1 ? "" : "s"}
                </span>
              </div>
            </div>
          </div>

          {/* Skill snapshot row (languages + difficulty pill row) */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "minmax(0,1.3fr) minmax(0,1fr)",
              gap: 18,
            }}
          >
            {/* Language distribution */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 10,
                }}
              >
                Language footprint
              </div>

              {languageEntries.length === 0 || totalLangSubs === 0 ? (
                <div
                  style={{
                    fontSize: 13,
                    color: "#6b7280",
                  }}
                >
                  Start solving problems to see your language stats.
                </div>
              ) : (
                <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                  {languageEntries.map(([lang, count]) => {
                    const pct = Math.round((count / totalLangSubs) * 100);
                    return (
                      <div key={lang}>
                        <div
                          style={{
                            display: "flex",
                            justifyContent: "space-between",
                            fontSize: 13,
                            marginBottom: 4,
                          }}
                        >
                          <span style={{ textTransform: "capitalize" }}>
                            {lang}
                          </span>
                          <span style={{ color: "#9ca3af" }}>
                            {count} · {pct}%
                          </span>
                        </div>
                        <div
                          style={{
                            position: "relative",
                            width: "100%",
                            height: 6,
                            borderRadius: 999,
                            background: "#020617",
                            overflow: "hidden",
                          }}
                        >
                          <div
                            style={{
                              position: "absolute",
                              inset: 0,
                              width: `${Math.min(100, pct)}%`,
                              background:
                                "linear-gradient(90deg,#38bdf8,#8b5cf6)",
                            }}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            {/* Difficulty summary pills */}
            <div
              style={{
                background: "rgba(15,23,42,0.92)",
                borderRadius: 16,
                padding: 16,
                border: "1px solid rgba(31,41,55,0.9)",
                display: "flex",
                flexDirection: "column",
                gap: 12,
                justifyContent: "space-between",
              }}
            >
              <div
                style={{
                  fontSize: 12,
                  textTransform: "uppercase",
                  color: "#9ca3af",
                  letterSpacing: 1.2,
                  marginBottom: 4,
                }}
              >
                Difficulty focus
              </div>
              <div
                style={{
                  display: "flex",
                  flexWrap: "wrap",
                  gap: 8,
                }}
              >
                <div
                  style={{
                    ...pillBase,
                    background: "rgba(22,163,74,0.18)",
                    border: "1px solid rgba(22,163,74,0.6)",
                    color: "#bbf7d0",
                    flexGrow: 1,
                    justifyContent: "space-between",
                  }}
                >
                  <span>Easy</span>
                  <span style={{ fontWeight: 600 }}>{easySolved}</span>
                </div>
                <div
                  style={{
                    ...pillBase,
                    background: "rgba(234,179,8,0.15)",
                    border: "1px solid rgba(234,179,8,0.65)",
                    color: "#facc15",
                    flexGrow: 1,
                    justifyContent: "space-between",
                  }}
                >
                  <span>Medium</span>
                  <span style={{ fontWeight: 600 }}>{mediumSolved}</span>
                </div>
                <div
                  style={{
                    ...pillBase,
                    background: "rgba(239,68,68,0.12)",
                    border: "1px solid rgba(239,68,68,0.6)",
                    color: "#fecaca",
                    flexGrow: 1,
                    justifyContent: "space-between",
                  }}
                >
                  <span>Hard</span>
                  <span style={{ fontWeight: 600 }}>{hardSolved}</span>
                </div>
              </div>
              <div
                style={{
                  fontSize: 12,
                  color: "#6b7280",
                  marginTop: 4,
                }}
              >
                Aim for a healthy mix of all three difficulties to build depth
                and speed.
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
