// src/App.js
import { Routes, Route, Link, useLocation } from "react-router-dom";
import Home from "./pages/Home";
import ProblemPage from "./pages/ProblemPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import MySubmissions from "./pages/MySubmissions";
import Profile from "./pages/Profile";
import { useAuth } from "./context/AuthContext";
import LandingPage from "./LandingPage";
import SimpleCodeEditor from "./components/SimpleCodeEditor";

function App() {
  const { user, logout } = useAuth();
  const location = useLocation();

  const isLanding = location.pathname === "/";
  const isSimpleEditor = location.pathname === "/simple-editor";

  return (
    <div
      style={{
        padding: isLanding ? 0 : "20px",
        fontFamily: "sans-serif",
      }}
    >
      {/* Navbar hidden on landing page */}
      {!isLanding && (
        <nav
          style={{
            marginBottom: 20,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderBottom: "1px solid #ddd",
            paddingBottom: 10,
          }}
        >
          <div>
            <Link
              to="/"
              style={{ fontWeight: "bold", textDecoration: "none" }}
            >
              CodeGen4Future
            </Link>
          </div>

          <div>
            {/* Hide Problems & Simple Editor links on Simple Editor page */}
            {!isSimpleEditor && (
              <>
                <Link to="/problems" style={{ marginRight: 10 }}>
                  Problems
                </Link>
                <Link to="/simple-editor" style={{ marginRight: 10 }}>
                  Simple Editor
                </Link>
              </>
            )}

            {user ? (
              <>
                <Link to="/submissions" style={{ marginRight: 10 }}>
                  My Submissions
                </Link>
                <Link to="/profile" style={{ marginRight: 10 }}>
                  Profile
                </Link>
                <span style={{ marginRight: 10 }}>Hi, {user.username}</span>
                <button onClick={logout}>Logout</button>
              </>
            ) : (
              <>
                <Link to="/login" style={{ marginRight: 10 }}>
                  Login
                </Link>
                <Link to="/register">Register</Link>
              </>
            )}
          </div>
        </nav>
      )}

      <Routes>
        {/* Landing page */}
        <Route path="/" element={<LandingPage />} />

        {/* LeetCode clone problem list */}
        <Route path="/problems" element={<Home />} />

        {/* Problem details */}
        <Route path="/problems/:slug" element={<ProblemPage />} />

        {/* Simple standalone editor */}
        <Route path="/simple-editor" element={<SimpleCodeEditor />} />

        {/* Auth */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* User pages */}
        <Route path="/submissions" element={<MySubmissions />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>
    </div>
  );
}

export default App;
