// client/src/pages/DuelPage.js
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function DuelPage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [roomCode, setRoomCode] = useState("");

  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  if (!user) return null;

  const generateRoomCode = () =>
    Math.random().toString(36).substring(2, 8).toLowerCase();

  const handleCreateRoom = () => {
    const code = generateRoomCode();
    navigate(`/duel-room/${code}`, {
      state: { isHost: true },
    });
  };

  const handleJoinRoom = (e) => {
    e.preventDefault();
    const trimmed = roomCode.trim();
    if (!trimmed) return;

    let code = trimmed;
    try {
      if (trimmed.startsWith("http")) {
        const u = new URL(trimmed);
        const parts = u.pathname.split("/").filter(Boolean);
        code = parts[parts.length - 1];
      }
    } catch (_) {}

    navigate(`/duel-room/${code}`, {
      state: { isHost: false },
    });
  };

  return (
    <div className="page-container">
      <div
        className="page-card"
        style={{
          borderRadius: 28,
          padding: 28,
          border: "1px solid #111827",
          background:
            "radial-gradient(circle at top, rgba(96,165,250,0.15), transparent 60%), #050509",
          boxShadow: "0 28px 80px rgba(0,0,0,0.9)",
        }}
      >
        <h1 className="page-heading" style={{ marginBottom: 6 }}>
          1 vs 1 Room
        </h1>
        <p className="page-subtitle" style={{ maxWidth: 520 }}>
          Create a private room and share the code, or join an existing room to
          battle a friend in real time.
        </p>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
            gap: "1.5rem",
            marginTop: "1.8rem",
          }}
        >
          {/* Create Room */}
          <div
            style={{
              padding: "1.4rem 1.6rem",
              borderRadius: 20,
              border: "1px solid rgba(59,130,246,0.5)",
              background:
                "linear-gradient(135deg, rgba(15,23,42,0.9), rgba(15,23,42,1))",
              boxShadow: "0 18px 40px rgba(0,0,0,0.9)",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "0.6rem",
              }}
            >
              <h3 className="section-title" style={{ marginBottom: 0 }}>
                Create Room
              </h3>
              <span
                style={{
                  fontSize: 11,
                  color: "#a5b4fc",
                  textTransform: "uppercase",
                  letterSpacing: 0.12,
                }}
              >
                Host
              </span>
            </div>
            <p className="page-muted" style={{ marginBottom: "0.9rem" }}>
              Generate a short room code and invite your opponent by sharing the
              URL or code.
            </p>
            <button
              className="btn btn-primary"
              onClick={handleCreateRoom}
              style={{
                padding: "0.6rem 1.2rem",
                borderRadius: 999,
                fontWeight: 600,
              }}
            >
              Create New Room
            </button>
          </div>

          {/* Join Room */}
          <div
            style={{
              padding: "1.4rem 1.6rem",
              borderRadius: 20,
              border: "1px solid rgba(16,185,129,0.5)",
              background:
                "linear-gradient(135deg, rgba(15,23,42,0.9), rgba(15,23,42,1))",
              boxShadow: "0 18px 40px rgba(0,0,0,0.9)",
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "0.6rem",
              }}
            >
              <h3 className="section-title" style={{ marginBottom: 0 }}>
                Join Room
              </h3>
              <span
                style={{
                  fontSize: 11,
                  color: "#6ee7b7",
                  textTransform: "uppercase",
                  letterSpacing: 0.12,
                }}
              >
                Guest
              </span>
            </div>
            <p className="page-muted" style={{ marginBottom: "0.6rem" }}>
              Enter the room code or full duel URL shared by your friend.
            </p>
            <form
              onSubmit={handleJoinRoom}
              style={{ display: "flex", gap: "0.6rem", flexWrap: "wrap" }}
            >
              <input
                type="text"
                value={roomCode}
                onChange={(e) => setRoomCode(e.target.value)}
                className="input-control"
                placeholder="e.g. ab12cd or full URL"
                style={{
                  maxWidth: 260,
                  textTransform: "lowercase",
                  letterSpacing: 0.06,
                }}
              />
              <button
                type="submit"
                className="btn btn-secondary"
                style={{
                  padding: "0.55rem 1.1rem",
                  borderRadius: 999,
                  fontWeight: 500,
                }}
              >
                Join Room
              </button>
            </form>
            <p
              className="page-muted"
              style={{ fontSize: 12, marginTop: 8, opacity: 0.8 }}
            >
              Tip: you can paste the full duel URL â€“ we&apos;ll extract the code
              for you.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DuelPage;
