import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function DuelPage() {
  const { user, token } = useAuth();
  const [matchLink, setMatchLink] = useState("");

  async function createMatch() {
    const res = await axios.post(`${API_URL}/duel/create`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const link = `${window.location.origin}/duel/${res.data.matchId}`;
    setMatchLink(link);
  }

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">Create a 1vs1 Match</h1>
        <p className="page-subtitle">Share the link with a friend to battle.</p>

        <button className="btn btn-primary" onClick={createMatch}>
          Generate Match Link
        </button>

        {matchLink && (
          <div style={{ marginTop: 20 }}>
            <p>Share this link:</p>
            <input value={matchLink} readOnly style={{ width: "100%" }} />

            <button
              className="btn btn-secondary"
              style={{ marginTop: 10 }}
              onClick={() => navigator.clipboard.writeText(matchLink)}
            >
              Copy Link
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default DuelPage;
