// src/pages/DuelRoomPage.js
import React, { useEffect, useState } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Editor from "@monaco-editor/react";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { user, token } = useAuth();

  const [problems, setProblems] = useState([]);
  const [selectedSlug, setSelectedSlug] = useState("");
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("// Pick a problem to start battling!\n");

  const [language, setLanguage] = useState("javascript");
  const [customInput, setCustomInput] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const [lastStatus, setLastStatus] = useState("Not submitted");
  const [lastSubmittedAt, setLastSubmittedAt] = useState(null);

  const DUEL_DURATION_SECONDS = 300;
  const [battleStarted, setBattleStarted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(null);
  const [isRunning, setIsRunning] = useState(false);
  const [isOver, setIsOver] = useState(false);
  const [winnerMessage, setWinnerMessage] = useState("");

  // login protection
  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  // fetch problem list
  useEffect(() => {
    async function fetchProblems() {
      try {
        const res = await axios.get(`${API_URL}/problems`);
        setProblems(res.data);
      } catch (err) {
        console.error("Problem fetch error");
      }
    }
    fetchProblems();
  }, []);

  // fetch selected problem
  useEffect(() => {
    if (!selectedSlug) return;

    async function fetchProblem() {
      try {
        const res = await axios.get(`${API_URL}/problems/${selectedSlug}`);
        setProblem(res.data);

        setCustomInput(res.data.exampleTests?.[0]?.input || "");
        setCode(res.data.starterCode?.[language] || "// Write solution\n");
      } catch (err) {
        console.error("Load problem error", err);
      }
    }
    fetchProblem();
  }, [selectedSlug, language]);

  function startBattle() {
    if (!selectedSlug) return alert("Select a problem first!");
    setBattleStarted(true);
    setTimeLeft(DUEL_DURATION_SECONDS);
    setIsRunning(true);
    setOutput("");
  }

  useEffect(() => {
    if (!isRunning || timeLeft === null || isOver) return;

    if (timeLeft <= 0) {
      setIsRunning(false);
      setIsOver(true);
      setWinnerMessage(
        lastStatus === "Accepted"
          ? "â± Time's up â€” You WIN!"
          : "â± Time's up â€” You Lose."
      );
      return;
    }

    const timer = setInterval(() => setTimeLeft((t) => t - 1), 1000);
    return () => clearInterval(timer);
  }, [isRunning, timeLeft, isOver, lastStatus]);

  const fullLink = `${window.location.origin}/duel-room/${roomId}`;

  async function handleCopyLink() {
    await navigator.clipboard.writeText(fullLink);
    alert("Link Copied");
  }

  async function handleRun() {
    if (!battleStarted || isOver) return;

    setLoading(true);
    setOutput("Running...");

    try {
      const res = await axios.post(`${API_URL}/submissions/run`, {
        code,
        language,
        input: customInput,
      });
      setOutput(res.data.output || "No output");
    } catch (err) {
      setOutput("Runtime Error");
    } finally {
      setLoading(false);
    }
  }

  async function handleSubmit() {
    if (!battleStarted || isOver) return;

    setLoading(true);
    try {
      const res = await axios.post(
        `${API_URL}/submissions`,
        { code, language, problemId: problem._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const status = res.data.status;
      setLastStatus(status);

      if (status === "Accepted") {
        setIsRunning(false);
        setIsOver(true);
        setWinnerMessage("ðŸŽ‰ Accepted â€” You WIN!");
      }

      setOutput("Result: " + status);
    } finally {
      setLoading(false);
    }
  }

  const formatTime = (s) =>
    `${String(Math.floor(s / 60)).padStart(2, "0")}:${String(s % 60).padStart(
      2,
      "0"
    )}`;

  return (
    <div className="page-container">
      <div className="page-card battle-header-card">
        <h1 className="page-heading">1v1 Duel Room: {roomId}</h1>

        <div className="battle-link-row">
          <input readOnly value={fullLink} className="input-control" />
          <button onClick={handleCopyLink} className="btn btn-secondary">
            Copy Link
          </button>
        </div>

        <h3>Status: {lastStatus}</h3>
        <h3>Time Left: {timeLeft ? formatTime(timeLeft) : "--:--"}</h3>

        {!battleStarted ? (
          <button className="btn btn-primary" onClick={startBattle}>
            Start Battle
          </button>
        ) : (
          <h3>Battle Started!</h3>
        )}

        {winnerMessage && <h2 style={{ color: "yellow" }}>{winnerMessage}</h2>}
      </div>

      <div className="problem-layout">
        <div className="problem-left">
          <label>Select Problem</label>
          <select
            className="select-control"
            disabled={battleStarted}
            onChange={(e) => setSelectedSlug(e.target.value)}
          >
            <option>-- Choose Problem --</option>
            {problems.map((p) => (
              <option key={p._id} value={p.slug}>
                {p.title} ({p.difficulty})
              </option>
            ))}
          </select>

          <label style={{ marginTop: "10px" }}>Language</label>
          <select
            className="select-control"
            disabled={!battleStarted || isOver}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="cpp">C++</option>
            <option value="java">Java</option>
            <option value="c">C</option>
          </select>

          {problem && (
            <>
              <h2>{problem.title}</h2>
              <p>{problem.description}</p>

              <h3>Sample Input</h3>
              <pre>{problem.exampleTests?.[0]?.input || "None"}</pre>

              <h3>Sample Output</h3>
              <pre>{problem.exampleTests?.[0]?.output || "None"}</pre>
            </>
          )}

          <label style={{ marginTop: "10px" }}>Custom Input:</label>
          <textarea
            className="input-control"
            rows="5"
            value={customInput}
            onChange={(e) => setCustomInput(e.target.value)}
          />
        </div>

        <div className="problem-right">
          <div className="problem-controls">
            <button className="btn-run" onClick={handleRun}>
              Run Code
            </button>
            <button className="btn-submit" onClick={handleSubmit}>
              Submit
            </button>
          </div>

          <Editor
            height="55vh"
            theme="vs-dark"
            language={language}
            value={code}
            onChange={(v) => battleStarted && !isOver && setCode(v)}
            options={{ readOnly: !battleStarted || isOver }}
          />

          <strong>Output:</strong>
          <pre className="problem-output">{output}</pre>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
