// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API_URL || "http://localhost:5000";

function Avatar({ name, src, size = 96 }) {
  const initials = name
    ? name
        .split(" ")
        .map((n) => n[0])
        .slice(0, 2)
        .join("")
        .toUpperCase()
    : "?";
  return src ? (
    <img
      src={src}
      alt={name}
      className="rounded-full object-cover"
      style={{ width: size, height: size }}
    />
  ) : (
    <div
      className="rounded-full bg-gradient-to-br from-indigo-600 to-purple-600 flex items-center justify-center text-white font-semibold"
      style={{ width: size, height: size }}
    >
      <span className="text-2xl">{initials}</span>
    </div>
  );
}

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const fetchProfile = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`${API}/users/profile`, {
          headers: token ? { Authorization: `Bearer ${token}` } : undefined,
        });
        setUser(res.data);
      } catch (err) {
        console.error("Profile load error", err?.response?.data || err.message);
        setError("Failed to load profile");
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  if (loading) {
    return (
      <div className="min-h-[60vh] flex items-center justify-center">
        <div className="text-gray-400">Loading profile...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-[60vh] flex items-center justify-center">
        <div className="text-red-400">{error}</div>
      </div>
    );
  }

  const {
    name,
    username,
    email,
    bio,
    avatarUrl,
    joinedAt,
    stats = {},
  } = user || {};

  return (
    <div className="container mx-auto p-4">
      {/* Header */}
      <div className="rounded-lg overflow-hidden shadow-sm">
        <div className="bg-gradient-to-r from-indigo-700 to-purple-700 p-6 text-white">
          <div className="flex flex-col md:flex-row md:items-center md:gap-6">
            <div className="flex-shrink-0 mb-4 md:mb-0">
              <Avatar name={name || username} src={avatarUrl} size={112} />
            </div>
            <div className="flex-1">
              <div className="flex items-center justify-between">
                <div>
                  <h1 className="text-2xl md:text-3xl font-semibold">
                    {name || username || "Unknown User"}
                  </h1>
                  <p className="text-sm text-indigo-200 mt-1">{username && `@${username}`}</p>
                </div>
                <div className="mt-4 md:mt-0">
                  <button className="bg-white text-indigo-700 px-4 py-2 rounded-md font-medium shadow-sm hover:opacity-95">
                    Edit Profile
                  </button>
                </div>
              </div>

              <p className="mt-4 text-indigo-100 max-w-2xl">
                {bio || "No bio yet. Add a short description about yourself."}
              </p>

              <div className="mt-4 text-indigo-100 text-sm">
                <span>Member since: </span>
                <span className="font-medium">
                  {joinedAt ? new Date(joinedAt).toLocaleDateString() : "—"}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Content */}
        <div className="bg-white dark:bg-gray-800 p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* Left column - stats & overview */}
            <div className="md:col-span-2 space-y-6">
              <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
                <StatCard label="Problems Solved" value={stats.solved || 0} />
                <StatCard label="Rating" value={stats.rating ?? "—"} />
                <StatCard label="Contests" value={stats.contests || 0} />
                <StatCard label="Rank" value={stats.rank ?? "—"} />
              </div>

              <div className="bg-gray-50 dark:bg-gray-900 rounded-md p-4">
                <h3 className="text-lg font-semibold mb-2">Overview</h3>
                <p className="text-sm text-gray-600 dark:text-gray-300">
                  A quick summary of your activity and performance. You can view
                  recent submissions and contest history here.
                </p>
              </div>

              <div className="bg-gray-50 dark:bg-gray-900 rounded-md p-4">
                <h3 className="text-lg font-semibold mb-3">Recent Activity</h3>
                <RecentActivity items={user.recentSubmissions || []} />
              </div>
            </div>

            {/* Right column - actions & contact */}
            <aside className="space-y-6">
              <div className="bg-gray-50 dark:bg-gray-900 rounded-md p-4">
                <h4 className="font-semibold mb-2">Contact</h4>
                <p className="text-sm text-gray-600 dark:text-gray-300">{email || "No email"}</p>
              </div>

              <div className="bg-gray-50 dark:bg-gray-900 rounded-md p-4">
                <h4 className="font-semibold mb-2">Quick Actions</h4>
                <div className="flex flex-col gap-2">
                  <button className="w-full text-left px-3 py-2 rounded-md border border-gray-200 hover:bg-gray-100">
                    View Submissions
                  </button>
                  <button className="w-full text-left px-3 py-2 rounded-md border border-gray-200 hover:bg-gray-100">
                    View Contests
                  </button>
                </div>
              </div>
            </aside>
          </div>
        </div>
      </div>
    </div>
  );
}

function StatCard({ label, value }) {
  return (
    <div className="bg-white dark:bg-gray-800 border border-gray-100 dark:border-gray-700 rounded-md p-4 text-center shadow-sm">
      <div className="text-sm text-gray-500 dark:text-gray-400">{label}</div>
      <div className="mt-2 text-2xl font-semibold text-gray-800 dark:text-gray-100">
        {value}
      </div>
    </div>
  );
}

function RecentActivity({ items = [] }) {
  if (!items.length) {
    return <div className="text-sm text-gray-500">No recent submissions</div>;
  }
  return (
    <ul className="space-y-3">
      {items.map((it) => (
        <li
          key={it._id || it.id}
          className="flex items-center justify-between bg-white dark:bg-gray-800 border border-gray-100 dark:border-gray-700 rounded-md p-3"
        >
          <div>
            <div className="font-medium">{it.problemName || it.title || "Problem"}</div>
            <div className="text-xs text-gray-500">{it.language || it.lang}</div>
          </div>
          <div className="text-sm">
            <span
              className={`px-2 py-1 rounded-full text-xs ${
                it.status === "Accepted"
                  ? "bg-green-100 text-green-700"
                  : it.status === "Wrong Answer"
                  ? "bg-red-100 text-red-700"
                  : "bg-yellow-100 text-yellow-700"
              }`}
            >
              {it.status || "Unknown"}
            </span>
          </div>
        </li>
      ))}
    </ul>
  );
}
