// src/pages/DuelRoomPage.js
import React, { useEffect, useState } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Editor from "@monaco-editor/react";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { user, token } = useAuth();

  const [problems, setProblems] = useState([]);
  const [selectedSlug, setSelectedSlug] = useState("");
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState("// Pick a problem to start battling!\n");
  const [language, setLanguage] = useState("javascript");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const [loadingProblems, setLoadingProblems] = useState(false);
  const [error, setError] = useState("");

  // battle status (local, for this user)
  const [lastStatus, setLastStatus] = useState("Not submitted");
  const [lastSubmittedAt, setLastSubmittedAt] = useState(null);

  // Protect route: must be logged in
  useEffect(() => {
    if (!user) {
      navigate("/login", {
        state: { from: location.pathname, message: "You must be logged in" },
      });
    }
  }, [user, navigate, location.pathname]);

  // Load problems list once
  useEffect(() => {
    async function fetchProblems() {
      try {
        setLoadingProblems(true);
        const res = await axios.get(`${API_URL}/problems`);
        setProblems(res.data || []);
      } catch (err) {
        console.error("Fetch problems error", err);
        setError("Failed to load problems list.");
      } finally {
        setLoadingProblems(false);
      }
    }
    fetchProblems();
  }, []);

  // Load selected problem details when slug or language changes
  useEffect(() => {
    if (!selectedSlug) {
      setProblem(null);
      setCode("// Pick a problem to start battling!\n");
      return;
    }
    async function fetchProblem() {
      try {
        setError("");
        const res = await axios.get(`${API_URL}/problems/${selectedSlug}`);
        setProblem(res.data);
        const starter =
          res.data.starterCode?.[language] ||
          res.data.starterCode ||
          "// Write your solution here\n";
        setCode(starter);
      } catch (err) {
        console.error("Fetch problem error", err);
        setError("Failed to load problem details.");
      }
    }
    fetchProblem();
  }, [selectedSlug, language]);

  const fullLink = `${window.location.origin}/duel-room/${roomId}`;

  const handleCopyLink = async () => {
    try {
      await navigator.clipboard.writeText(fullLink);
      alert("Room link copied!");
    } catch (err) {
      console.error("Copy failed", err);
    }
  };

  async function handleRun() {
    if (!code.trim()) return;
    setLoading(true);
    setOutput("Running...");
    try {
      const res = await axios.post(`${API_URL}/submissions/run`, {
        code,
        language,
      });
      setOutput(res.data.output || "No output");
    } catch (err) {
      console.error("Run error", err);
      setOutput(
        err.response?.data?.message || "Error while running your code."
      );
    } finally {
      setLoading(false);
    }
  }

  async function handleSubmit() {
    if (!problem) {
      setOutput("Select a problem before submitting.");
      return;
    }
    if (!token) {
      setOutput("You must be logged in to submit.");
      return;
    }
    setLoading(true);
    setOutput("Submitting...");
    try {
      const res = await axios.post(
        `${API_URL}/submissions`,
        {
          code,
          language,
          problemId: problem._id,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const status = res.data.status || "Submitted";
      setLastStatus(status);
      setLastSubmittedAt(new Date().toISOString());

      setOutput(`Result: ${status}`);
    } catch (err) {
      console.error("Submit error", err);
      setLastStatus("Error");
      setOutput(
        err.response?.data?.message || "Error while submitting your code."
      );
    } finally {
      setLoading(false);
    }
  }

  const statusClass = (() => {
    switch (lastStatus) {
      case "Accepted":
        return "battle-status-pill status-accepted";
      case "Rejected":
        return "battle-status-pill status-rejected";
      case "Error":
        return "battle-status-pill status-error";
      default:
        return "battle-status-pill status-pending";
    }
  })();

  if (!user) return null;

  return (
    <div className="page-container">
      {/* Room info + link + battle status */}
      <div className="page-card battle-header-card">
        <div className="battle-header-top">
          <div>
            <h1 className="page-heading">1v1 Room: {roomId}</h1>
            <p className="page-subtitle">
              Share this link with your opponent. Choose the same problem and
              race to get <b>Accepted</b> first.
            </p>
          </div>

          <div className="battle-status-box">
            <span className="battle-status-label">Your Status</span>
            <span className={statusClass}>{lastStatus}</span>
            {lastSubmittedAt && (
              <span className="battle-status-time">
                Last submit:{" "}
                {new Date(lastSubmittedAt).toLocaleTimeString([], {
                  hour: "2-digit",
                  minute: "2-digit",
                  second: "2-digit",
                })}
              </span>
            )}
          </div>
        </div>

        <div className="battle-link-row">
          <input
            type="text"
            value={fullLink}
            readOnly
            className="input-control"
          />
          <button onClick={handleCopyLink} className="btn btn-secondary">
            Copy Room Link
          </button>
        </div>
      </div>

      {/* Battle layout: problem left, editor right */}
      <div className="problem-layout">
        {/* LEFT: problem selection + description */}
        <div className="problem-left">
          <div style={{ marginBottom: "0.8rem" }}>
            <label className="auth-label">Select Problem</label>
            {loadingProblems ? (
              <p className="page-muted">Loading problems...</p>
            ) : (
              <select
                className="select-control"
                value={selectedSlug}
                onChange={(e) => setSelectedSlug(e.target.value)}
                style={{ width: "100%", marginTop: "0.3rem" }}
              >
                <option value="">-- Choose a problem --</option>
                {problems.map((p) => (
                  <option key={p._id} value={p.slug}>
                    {p.title} ({p.difficulty})
                  </option>
                ))}
              </select>
            )}
          </div>

          {error && <p className="page-error">{error}</p>}

          {problem ? (
            <>
              <h1 className="problem-title">{problem.title}</h1>
              <p className="problem-difficulty pill">
                {problem.difficulty || ""}
              </p>

              <div className="problem-description">
                <h2>Description</h2>
                <p>{problem.description}</p>

                {problem.exampleTests && problem.exampleTests.length > 0 && (
                  <>
                    <h2>Example</h2>
                    <pre className="problem-example">
                      {problem.exampleTests[0].input && (
                        <>
                          <strong>Input:</strong>{" "}
                          {problem.exampleTests[0].input}
                          {"\n"}
                        </>
                      )}
                      {problem.exampleTests[0].output && (
                        <>
                          <strong>Output:</strong>{" "}
                          {problem.exampleTests[0].output}
                        </>
                      )}
                    </pre>
                  </>
                )}
              </div>
            </>
          ) : (
            <p className="page-muted">
              Choose a problem from the dropdown to start the duel.
            </p>
          )}
        </div>

        {/* RIGHT: editor + controls + output */}
        <div className="problem-right">
          <div className="problem-controls">
            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              className="select-control"
              style={{ maxWidth: 180 }}
            >
              <option value="javascript">JavaScript</option>
              <option value="python">Python</option>
              <option value="cpp">C++</option>
            </select>

            <button
              className="btn-run"
              onClick={handleRun}
              disabled={loading || !code.trim()}
            >
              {loading ? "Please wait..." : "Run"}
            </button>

            <button
              className="btn-submit"
              onClick={handleSubmit}
              disabled={loading || !code.trim()}
            >
              Submit
            </button>
          </div>

          <Editor
            height="55vh"
            theme="vs-dark"
            language={language}
            value={code}
            onChange={(val) => setCode(val ?? "")}
          />

          <div className="problem-output">
            <strong>Output:</strong>
            <pre>{output}</pre>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
