// client/src/pages/DuelRoomPage.js
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import api from "../utils/api";

const TARGET_POINTS = 3; // first to 3 solves wins
const DEFAULT_DURATION = 600; // 10 minutes

// Format seconds as mm:ss or hh:mm:ss
function formatSeconds(sec) {
  if (sec <= 0) return "00:00";
  const h = Math.floor(sec / 3600);
  const m = Math.floor((sec % 3600) / 60);
  const s = sec % 60;

  if (h > 0) {
    return `${h.toString().padStart(2, "0")}:${m
      .toString()
      .padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  }
  return `${m.toString().padStart(2, "0")}:${s
    .toString()
    .padStart(2, "0")}`;
}

// Compute styling for difficulty pill
function getDifficultyBadge(difficulty) {
  const d = (difficulty || "").toLowerCase();
  if (d === "easy")
    return {
      label: "EASY",
      bg: "rgba(22,163,74,0.16)",
      border: "#16a34a",
      color: "#4ade80",
    };
  if (d === "medium")
    return {
      label: "MEDIUM",
      bg: "rgba(245,158,11,0.16)",
      border: "#f59e0b",
      color: "#fbbf24",
    };
  if (d === "hard")
    return {
      label: "HARD",
      bg: "rgba(239,68,68,0.16)",
      border: "#ef4444",
      color: "#f87171",
    };
  return null;
}

function DuelRoomPage() {
  const { roomId } = useParams();
  const location = useLocation();
  const { user, token } = useAuth();

  // Optional opponent info and pre-selected problem
  const opponentName = location.state?.opponentName || "Opponent";
  const opponentId = location.state?.opponentId || null;
  const initialProblemId = location.state?.problemId || null;
  const initialProblemTitle = location.state?.problemTitle || null;
  const initialProblemDifficulty = location.state?.problemDifficulty || null;

  // Duel state
  const [timeLeft, setTimeLeft] = useState(DEFAULT_DURATION);
  const [isRunning, setIsRunning] = useState(false);
  const [isOver, setIsOver] = useState(false);
  const [myScore, setMyScore] = useState(0);
  const [opponentScore, setOpponentScore] = useState(0);

  // Result / rating
  const [winner, setWinner] = useState(null); // "A" | "B" | "draw"
  const [resultMsg, setResultMsg] = useState("");
  const [savingResult, setSavingResult] = useState(false);

  // Problem data
  const [problem, setProblem] = useState(null);
  const [problemLoading, setProblemLoading] = useState(false);
  const [problemError, setProblemError] = useState("");

  // Battle editor state
  const [language, setLanguage] = useState("python");
  const [code, setCode] = useState("");
  const [stdin, setStdin] = useState("");
  const [output, setOutput] = useState("");
  const [running, setRunning] = useState(false);
  const [runError, setRunError] = useState("");

  // ------------ Problem loading ------------

  useEffect(() => {
    const fetchProblem = async () => {
      try {
        setProblemLoading(true);
        setProblemError("");

        // If problemId was passed via navigation, fetch that
        if (initialProblemId) {
          const res = await api.get(`/problems/${initialProblemId}`);
          setProblem(res.data);
          return;
        }

        // Otherwise: fetch all problems, pick a random one
        const res = await api.get("/problems");
        const list = res.data || [];
        if (list.length === 0) {
          setProblemError("No problems available.");
          return;
        }
        const randomIndex = Math.floor(Math.random() * list.length);
        setProblem(list[randomIndex]);
      } catch (err) {
        console.error("Failed to load duel problem:", err);
        const msg =
          err?.response?.data?.message ||
          err?.response?.data?.error ||
          "Failed to load problem.";
        setProblemError(msg);
      } finally {
        setProblemLoading(false);
      }
    };

    fetchProblem();
  }, [initialProblemId]);

  const effectiveProblemId = problem?._id || initialProblemId || null;
  const effectiveProblemTitle = problem?.title || initialProblemTitle || "";
  const effectiveProblemDifficulty =
    problem?.difficulty || initialProblemDifficulty || "";

  const diff = getDifficultyBadge(effectiveProblemDifficulty);

  // ------------ Duel logic ------------

  const statusText = useMemo(() => {
    if (isOver) return "Duel finished";
    if (isRunning) return "Duel in progress";
    return "Waiting to start";
  }, [isRunning, isOver]);

  const resetDuelState = () => {
    setTimeLeft(DEFAULT_DURATION);
    setIsRunning(false);
    setIsOver(false);
    setMyScore(0);
    setOpponentScore(0);
    setWinner(null);
    setResultMsg("");
  };

  const handleStart = () => {
    if (isRunning) return;
    resetDuelState();
    setIsRunning(true);
  };

  const handleMySolve = () => {
    if (!isRunning || isOver) return;
    const next = myScore + 1;
    setMyScore(next);
    if (next >= TARGET_POINTS) {
      decideWinner("points", next, opponentScore);
    }
  };

  const handleOpponentSolve = () => {
    if (!isRunning || isOver) return;
    const next = opponentScore + 1;
    setOpponentScore(next);
    if (next >= TARGET_POINTS) {
      decideWinner("points", myScore, next);
    }
  };

  const decideWinner = async (
    reason = "time",
    myScoreFinal = myScore,
    opponentScoreFinal = opponentScore
  ) => {
    if (isOver) return;

    setIsOver(true);
    setIsRunning(false);

    let duelWinner;
    if (myScoreFinal > opponentScoreFinal) duelWinner = "A";
    else if (opponentScoreFinal > myScoreFinal) duelWinner = "B";
    else duelWinner = "draw";

    setWinner(duelWinner);

    const readableWinner =
      duelWinner === "A"
        ? "You"
        : duelWinner === "B"
        ? opponentName
        : "Draw";

    // Not logged in → local result only
    if (!user || !token) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner} (not logged in — result not saved).`
      );
      return;
    }

    // No opponent ID → cannot update ratings
    if (!opponentId) {
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner}. (Opponent ID missing — ratings not updated.)`
      );
      return;
    }

    try {
      setSavingResult(true);

      const body = {
        playerAId: user._id || user.id,
        playerBId: opponentId,
        winner: duelWinner, // "A" | "B" | "draw"
        roomId,
        problemId: effectiveProblemId || null,
        durationSeconds: DEFAULT_DURATION - timeLeft,
      };

      const res = await api.post("/duels/finish", body);

      const info = res.data?.ratings;
      if (info) {
        const me =
          duelWinner === "A"
            ? info.playerA
            : duelWinner === "B"
            ? info.playerB
            : info.playerA;

        setResultMsg(
          `Duel finished (${reason}). Winner: ${readableWinner}. Your rating: ${me.old} → ${me.new}.`
        );
      } else {
        setResultMsg(
          `Duel finished (${reason}). Winner: ${readableWinner}.`
        );
      }
    } catch (err) {
      console.error("duel finish error:", err.response?.data || err.message);
      setResultMsg(
        `Duel finished (${reason}). Winner: ${readableWinner}. (Error saving result.)`
      );
    } finally {
      setSavingResult(false);
    }
  };

  // Timer effect
  useEffect(() => {
    if (!isRunning || isOver) return;

    if (timeLeft <= 0) {
      decideWinner("time");
      return;
    }

    const id = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isRunning, timeLeft, isOver]);

  // ------------ Battle editor logic ------------

  const handleRunCode = async () => {
    if (!code.trim()) return;

    try {
      setRunning(true);
      setRunError("");
      setOutput("");

      // NOTE: adjust path/body if your judge route differs
      const res = await api.post("/submissions/run", {
        code,
        language,
        input: stdin,
        problemId: effectiveProblemId || null,
        duelRoomId: roomId,
      });

      const data = res.data || {};
      setOutput(data.output || data.stdout || JSON.stringify(data, null, 2));
    } catch (err) {
      console.error("run code error:", err.response?.data || err.message);
      setRunError(
        err?.response?.data?.error ||
          err?.response?.data?.message ||
          "Failed to run code."
      );
    } finally {
      setRunning(false);
    }
  };

  // ------------ Render ------------

  return (
    <div className="page-container">
      <div
        className="page-card"
        style={{
          borderRadius: 28,
          padding: 26,
          border: "1px solid #111827",
          background:
            "radial-gradient(circle at top, rgba(168,85,247,0.16), transparent 60%), #050509",
          boxShadow: "0 28px 90px rgba(0,0,0,0.95)",
        }}
      >
        {/* Header */}
        <div
          style={{
            marginBottom: 18,
            display: "flex",
            flexDirection: "column",
            gap: 6,
          }}
        >
          <h1 className="page-heading" style={{ marginBottom: 0 }}>
            1v1 Duel Room
          </h1>
          <p className="page-subtitle" style={{ marginBottom: 0 }}>
            Room ID: <strong>{roomId}</strong> — share this with your opponent.
            First to <strong>{TARGET_POINTS}</strong> solves wins, or whoever
            has more points when the timer expires.
          </p>
        </div>

        {/* Scoreboard + Timer */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(3, minmax(0, 1fr))",
            gap: 18,
            marginBottom: 24,
          }}
        >
          {/* You */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(34,197,94,0.7)",
              background:
                "radial-gradient(circle at top, rgba(22,163,74,0.25), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#bbf7d0",
              }}
            >
              You
            </h3>
            <p
              style={{
                fontSize: 32,
                fontWeight: 800,
                margin: "4px 0 10px",
              }}
            >
              {myScore} / {TARGET_POINTS}
            </p>
            <button
              className="btn btn-secondary"
              onClick={handleMySolve}
              disabled={!isRunning || isOver}
            >
              I solved one
            </button>
          </div>

          {/* Timer */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(59,130,246,0.7)",
              background:
                "radial-gradient(circle at top, rgba(59,130,246,0.25), transparent 60%), #020617",
              textAlign: "center",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <div
              style={{
                fontSize: 13,
                color: "#cbd5f5",
                marginBottom: 4,
              }}
            >
              {statusText}
            </div>
            <div
              style={{
                fontSize: 32,
                fontWeight: 700,
                letterSpacing: 2,
                marginBottom: 10,
              }}
            >
              {formatSeconds(timeLeft)}
            </div>
            <button
              className="btn btn-primary"
              onClick={handleStart}
              disabled={isRunning}
            >
              {isRunning ? "Running…" : "Start Duel"}
            </button>
            <button
              className="btn btn-ghost-sm"
              onClick={() => decideWinner("manual")}
              disabled={isOver || !isRunning}
              style={{ marginTop: 8, fontSize: 12 }}
            >
              End now & decide winner
            </button>
          </div>

          {/* Opponent */}
          <div
            style={{
              padding: 18,
              borderRadius: 22,
              border: "1px solid rgba(248,250,252,0.12)",
              background:
                "radial-gradient(circle at top, rgba(248,250,252,0.1), transparent 60%), #020617",
              boxShadow: "0 18px 45px rgba(0,0,0,0.9)",
            }}
          >
            <h3
              style={{
                fontSize: 16,
                fontWeight: 600,
                marginBottom: 4,
                color: "#e5e7eb",
              }}
            >
              {opponentName}
            </h3>
            <p
              style={{
                fontSize: 32,
                fontWeight: 800,
                margin: "4px 0 10px",
              }}
            >
              {opponentScore} / {TARGET_POINTS}
            </p>
            <button
              className="btn btn-secondary"
              onClick={handleOpponentSolve}
              disabled={!isRunning || isOver}
            >
              Opponent solved
            </button>
          </div>
        </div>

        {/* Result banner */}
        {resultMsg && (
          <div
            style={{
              marginBottom: 18,
              padding: 10,
              borderRadius: 12,
              background: "rgba(15,23,42,0.9)",
              border: "1px solid rgba(148,163,184,0.5)",
              fontSize: "0.9rem",
            }}
          >
            {savingResult ? "Saving result… " : null}
            {resultMsg}
          </div>
        )}

        {/* Rating info */}
        {!user && (
          <p className="page-muted" style={{ marginBottom: 8 }}>
            You are not logged in, so duel results will not update ratings.
          </p>
        )}
        {!opponentId && (
          <p className="page-muted" style={{ marginBottom: 16, fontSize: 12 }}>
            To enable rating updates for both players, navigate here with{" "}
            <code>opponentId</code> and <code>opponentName</code> set in{" "}
            <code>location.state</code>.
          </p>
        )}

        {/* ---------------- Problem Panel ---------------- */}
        <div
          style={{
            marginBottom: 16,
            paddingTop: 10,
            borderTop: "1px solid rgba(31,41,55,0.9)",
          }}
        >
          <h2
            style={{
              fontSize: 18,
              fontWeight: 600,
              marginBottom: 6,
            }}
          >
            Battle Problem
          </h2>

          {problemLoading && (
            <p style={{ fontSize: 13, color: "#9ca3af" }}>Loading problem…</p>
          )}

          {problemError && (
            <p style={{ fontSize: 13, color: "#fca5a5" }}>{problemError}</p>
          )}

          {!problemLoading && !problemError && effectiveProblemTitle && (
            <div
              style={{
                padding: 14,
                borderRadius: 18,
                background: "rgba(15,23,42,0.96)",
                border: "1px solid #111827",
                display: "flex",
                justifyContent: "space-between",
                gap: 12,
                alignItems: "center",
                flexWrap: "wrap",
              }}
            >
              <div style={{ minWidth: 0 }}>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: 8,
                    marginBottom: 4,
                    flexWrap: "wrap",
                  }}
                >
                  <h3
                    style={{
                      fontSize: 16,
                      fontWeight: 600,
                      margin: 0,
                    }}
                  >
                    {effectiveProblemTitle}
                  </h3>
                  {diff && (
                    <span
                      style={{
                        padding: "2px 8px",
                        borderRadius: 999,
                        background: diff.bg,
                        border: `1px solid ${diff.border}`,
                        color: diff.color,
                        fontSize: 11,
                        textTransform: "uppercase",
                        letterSpacing: 0.08,
                      }}
                    >
                      {diff.label}
                    </span>
                  )}
                </div>

                {problem?.description && (
                  <p
                    style={{
                      fontSize: 12,
                      color: "#9ca3af",
                      marginTop: 2,
                      maxHeight: 60,
                      overflow: "hidden",
                      textOverflow: "ellipsis",
                    }}
                  >
                    {problem.description.replace(/<[^>]+>/g, "")}
                  </p>
                )}
              </div>

              {effectiveProblemId && (
                <button
                  type="button"
                  className="btn btn-ghost-sm"
                  onClick={() =>
                    window.open(`/problems/${effectiveProblemId}`, "_blank")
                  }
                  style={{ fontSize: 12 }}
                >
                  Open full problem
                </button>
              )}
            </div>
          )}
        </div>

        {/* ---------------- Battle Editor ---------------- */}
        <div
          style={{
            paddingTop: 10,
            borderTop: "1px solid rgba(31,41,55,0.9)",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: 10,
              alignItems: "center",
              gap: 12,
              flexWrap: "wrap",
            }}
          >
            <div>
              <h2
                style={{
                  fontSize: 18,
                  fontWeight: 600,
                  marginBottom: 2,
                }}
              >
                Battle Editor
              </h2>
              <p
                style={{
                  fontSize: 13,
                  color: "#9ca3af",
                }}
              >
                Write and test your code here while the timer runs. Use{" "}
                <strong>I solved one</strong> when you get an accepted
                solution.
              </p>
            </div>

            <div
              style={{
                display: "flex",
                gap: 8,
                alignItems: "center",
                flexWrap: "wrap",
              }}
            >
              <label
                style={{
                  fontSize: 12,
                  color: "#9ca3af",
                }}
              >
                Language
              </label>
              <select
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
                style={{
                  padding: "6px 10px",
                  borderRadius: 999,
                  border: "1px solid #1f2937",
                  background: "#020617",
                  color: "#e5e7eb",
                  fontSize: 13,
                  outline: "none",
                }}
              >
                <option value="cpp">C++</option>
                <option value="c">C</option>
                <option value="java">Java</option>
                <option value="python">Python</option>
                <option value="javascript">JavaScript</option>
              </select>
            </div>
          </div>

          <div
            style={{
              display: "grid",
              gridTemplateColumns: "minmax(0, 2fr) minmax(0, 1fr)",
              gap: 16,
            }}
          >
            {/* Code + input */}
            <div
              style={{
                padding: 14,
                borderRadius: 18,
                background: "rgba(15,23,42,0.96)",
                border: "1px solid #111827",
              }}
            >
              <label
                style={{
                  fontSize: 12,
                  color: "#9ca3af",
                  display: "block",
                  marginBottom: 4,
                }}
              >
                Code
              </label>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                rows={14}
                spellCheck={false}
                style={{
                  width: "100%",
                  borderRadius: 10,
                  border: "1px solid #1f2937",
                  background: "#020617",
                  color: "#e5e7eb",
                  fontFamily:
                    "JetBrains Mono, SFMono-Regular, Menlo, Monaco, Consolas, monospace",
                  fontSize: 13,
                  padding: 10,
                  resize: "vertical",
                  minHeight: 260,
                }}
              />

              <div
                style={{
                  marginTop: 10,
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  gap: 10,
                  flexWrap: "wrap",
                }}
              >
                <div style={{ flex: 1, minWidth: 160 }}>
                  <label
                    style={{
                      fontSize: 12,
                      color: "#9ca3af",
                      display: "block",
                      marginBottom: 4,
                    }}
                  >
                    Custom input
                  </label>
                  <textarea
                    value={stdin}
                    onChange={(e) => setStdin(e.target.value)}
                    rows={3}
                    style={{
                      width: "100%",
                      borderRadius: 8,
                      border: "1px solid #1f2937",
                      background: "#020617",
                      color: "#e5e7eb",
                      fontFamily:
                        "JetBrains Mono, SFMono-Regular, Menlo, Monaco, Consolas, monospace",
                      fontSize: 12,
                      padding: 8,
                      resize: "vertical",
                    }}
                  />
                </div>

                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={handleRunCode}
                  disabled={running || !code.trim()}
                  style={{ alignSelf: "flex-end" }}
                >
                  {running ? "Running…" : "Run Code"}
                </button>
              </div>
            </div>

            {/* Output */}
            <div
              style={{
                padding: 14,
                borderRadius: 18,
                background: "rgba(15,23,42,0.96)",
                border: "1px solid #111827",
              }}
            >
              <label
                style={{
                  fontSize: 12,
                  color: "#9ca3af",
                  display: "block",
                  marginBottom: 4,
                }}
              >
                Output
              </label>
              <div
                style={{
                  height: 260,
                  borderRadius: 10,
                  border: "1px solid #1f2937",
                  background: "#020617",
                  color: "#e5e7eb",
                  fontFamily:
                    "JetBrains Mono, SFMono-Regular, Menlo, Monaco, Consolas, monospace",
                  fontSize: 12,
                  padding: 10,
                  overflow: "auto",
                  whiteSpace: "pre-wrap",
                }}
              >
                {runError ? (
                  <span style={{ color: "#fca5a5" }}>{runError}</span>
                ) : output ? (
                  output
                ) : (
                  <span style={{ color: "#6b7280" }}>
                    Run your code to see the output here.
                  </span>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DuelRoomPage;
