// client/src/pages/DuelRoomPage.js
import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import { io as socketIO } from "socket.io-client";
import api from "../utils/api";
import CodeEditor from "../components/CodeEditor"; // whatever your editor is
import { LANGUAGE_SNIPPETS } from "../constants/languages"; // your existing map

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:5000";

const DuelRoomPage = () => {
  const { roomId } = useParams();
  const [socket, setSocket] = useState(null);

  const [players, setPlayers] = useState([]);
  const [myId, setMyId] = useState(null);
  const [problem, setProblem] = useState(null);
  const [duelStarted, setDuelStarted] = useState(false);
  const [duelEnded, setDuelEnded] = useState(false);

  const [language, setLanguage] = useState("python");
  const [code, setCode] = useState(LANGUAGE_SNIPPETS.python || "");
  const [stdin, setStdin] = useState("");
  const [runOutput, setRunOutput] = useState("");
  const [runStatus, setRunStatus] = useState("");

  const [isRunningCode, setIsRunningCode] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [connError, setConnError] = useState("");
  const [connecting, setConnecting] = useState(true);

  // ---------- derived ----------
  const isHost = useMemo(
    () => players.length > 0 && players[0].id === myId,
    [players, myId]
  );

  const me = useMemo(
    () => players.find((p) => (p.id || p.userId) === myId),
    [players, myId]
  );

  const opponent = useMemo(
    () => players.find((p) => (p.id || p.userId) !== myId),
    [players, myId]
  );

  const statusText = useMemo(() => {
    if (connError) return "Error";
    if (connecting) return "Connecting…";
    if (!duelStarted) return "Waiting for players / start";
    if (duelEnded) return "Duel finished";
    return "Duel in progress";
  }, [connError, connecting, duelStarted, duelEnded]);

  const editorReadOnly = duelEnded || !!connError || connecting;
  const allowSubmit = duelStarted && !duelEnded && !connError;

  const examples =
    problem?.examples || problem?.exampleTests || [];
  const hasIO =
    problem?.inputFormat ||
    problem?.outputFormat ||
    problem?.constraints;

  // ---------- socket setup ----------
  useEffect(() => {
    const s = socketIO(SOCKET_URL, {
      withCredentials: true,
    });

    setSocket(s);
    setConnecting(true);

    const myUserId = localStorage.getItem("userId") || "me";
    setMyId(myUserId);

    s.on("connect", () => {
      setConnecting(false);
      setConnError("");

      s.emit("join_duel", { duelId: roomId, userId: myUserId });

      // optional: you can re-join or sync state here
    });

    s.on("connect_error", (err) => {
      setConnError(err.message || "Socket connection error");
      setConnecting(false);
    });

    s.on("duel_error", (payload) => {
      setConnError(payload?.message || "Duel error");
    });

    s.on("room_update", ({ players, started }) => {
      setPlayers(players || []);
      setDuelStarted(!!started);
    });

    s.on("duel_started", ({ problem }) => {
      setProblem(problem);
      setDuelStarted(true);
      setDuelEnded(false);
      setRunStatus("");
      setRunOutput("");
      setStdin("");
      setCode(LANGUAGE_SNIPPETS[language] || "");
    });

    s.on("duel_submission_result", (payload) => {
      // This fires for both players after a submission
      if (payload.accepted) {
        setRunStatus("✅ Accepted – All duel tests passed");
      } else {
        const first = (payload.details || [])[0];
        const desc =
          first?.status?.description ||
          (payload.accepted ? "Accepted" : "Runtime Error (NZEC)");
        setRunStatus(`⚠️ Not accepted – ${desc}`);
      }
    });

    s.on("duel_finished", () => {
      setDuelEnded(true);
    });

    return () => {
      s.disconnect();
    };
  }, [roomId, language]);

  // ---------- actions ----------
  const handleStart = () => {
    if (!socket) return;
    if ((players?.length || 0) < 2) {
      setConnError("Need at least 2 players to start the duel");
      return;
    }
    if (!isHost) {
      setConnError("Only the host can start the duel");
      return;
    }
    socket.emit("start_duel", { duelId: roomId }, (resp) => {
      if (!resp?.ok) {
        setConnError(resp?.message || "Failed to start duel");
      }
    });
  };

  const handleUseExampleInput = (input) => {
    if (!input) return;
    setStdin(input);
    const el = document.getElementById("duel-editor-section");
    if (el) el.scrollIntoView({ behavior: "smooth", block: "start" });
  };

  const handleLanguageChange = (nextLang) => {
    setLanguage(nextLang);
    setCode(LANGUAGE_SNIPPETS[nextLang] || "");
  };

  // ---------- RUN CODE (same logic as Problems page, with example fallback) ----------
  const handleRunCode = async () => {
    const source = code ?? "";
    if (!source.trim()) {
      setRunOutput("Write some code before running.");
      return;
    }

    const exs = examples || [];
    const fallbackInput =
      exs[0]?.input || exs[0]?.inputText || "";

    const effectiveInput =
      stdin && stdin.trim().length > 0 ? stdin : fallbackInput;

    const expectsInput =
      source.includes("input(") || // Python
      source.includes("fs.readFileSync(0") || // Node
      source.includes("readLine()"); // others

    if (!effectiveInput && expectsInput) {
      setRunOutput(
        "Your code expects input. Either type it in the Custom Input box or click 'Use as Input' on an example."
      );
      return;
    }

    setIsRunningCode(true);
    setRunOutput("Running via Judge0…");
    try {
      const resp = await api.post("/judge/run", {
        code: source,
        language,
        input: effectiveInput,
      });
      const data = resp.data || {};
      const output =
        data.output ||
        data.stdout ||
        data?.raw?.stdout ||
        data?.raw?.outputFallback ||
        "Program finished with no output.";
      setRunOutput(output);
    } catch (err) {
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        err.message ||
        "Error running code.";
      setRunOutput(`Error: ${msg}`);
    } finally {
      setIsRunningCode(false);
    }
  };

  // ---------- SUBMIT TO DUEL ----------
  const handleSubmit = () => {
    if (!socket || !duelStarted || duelEnded) return;

    const source = code ?? "";
    if (!source.trim()) {
      setRunStatus("Write some code before submitting.");
      return;
    }

    setIsSubmitting(true);
    setRunStatus("");

    socket.emit(
      "duel_submit_code",
      {
        duelId: roomId,
        userId: myId,
        code: source,
        languageId: language, // string like "python" – same as Problems page
        stdin,
      },
      (resp) => {
        setIsSubmitting(false);

        if (!resp) {
          setRunStatus("No response from server");
          return;
        }
        if (!resp.ok) {
          setRunStatus(
            `Submit failed: ${resp.message || "Server error while submitting"}`
          );
          return;
        }

        const accepted = !!resp.accepted;
        const statusDesc =
          resp?.judge?.status?.description ||
          resp?.judge?.result ||
          (accepted ? "Accepted" : "Not accepted");

        setRunStatus(
          accepted
            ? `✅ Accepted – ${statusDesc}`
            : `⚠️ Not accepted – ${statusDesc}`
        );
      }
    );
  };

  // ---------- render ----------
  return (
    <div className="page-container">
      <div
        className="page-card"
        style={{
          borderRadius: 28,
          padding: 26,
          border: "1px solid #111827",
          background:
            "radial-gradient(circle at top, rgba(168,85,247,0.16), transparent 60%), #050509",
          boxShadow: "0 28px 90px rgba(0,0,0,0.95)",
          maxWidth: "min(1400px, 95vw)",
          margin: "0 auto",
        }}
      >
        {/* Header */}
        <div
          style={{
            marginBottom: 18,
            display: "flex",
            flexDirection: "column",
            gap: 6,
          }}
        >
          <h1 className="page-heading" style={{ marginBottom: 0 }}>
            1v1 Duel Room
          </h1>
          <p className="page-subtitle" style={{ marginBottom: 0 }}>
            Room ID: <strong>{roomId}</strong> — share this with your opponent.
            First to solve wins, or server decides when the timer expires.
          </p>
          <p style={{ fontSize: 12, margin: 0, opacity: 0.8 }}>
            Status: {statusText}
          </p>
        </div>

        {connError && (
          <div
            style={{
              marginBottom: 14,
              padding: 10,
              borderRadius: 12,
              background: "rgba(127,29,29,0.9)",
              border: "1px solid rgba(248,113,113,0.7)",
              fontSize: 13,
              color: "#fee2e2",
            }}
          >
            {connError}
          </div>
        )}

        {/* Layout: left = problem, right = editor */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "minmax(0, 1.1fr) minmax(0, 1.3fr)",
            gap: 20,
            alignItems: "stretch",
          }}
        >
          {/* Problem panel */}
          <div
            style={{
              borderRadius: 22,
              background:
                "radial-gradient(circle at top left, rgba(55,65,81,0.6), #020617)",
              padding: 18,
              border: "1px solid rgba(31,41,55,0.9)",
              minHeight: 360,
            }}
          >
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 10,
              }}
            >
              <div>
                <h2 style={{ margin: 0, fontSize: 20 }}>
                  {problem?.title || "Waiting for problem…"}
                </h2>
                {problem?.difficulty && (
                  <p style={{ margin: 0, fontSize: 13, opacity: 0.8 }}>
                    Difficulty: {problem.difficulty}
                  </p>
                )}
              </div>
              <button
                onClick={handleStart}
                disabled={!isHost || duelStarted || players.length < 2}
                style={{
                  padding: "6px 14px",
                  borderRadius: 999,
                  border: "none",
                  fontSize: 13,
                  fontWeight: 600,
                  cursor:
                    !isHost || duelStarted || players.length < 2
                      ? "not-allowed"
                      : "pointer",
                  opacity:
                    !isHost || duelStarted || players.length < 2 ? 0.4 : 1,
                  background:
                    "linear-gradient(to right, #22c55e, #16a34a, #22c55e)",
                  color: "#020617",
                }}
              >
                {duelStarted ? "Duel in progress" : "Start Duel"}
              </button>
            </div>

            <div
              style={{
                height: 1,
                background:
                  "linear-gradient(to right, transparent, rgba(148,163,184,0.7), transparent)",
                margin: "10px 0 14px",
              }}
            />

            <div
              style={{
                maxHeight: 420,
                overflowY: "auto",
                paddingRight: 4,
                fontSize: 14,
              }}
            >
              {problem ? (
                <>
                  <p style={{ whiteSpace: "pre-wrap", lineHeight: 1.6 }}>
                    {problem.description}
                  </p>

                  {hasIO && (
                    <div style={{ marginTop: 10 }}>
                      {problem.inputFormat && (
                        <>
                          <h4 style={{ marginBottom: 4, marginTop: 10 }}>
                            Input Format
                          </h4>
                          <pre className="problem-pre">
                            {problem.inputFormat}
                          </pre>
                        </>
                      )}
                      {problem.outputFormat && (
                        <>
                          <h4 style={{ marginBottom: 4, marginTop: 10 }}>
                            Output Format
                          </h4>
                          <pre className="problem-pre">
                            {problem.outputFormat}
                          </pre>
                        </>
                      )}
                      {problem.constraints && (
                        <>
                          <h4 style={{ marginBottom: 4, marginTop: 10 }}>
                            Constraints
                          </h4>
                          <pre className="problem-pre">
                            {problem.constraints}
                          </pre>
                        </>
                      )}
                    </div>
                  )}

                  {examples?.length > 0 && (
                    <div style={{ marginTop: 14 }}>
                      <h4 style={{ marginBottom: 8 }}>Examples</h4>
                      <div
                        style={{
                          display: "flex",
                          flexDirection: "column",
                          gap: 10,
                        }}
                      >
                        {examples.map((ex, idx) => (
                          <div
                            key={idx}
                            style={{
                              borderRadius: 14,
                              border: "1px solid rgba(31,41,55,0.9)",
                              background: "rgba(15,23,42,0.9)",
                              padding: 10,
                            }}
                          >
                            <div
                              style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                marginBottom: 4,
                              }}
                            >
                              <strong style={{ fontSize: 13 }}>
                                Example {idx + 1}
                              </strong>
                              <button
                                onClick={() =>
                                  handleUseExampleInput(ex.input || ex.inputText)
                                }
                                style={{
                                  fontSize: 11,
                                  padding: "3px 9px",
                                  borderRadius: 999,
                                  border: "none",
                                  cursor: "pointer",
                                  background:
                                    "linear-gradient(to right, #38bdf8, #0ea5e9)",
                                  color: "#020617",
                                }}
                              >
                                Use as Input
                              </button>
                            </div>
                            <div style={{ fontSize: 12 }}>
                              <div style={{ marginBottom: 4 }}>
                                <span style={{ opacity: 0.7 }}>Input:</span>
                                <pre className="problem-pre">
                                  {ex.input || ex.inputText}
                                </pre>
                              </div>
                              <div>
                                <span style={{ opacity: 0.7 }}>Output:</span>
                                <pre className="problem-pre">
                                  {ex.output || ex.outputText}
                                </pre>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </>
              ) : (
                <p style={{ opacity: 0.8 }}>
                  Waiting for the host to start the duel and pick a problem…
                </p>
              )}
            </div>
          </div>

          {/* Editor panel */}
          <div
            id="duel-editor-section"
            style={{
              borderRadius: 22,
              background:
                "radial-gradient(circle at top right, rgba(30,64,175,0.7), #020617)",
              padding: 18,
              border: "1px solid rgba(31,41,55,0.9)",
            }}
          >
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                marginBottom: 10,
                gap: 10,
              }}
            >
              <div>
                <h3 style={{ margin: 0, fontSize: 16 }}>Your Code</h3>
                <p style={{ margin: 0, fontSize: 12, opacity: 0.8 }}>
                  Use the examples on the left and the custom input below to
                  test your solution, then submit to the duel. First accepted
                  solution wins.
                </p>
              </div>

              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: 8,
                }}
              >
                {/* Language select */}
                <select
                  value={language}
                  onChange={(e) => handleLanguageChange(e.target.value)}
                  style={{
                    fontSize: 13,
                    borderRadius: 999,
                    padding: "4px 10px",
                    background: "#020617",
                    color: "#e5e7eb",
                    border: "1px solid rgba(75,85,99,0.9)",
                  }}
                  disabled={editorReadOnly}
                >
                  <option value="python">Python</option>
                  <option value="javascript">JavaScript</option>
                  <option value="cpp">C++</option>
                </select>

                <button
                  onClick={handleRunCode}
                  disabled={editorReadOnly || isRunningCode}
                  style={{
                    padding: "6px 12px",
                    borderRadius: 999,
                    border: "none",
                    fontSize: 13,
                    fontWeight: 600,
                    cursor:
                      editorReadOnly || isRunningCode ? "not-allowed" : "pointer",
                    opacity: editorReadOnly || isRunningCode ? 0.5 : 1,
                    background:
                      "linear-gradient(to right, #22c55e, #16a34a, #22c55e)",
                    color: "#020617",
                  }}
                >
                  {isRunningCode ? "Running…" : "Run Code"}
                </button>

                <button
                  onClick={handleSubmit}
                  disabled={!allowSubmit || isSubmitting}
                  style={{
                    padding: "6px 14px",
                    borderRadius: 999,
                    border: "none",
                    fontSize: 13,
                    fontWeight: 600,
                    cursor:
                      !allowSubmit || isSubmitting ? "not-allowed" : "pointer",
                    opacity: !allowSubmit || isSubmitting ? 0.5 : 1,
                    background:
                      "linear-gradient(to right, #a855f7, #7c3aed, #a855f7)",
                    color: "#f9fafb",
                  }}
                >
                  {isSubmitting ? "Submitting…" : "Submit to Duel"}
                </button>
              </div>
            </div>

            {/* Code editor */}
            <div style={{ borderRadius: 16, overflow: "hidden", marginBottom: 10 }}>
              <CodeEditor
                value={code}
                language={language}
                onChange={setCode}
                readOnly={editorReadOnly}
              />
            </div>

            {/* Output + stdin */}
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "minmax(0, 1.2fr) minmax(0, 0.9fr)",
                gap: 12,
                marginTop: 8,
                alignItems: "stretch",
              }}
            >
              {/* Output */}
              <div
                style={{
                  borderRadius: 14,
                  background: "rgba(15,23,42,0.95)",
                  border: "1px solid rgba(30,64,175,0.7)",
                  padding: 10,
                  fontSize: 13,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    marginBottom: 6,
                  }}
                >
                  <span style={{ opacity: 0.8 }}>Output</span>
                  <span style={{ fontSize: 11, opacity: 0.7 }}>
                    {runStatus || "Run your code or submit to the duel."}
                  </span>
                </div>
                <pre
                  style={{
                    margin: 0,
                    whiteSpace: "pre-wrap",
                    overflowWrap: "anywhere",
                  }}
                >
                  {runOutput || "Run your code to see output here."}
                </pre>
              </div>

              {/* Custom input */}
              <div
                style={{
                  borderRadius: 14,
                  background: "rgba(15,23,42,0.95)",
                  border: "1px solid rgba(30,64,175,0.7)",
                  padding: 10,
                  fontSize: 13,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    marginBottom: 6,
                  }}
                >
                  <span style={{ opacity: 0.8 }}>Custom Input (stdin)</span>
                  <span style={{ fontSize: 11, opacity: 0.7 }}>
                    Optional – used for Run Code, duel uses hidden tests.
                  </span>
                </div>
                <textarea
                  value={stdin}
                  onChange={(e) => setStdin(e.target.value)}
                  rows={6}
                  style={{
                    width: "100%",
                    resize: "vertical",
                    borderRadius: 10,
                    border: "1px solid rgba(55,65,81,0.9)",
                    background: "#020617",
                    color: "#e5e7eb",
                    fontSize: 13,
                    padding: 8,
                    fontFamily: "ui-monospace, SFMono-Regular, Menlo, Monaco",
                  }}
                  placeholder="Optional custom input"
                  disabled={editorReadOnly}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DuelRoomPage;
