import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import Editor from "@monaco-editor/react";

const LANGUAGE_OPTIONS = [
  {
    label: "JavaScript (Node.js)",
    value: "javascript",
    judgeId: 63,
    template: `// JavaScript solution\n\nfunction solve() {\n  console.log("Hello from JavaScript");\n}\n\nsolve();`,
  },
  {
    label: "Python (3.x)",
    value: "python",
    judgeId: 71,
    template: `# Python solution\n\ndef solve():\n    print("Hello from Python")\n\nif __name__ == "__main__":\n    solve()`,
  },
  {
    label: "C++ (GCC)",
    value: "cpp",
    judgeId: 54,
    template: `// C++ solution\n#include <bits/stdc++.h>\nusing namespace std;\n\nint main() {\n    ios::sync_with_stdio(false);\n    cin.tie(nullptr);\n\n    cout << "Hello from C++\\n";\n    return 0;\n}\n`,
  },
];

function ProblemPage() {
  const { slug, contestId } = useParams(); // contestId is optional
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState(LANGUAGE_OPTIONS[0].template);
  const [message, setMessage] = useState("");
  const [runResult, setRunResult] = useState(null);
  const [running, setRunning] = useState(false);
  const [language, setLanguage] = useState(LANGUAGE_OPTIONS[0]);
  const { user, token } = useAuth();

  useEffect(() => {
    fetch(`http://localhost:5000/problems/${slug}`)
      .then((res) => res.json())
      .then((data) => setProblem(data))
      .catch((err) => console.error(err));
  }, [slug]);

  function handleLanguageChange(e) {
    const selected = LANGUAGE_OPTIONS.find((l) => l.value === e.target.value);
    setLanguage(selected);
    setCode(selected.template);
    setRunResult(null);
    setMessage("");
  }

  async function handleSubmit() {
    setMessage("");
    setRunResult(null);

    if (!user) {
      setMessage("You must be logged in to submit.");
      return;
    }

    if (!code.trim()) {
      setMessage("Code cannot be empty.");
      return;
    }

    try {
      const body = {
        problemId: problem._id,
        code,
        language: language.value,
      };

      if (contestId) {
        body.contestId = contestId;
      }

      const res = await axios.post(
        "http://localhost:5000/submissions",
        body,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage(`Submission saved with status: ${res.data.status}`);
    } catch (err) {
      console.error(err);
      setMessage(
        err.response?.data?.message || "Error submitting solution."
      );
    }
  }

  async function handleRun() {
    setMessage("");
    setRunResult(null);

    if (!user) {
      setMessage("You must be logged in to run code.");
      return;
    }

    if (!code.trim()) {
      setMessage("Code cannot be empty.");
      return;
    }

    try {
      setRunning(true);

      const res = await axios.post(
        "http://localhost:5000/judge/run",
        {
          code,
          languageId: language.judgeId,
          stdin: "",
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setRunResult(res.data);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Error running code.");
    } finally {
      setRunning(false);
    }
  }

  if (!problem) return <p>Loading problem...</p>;

  return (
    <div>
      <h2>{problem.title}</h2>
      <p>
        <b>Difficulty:</b> {problem.difficulty}
      </p>
      {contestId && (
        <p style={{ color: "purple" }}>
          Contest mode â€“ submissions will count towards this contest.
        </p>
      )}

      <h3>Description</h3>
      <p>{problem.description}</p>

      <h3>Examples</h3>
      {problem.exampleTests.map((ex, i) => (
        <pre key={i} style={{ background: "#eee", padding: 10 }}>
          <b>Input:</b> {ex.input}
          {"\n"}
          <b>Output:</b> {ex.output}
        </pre>
      ))}

      <h3>Your Code</h3>

      <div style={{ marginBottom: 10 }}>
        <label>
          <b>Language: </b>
          <select
            value={language.value}
            onChange={handleLanguageChange}
            style={{ marginLeft: 8, padding: 4 }}
          >
            {LANGUAGE_OPTIONS.map((lang) => (
              <option key={lang.value} value={lang.value}>
                {lang.label}
              </option>
            ))}
          </select>
        </label>
      </div>

      <div style={{ border: "1px solid #ccc", marginBottom: 10 }}>
        <Editor
          height="300px"
          language={language.value === "cpp" ? "cpp" : language.value}
          value={code}
          onChange={(value) => setCode(value || "")}
          options={{
            fontSize: 14,
            minimap: { enabled: false },
          }}
        />
      </div>

      <button
        style={{ marginTop: 10, padding: "10px 20px", fontSize: 16 }}
        onClick={handleRun}
        disabled={running}
      >
        {running ? "Running..." : "Run Code"}
      </button>

      <button
        style={{
          marginTop: 10,
          marginLeft: 10,
          padding: "10px 20px",
          fontSize: 16,
        }}
        onClick={handleSubmit}
      >
        Submit
      </button>

      {message && (
        <p style={{ marginTop: 10, color: "blue" }}>
          {message}
        </p>
      )}

      {runResult && (
        <div style={{ marginTop: 20 }}>
          <h3>Run Result</h3>
          <p>
            <b>Status:</b> {runResult.status?.description}
          </p>

          {runResult.stdout && (
            <>
              <h4>Output:</h4>
              <pre
                style={{
                  background: "#111",
                  color: "#0f0",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.stdout}
              </pre>
            </>
          )}

          {runResult.stderr && (
            <>
              <h4>Error:</h4>
              <pre
                style={{
                  background: "#200",
                  color: "#f55",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.stderr}
              </pre>
            </>
          )}

          {runResult.compile_output && (
            <>
              <h4>Compile Output:</h4>
              <pre
                style={{
                  background: "#222",
                  color: "#ff0",
                  padding: 10,
                  whiteSpace: "pre-wrap",
                }}
              >
                {runResult.compile_output}
              </pre>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default ProblemPage;
