// client/src/pages/DuelRoomPage.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";
import { useAuth } from "../context/AuthContext";

const socket = io("http://localhost:5000");

export default function DuelRoomPage() {
  const { roomId } = useParams();
  const { user } = useAuth();

  const [myScore, setMyScore] = useState(0);
  const [opponentScore, setOpponentScore] = useState(0);
  const [started, setStarted] = useState(false);
  const [winner, setWinner] = useState(null);
  const TARGET = 3;

  useEffect(() => {
    socket.emit("join_duel", roomId);

    socket.on("duel_started", () => setStarted(true));
    socket.on("score_updated", ({ player, score }) => {
      if (player === "A") setOpponentScore(score);
      if (player === "B") setMyScore(score);
    });
    socket.on("duel_finished", (winner) => setWinner(winner));
  }, [roomId]);

  const startDuel = () => socket.emit("start_duel", roomId);

  const updateScore = () => {
    const newScore = myScore + 1;
    setMyScore(newScore);
    socket.emit("score_update", { roomId, player: "B", score: newScore });

    if (newScore >= TARGET) {
      socket.emit("finish_duel", { roomId, winner: user?.username });
    }
  };

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">1v1 Duel Room</h1>
        <p>Room ID: {roomId}</p>

        <div style={{ display: "flex", gap: 24 }}>
          <div className="score-box">
            <h2>You</h2>
            <p>{myScore} / {TARGET}</p>
            {!started && <button onClick={startDuel}>Start Duel</button>}
            {started && <button onClick={updateScore}>Simulate Solve</button>}
          </div>

          <div className="score-box">
            <h2>Opponent</h2>
            <p>{opponentScore} / {TARGET}</p>
          </div>
        </div>

        {winner && (
          <h2 style={{ marginTop: 20, color: "#00ff88", fontSize: 28 }}>
            Winner: {winner}
          </h2>
        )}
      </div>
    </div>
  );
}
