// src/pages/MySubmissions.js
import React, { useEffect, useState } from "react";
import api from "../utils/api";

import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";


function MySubmissions() {
  const { token } = useAuth();
  const [subs, setSubs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errMsg, setErrMsg] = useState("");

  useEffect(() => {
    async function load() {
      if (!token) {
        setErrMsg("You must be logged in to view your submissions.");
        setLoading(false);
        return;
      }
      try {
        setLoading(true);
        setErrMsg("");

        const res = await api.get("/submissions/mine", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setSubs(res.data || []);
      } catch (err) {
        console.error("Load submissions error:", err);
        setErrMsg("Failed to load submissions.");
      } finally {
        setLoading(false);
      }
    }

    load();
  }, [token]);

  if (loading) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-muted">Loading submissions...</p>
        </div>
      </div>
    );
  }

  if (errMsg) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-error">{errMsg}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">My Submissions</h1>
        {subs.length === 0 ? (
          <p className="page-muted">No submissions yet.</p>
        ) : (
          <div className="submissions-table-wrapper">
            <table className="submissions-table">
              <thead>
                <tr>
                  <th>Problem</th>
                  <th>Difficulty</th>
                  <th>Status</th>
                  <th>Language</th>
                  <th>When</th>
                </tr>
              </thead>
              <tbody>
                {subs.map((s) => (
                  <tr key={s._id}>
                    <td>
                      {s.problem ? (
                        <Link
                          to={`/problems/${s.problem.slug}`}
                          className="link-muted"
                        >
                          {s.problem.title}
                        </Link>
                      ) : (
                        "Unknown"
                      )}
                    </td>
                    <td>{s.problem?.difficulty || "-"}</td>
                    <td>{s.status}</td>
                    <td>{s.language}</td>
                    <td>
                      {new Date(s.createdAt).toLocaleString(undefined, {
                        dateStyle: "short",
                        timeStyle: "short",
                      })}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default MySubmissions;
