// src/pages/ContestsList.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const API_URL = "http://localhost:5000";

function ContestsList() {
  const { user, token } = useAuth();
  const [contests, setContests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchContests() {
      try {
        setLoading(true);
        const res = await axios.get(`${API_URL}/contests`);
        setContests(res.data || []);
      } catch (err) {
        console.error("Error fetching contests:", err);
        setError("Failed to load contests.");
      } finally {
        setLoading(false);
      }
    }
    fetchContests();
  }, []);

  const getStatus = (c) => {
    const now = new Date();
    const start = new Date(c.startTime);
    const end = new Date(c.endTime);
    if (now < start) return "Upcoming";
    if (now > end) return "Finished";
    return "Running";
  };

  const statusClass = (status) => {
    switch (status) {
      case "Running":
        return "contest-status-pill status-running";
      case "Upcoming":
        return "contest-status-pill status-upcoming";
      case "Finished":
        return "contest-status-pill status-finished";
      default:
        return "contest-status-pill";
    }
  };

  return (
    <div className="page-container">
      <div className="page-card">
        <div className="page-header">
          <div>
            <h1 className="page-heading">Contests</h1>
            <p className="page-subtitle">
              Battle with other coders in timed contests and see your rank.
            </p>
          </div>
          {user && (
            <button
              className="btn btn-secondary"
              onClick={() => navigate("/contests/create")}
            >
              + Create Contest
            </button>
          )}
        </div>

        {error && <p className="page-error">{error}</p>}
        {loading ? (
          <p className="page-muted">Loading contests...</p>
        ) : contests.length === 0 ? (
          <p className="page-muted">No contests yet.</p>
        ) : (
          <div className="contests-grid">
            {contests.map((contest) => {
              const status = getStatus(contest);
              return (
                <Link
                  to={`/contests/${contest._id}`}
                  key={contest._id}
                  className="contest-card"
                >
                  <div className="contest-card-top">
                    <h3>{contest.name}</h3>
                    <span className={statusClass(status)}>{status}</span>
                  </div>
                  {contest.description && (
                    <p className="contest-description">
                      {contest.description}
                    </p>
                  )}
                  <div className="contest-meta">
                    <div>
                      <span className="contest-meta-label">Starts</span>
                      <span className="contest-meta-value">
                        {new Date(contest.startTime).toLocaleString()}
                      </span>
                    </div>
                    <div>
                      <span className="contest-meta-label">Ends</span>
                      <span className="contest-meta-value">
                        {new Date(contest.endTime).toLocaleString()}
                      </span>
                    </div>
                  </div>
                </Link>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

export default ContestsList;
