// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { Link } from "react-router-dom";

const API_URL = "http://localhost:5000";

function Profile() {
  const { token } = useAuth();
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errMsg, setErrMsg] = useState("");

  useEffect(() => {
    async function load() {
      if (!token) {
        setErrMsg("You must be logged in to view profile.");
        setLoading(false);
        return;
      }
      try {
        setLoading(true);
        setErrMsg("");

        const [meRes, statsRes] = await Promise.all([
          axios.get(`${API_URL}/users/me`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get(`${API_URL}/users/me/stats`, {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setProfile(meRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error("Profile load error:", err.response?.data || err.message);
        setErrMsg("Failed to load profile.");
      } finally {
        setLoading(false);
      }
    }

    load();
  }, [token]);

  if (loading) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-muted">Loading profile...</p>
        </div>
      </div>
    );
  }

  if (errMsg || !profile) {
    return (
      <div className="page-container">
        <div className="page-card">
          <p className="page-error">{errMsg || "No profile data."}</p>
        </div>
      </div>
    );
  }

  const { difficultyStats, recent } = stats || {
    difficultyStats: { Easy: 0, Medium: 0, Hard: 0 },
    recent: [],
  };

  return (
    <div className="page-container">
      <div className="page-card">
        <h1 className="page-heading">Profile</h1>
        <p className="page-subtitle">
          Member since {new Date(profile.createdAt).toLocaleDateString()}
        </p>

        <div className="profile-grid">
          <div className="profile-main">
            <div className="profile-header">
              <div className="profile-avatar">
                {profile.username?.charAt(0).toUpperCase()}
              </div>
              <div>
                <h2>{profile.username}</h2>
                <p className="page-muted">{profile.email}</p>
              </div>
            </div>

            <div className="profile-stats-cards">
              <div className="profile-stat-card">
                <div className="stat-label">Rating</div>
                <div className="stat-value">{profile.rating}</div>
              </div>
              <div className="profile-stat-card">
                <div className="stat-label">Total Solved</div>
                <div className="stat-value">{profile.totalSolved}</div>
              </div>
              <div className="profile-stat-card">
                <div className="stat-label">Duel Record</div>
                <div className="stat-value">
                  {profile.duelWins}W - {profile.duelLosses}L
                </div>
              </div>
            </div>

            <h3 style={{ marginTop: "1rem" }}>Recent Submissions</h3>
            {recent && recent.length > 0 ? (
              <div className="submissions-table-wrapper">
                <table className="submissions-table">
                  <thead>
                    <tr>
                      <th>Problem</th>
                      <th>Difficulty</th>
                      <th>Status</th>
                      <th>Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recent.map((s) => (
                      <tr key={s._id}>
                        <td>
                          {s.problem ? (
                            <Link
                              to={`/problems/${s.problem.slug}`}
                              className="link-muted"
                            >
                              {s.problem.title}
                            </Link>
                          ) : (
                            "Unknown problem"
                          )}
                        </td>
                        <td>{s.problem?.difficulty || "-"}</td>
                        <td>{s.status}</td>
                        <td>
                          {new Date(s.createdAt).toLocaleString(undefined, {
                            dateStyle: "short",
                            timeStyle: "short",
                          })}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="page-muted">No recent submissions.</p>
            )}
          </div>

          <div className="profile-side">
            <h3>Difficulty Breakdown</h3>
            <div className="difficulty-list">
              <div className="difficulty-item">
                <span className="pill pill-easy">Easy</span>
                <span>{difficultyStats.Easy}</span>
              </div>
              <div className="difficulty-item">
                <span className="pill pill-medium">Medium</span>
                <span>{difficultyStats.Medium}</span>
              </div>
              <div className="difficulty-item">
                <span className="pill pill-hard">Hard</span>
                <span>{difficultyStats.Hard}</span>
              </div>
            </div>

            <Link to="/duel" className="btn btn-primary" style={{ marginTop: 16 }}>
              Start a 1v1 Duel
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
